INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories378,'Học bổng du học Anh, Úc, Mỹ, Canada, New Zealand, Singapore','Là đại diện ủy quyền của hơn 3.000 trường phổ thông, cao đẳng, đại học tại Anh, Úc, Mỹ, Canada, New Zealand, Thụy Sỹ, Hà Lan, Singapore, Malaysia, Đức Anh EduConnect điều phối nhiều chương trình học bổng và các hỗ trợ tài chính giúp nhiều học sinh sinh viên du học thành công.','<p style=\"margin: 0px; padding: 5px 0px; border: 0px; font-size: 12px; vertical-align: baseline; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 16.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\">Là đại diện ủy quyền của hơn 3.000 trường phổ thông, cao đẳng, đại học tại Anh, Úc, Mỹ, Canada, New Zealand, Thụy Sỹ, Hà Lan, Singapore, Malaysia, Đức Anh EduConnect điều phối nhiều chương trình học bổng và các hỗ trợ tài chính giúp nhiều học sinh sinh viên du học thành công.</p>\r\n\r\n<p style=\"margin: 0px; padding: 5px 0px; border: 0px; font-size: 12px; vertical-align: baseline; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 16.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\">Công ty du học Đức Anh đảm bảo &nbsp;cung cấp thông tin đầy đủ, minh bạch, dịch vụ chuyên nghiệp, hiệu quả và trực tiếp với các trường đại diện, không qua trung gian.&nbsp;Trên thực tế, Đức Anh là công ty tư vấn du học Việt Nam nhận được nhiều giải thưởng về chất lượng dịch vụ du học dành cho du học sinh (các giải thưởng mà Đức Anh đạt được xem <a href=\"http://ducanhduhoc.vn/thanh-tuu-cua-duc-anh-2\" style=\"margin: 0px; padding: 0px; border: 0px; font-size: 12px; vertical-align: baseline; text-decoration: none; color: rgb(0, 0, 255);\">tại đây</a>) từ các Phòng giáo dục trực thuộc các Đại sứ quán nước ngoài tại Việt Nam, và từ các trường công ty đại diện tuyển sinh. Tỷ lệ visa được cấp cho du học sinh của công ty gần nhừ là tuyệt đối.</p>\r\n\r\n<p style=\"margin: 0px; padding: 5px 0px; border: 0px; font-size: 12px; vertical-align: baseline; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 16.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\">Công ty hỗ trợ cho các du học sinh:</p>\r\n\r\n<ul style=\"padding: 0px; margin-left: 20px; margin-top: 0px; margin-bottom: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 16.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\">\r\n	<li style=\"font-size: 12px; line-height: 15px; color: rgb(0, 0, 0); padding: 0px;\">Xét hồ sơ nhanh;</li>\r\n	<li style=\"font-size: 12px; line-height: 15px; color: rgb(0, 0, 0); padding: 0px;\">Visa nhanh;</li>\r\n	<li style=\"font-size: 12px; line-height: 15px; color: rgb(0, 0, 0); padding: 0px;\">Học bổng từ nhà trường <b style=\"margin: 0px; padding: 0px; border: 0px; font-size: 12px; vertical-align: baseline;\">10-100% học phí</b>;</li>\r\n	<li style=\"font-size: 12px; line-height: 15px; color: rgb(0, 0, 0); padding: 0px;\">Hỗ trợ vé máy bay tới Úc trị giá <b style=\"margin: 0px; padding: 0px; border: 0px; font-size: 12px; vertical-align: baseline;\">USD 500</b> cho các em đủ điểm tiếng Anh và học lực vào thẳng chương trình cử nhân, thạc sỹ và tiến sỹ tại Úc kì nhập học tháng 7 và 8/2015;</li>\r\n	<li style=\"font-size: 12px; line-height: 15px; color: rgb(0, 0, 0); padding: 0px;\">Tặng <b style=\"margin: 0px; padding: 0px; border: 0px; font-size: 12px; vertical-align: baseline;\">USD 150</b> lệ phí thi chứng chỉ tiếng Anh quốc tế PTE- Academic (Kết quả thay thế cho IELTS/TOEFL) cho tất cả học sinh&nbsp;sinh viên đăng ký du học qua công ty;</li>\r\n	<li style=\"font-size: 12px; line-height: 15px; color: rgb(0, 0, 0); padding: 0px;\">Hỗ trợ <b style=\"margin: 0px; padding: 0px; border: 0px; font-size: 12px; vertical-align: baseline;\">10-20% học phí</b> các khoá tiếng Anh tại Trung tâm Anh ngữ Đức Anh – DACE;</li>\r\n</ul>\r\n\r\n<p style=\"margin: 0px; padding: 5px 0px; border: 0px; font-size: 12px; vertical-align: baseline; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 16.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\">&nbsp;Du học sinh có nhu cầu, vui lòng <b style=\"margin: 0px; padding: 0px; border: 0px; font-size: 12px; vertical-align: baseline;\">Đăng kí </b><a href=\"http://hoithao.ducanhduhoc.vn/\" style=\"margin: 0px; padding: 0px; border: 0px; font-size: 12px; vertical-align: baseline; text-decoration: none; color: rgb(0, 0, 255);\"><b style=\"margin: 0px; padding: 0px; border: 0px; font-size: 12px; vertical-ali