INSERT INTO categorypage (id, title, content, site_id, user_id, `alias`, created_time, modified_time, modified_by, meta_keywords, meta_description, meta_title, image_path, image_name) VALUES (null,'Thiết kế đồ họa chuyên nghiệp','<h1 class=\"titleLeftRight\">&nbsp;</h1>\r\n\r\n<div class=\"boxmain\">\r\n<p style=\"text-align: center\"><img alt=\"\" src=\"http://vnskills.com/hinhanh/images/banner/tk%20do%20hoa%20chuyen%20nghiep1%281%29.jpg\" style=\"width: 738px; height: 281px;\" /></p>\r\n\r\n<p style=\"text-align: center\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>Khóa học Chuyên gia thiết kế&nbsp;đồ họa chuyên nghiệp</em></span></span></p>\r\n\r\n<p style=\"text-align: justify\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; padding: 0px; margin: 0px;\"><span style=\"padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">1. MỤC TIÊU ĐÀO TẠO</strong></span></span></span><br />\r\n<br />\r\n<span style=\"color: rgb(0, 0, 255);\"><span style=\"padding: 0px; margin: 0px;\"><strong>&nbsp; &nbsp;&nbsp;<a href=\"http://vnskills.com/thiet-ke-do-hoa-chuyen-nghiep.html\" target=\"_blank\">Khóa học Thiết kế Đồ họa chuyên nghiệp</a></strong></span></span><span style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0);\">&nbsp; được xây dựng nhằm cung cấp cho xã hội đội ngũ chuyên viên thiết kế sử dụng hiệu quả các phần mềm đồ họa như Adobe Photoshop, Adobe Illustrator, Adobe InDesign, Corel Draw...</span></span></span><br />\r\n<br />\r\n<span style=\"color: rgb(0, 0, 0); font-family: \'times new roman\', times, serif; font-size: 16px;\">&nbsp; &nbsp;Đồng thời học viên có kiến thức vững chắc về mỹ thuật đồ họa; lựa chọn màu sắc; kỹ thuật nhiếp ảnh; phát triển ý tưởng thiết kế; phân tích phương pháp trình bày ấn phẩm đồ họa có tính mỹ thuật cao.</span></p>\r\n\r\n<p style=\"text-align: justify\"><br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; padding: 0px; margin: 0px; color: rgb(0, 0, 0);\">&nbsp; &nbsp;Kết thúc khoá học, học viên có thể ứng dụng các kiến thức trên để thực hiện dự án xây dựng bộ nhận dạng thương hiệu bao gồm thiết kế Logo, Business card, Poster, Quảng cáo, Brochure, Bảng hiệu, Trang phục, Bao bì, Nhãn đĩa, Album, Lịch, Catologue …</span></span></span><br />\r\n<br />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px;\">&nbsp; &nbsp;Đội ngũ giảng viên là các thầy cô hiện là Giám đốc, trưởng phòng tại các công ty thiết kế đồ họa trực tiếp tham gia hướng dẫn và giảng dạy.</span></p>\r\n\r\n<p style=\"text-align: justify\">&nbsp;</p>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\"><strong style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">2. ĐỐI TƯỢNG THAM GIA</span></span></strong><br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">- Học sinh đã tốt nghiệp THPT có định hướng theo đuổi chuyên ngành thiết kế</span></span></div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">- Sinh viên khoa CNTT các trường Trung cấp – Cao Đẳng – Đại học</span></span></div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">- Nhân viên design muốn học nâng cao kiên thức.</span></span></div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">- Tất cả đối tượng yêu thích, đam mê và mong muốn thành công trong lĩnh vực thiết kế đồ họa, ấn phẩm quảng cáo..</span></span></div>\r\n\r\n<p style=\"text-align: justify\"><br />\r\n<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"background-color:#ffffff;\">3. THU HOẠCH SAU KHÓA HỌC</span></strong></span></span></span><br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<br />\r\n<span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp;VnSkills đảm bảo rằng sau khi hoàn thành khóa học, học viên sẽ có đầy đủ kỹ năng và kiến thức để làm việc với môi trường thức tế, phức tạp và nhiều thử thách.</span></span></span></p>\r\n\r\n<p style=\"text-align: justify\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; padding: 0px; margin: 0px; color: rgb(0, 0, 0);\">Học viên sẽ được cấp chứng chỉ cuối khóa học bởi VnSkills Multimedia (Viện CNTT - ĐHQGHN) có giá trị toàn quốc.<br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<br />\r\n&nbsp; &nbsp;Sau khi hoàn thành khóa học, học viên sẽ làm chủ các công cụ thiết kế, có khả năng sáng tạo đồ họa, hiệu chỉnh hình ảnh, thiết kế quảng cáo 2D, thiết kế in ấn và xuất bản.<br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n&nbsp; &nbsp;Sau khi kết thúc khóa học, học viên nắm được những kiến thức, kỹ năng, kỹ thuật áp dụng thực tế trong các chuyên ngành Mỹ thuật quảng cáo, Đồ họa chế bản, In ấn… Học viên có thể làm đồ họa cho các studio ảnh, các nhà xuất bản hay các đơn vị quảng cáo, các công ty thiết kế hay các nhà xuất bản.</span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align: justify\"><strong style=\"color: rgb(0, 0, 0); font-family: \'times new roman\', times, serif; font-size: 16px;\">4. BẰNG CẤP &amp; CHỨNG CHỈ</strong></p>\r\n\r\n<p style=\"text-align: center;\"><br />\r\n<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"http://vnskills.com/hinhanh/images/thuvienanh/chung-chi-tot-nghiep-vnskills.jpg\" style=\"width: 650px; height: 471px;\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; padding: 0px; margin: 0px; color: rgb(0, 0, 0);\">&nbsp; &nbsp;Học viên sẽ được cấp chứng nhận&nbsp;<strong>\" Chuyên gia thiết kế đồ họa chuyên nghiệp \"</strong> do Viện Công nghệ thông tin - ĐHQGHN cấp. Chứng nhận có giá trị toàn quốc</span><br />\r\n<br />\r\n<strong><span style=\"text-align: justify; padding: 0px; margin: 0px; color: rgb(0, 0, 0);\">5. ƯU THẾ CHƯƠNG TRÌNH</span></strong></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align: justify\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; padding: 0px; margin: 0px; color: rgb(0, 0, 0);\">&nbsp; &nbsp;Hệ thống bài giảng điện tử: Từng buổi học trên lớp, sẽ có camera ghi lại toàn bộ bài giảng môn học, sau đó chép bài giảng này cho tất cả học viên. Với phương pháp này giúp các học viên ôn lại kiến thức mà giảng viên đã trình bày trên lớp một các rõ ràng &amp; cụ thể.<br />\r\n<br />\r\n&nbsp; &nbsp;Thư viện tư liệu trực tuyến &amp; đội ngữ giảng viên giàu kinh nghiệm...sẽ giúp sinh viên nâng cao kiến thức &amp; kinh nghiệm trong từng môn học<br />\r\n<br />\r\n&nbsp; &nbsp;Đồ án thực tế: Ngay cuối kỳ học học viên sẽ được trải nghiệm với các dự án thiết kế đồ họa...<br />\r\n<br />\r\n<strong>6. CƠ HỘI NGHỀ NGHIỆP</strong><br />\r\n<br />\r\n&nbsp; &nbsp;Học viên tốt nghiệp chương trình đào tao \" Thiết kế đồ họa chuyên nghiệp \" tại VnSkills Multimedia Academy sẽ có cơ hội trả thành các chuyên viên phát triển ý tưởng, Phân tích đánh giá ấn phẩm đồ họa, Nhà thiết kế đồ họa, Thiết kế dàn trang, Biên tập ảnh số trong các công ty truyền thông quảng cáo, tạp chí, in ấn, nhà xuất bản...<br />\r\n<br />\r\n&nbsp; &nbsp;VnSkills Multimedia Academy hỗ trợ việc làm cho các học viên tốt nghiệp loại giỏi thông qua mạng lưới đối tác uy tín.</span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"text-align: center\"><span style=\"font-size:22px;\"><span style=\"font-family:times new roman,times,serif;\"><u><strong>NỘI DUNG KHÓA HỌC</strong></u></span></span><br />\r\n&nbsp;\r\n<div><em><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">(T<font color=\"#000000\">ổ</font></span></span><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">ng t</span></span><em style=\"color: rgb(0, 0, 0); font-family: \'times new roman\', times, serif; font-size: 16px;\">hời lượng</em><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">: 129h)</span></span></em></div>\r\n</div>\r\n\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n\r\n<p style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\"><br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:20px;\"><strong>Đồ họa xử lý ảnh - ADOBE PHOTOSHOP</strong></span><br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<span style=\"text-align: justify; padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><em style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">&nbsp;</em></strong></span></strong></span></span></span><br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<em>Thời lượng: 27h</em><br />\r\n<br />\r\n<strong>1. Giới thiệu:&nbsp;</strong></span></span><br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align: justify; padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Addobe Photoshop được đánh giá là phần mềm dẫn đầu thị trường về sửa ảnh bitmap và bạn có thể làm được hơn nhiều điều với Photoshop. Những công cụ sáng tạo của nó giúp bạn làm được những kết quả thật phi thường. Đồng thời với nhiều hiệu ứng biên tập, xử lý và biến đổi hình ảnh công việc của bạn sẽ được giải quyết một cách thật sự nhanh chóng.<br />\r\n<br />\r\n&nbsp; &nbsp;Adobe Photoshop là phần mềm không thể thiếu của các nhiếp ảnh gia, các nhà thiết kế đồ hoạ, thiết kế web và biên tập video và&nbsp; nó có khả năng tương thích với hầu hết các chương trình đồ họa khác của Adobe như Adobe Illustrator, Adobe Premiere, After After Effects và Adobe Encore.</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"padding: 0px; margin: 0px; font-size: 13px;\">&nbsp;</p>\r\n\r\n<div style=\"text-align: justify;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><b>2. Mục đích khóa học</b></span></span><br />\r\n&nbsp;</div>\r\n\r\n<p style=\"text-align: justify; padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Khóa học Photoshop trang bị cho học viên:</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align: justify; margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span><strong>&nbsp;</strong>Phương pháp sửa ảnh (retouching);</span></span></p>\r\n\r\n<p style=\"text-align: justify; margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span>&nbsp;Ghép ảnh (composing);</span></span></p>\r\n\r\n<p style=\"text-align: justify; margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span><strong>&nbsp;</strong>Phục chế ảnh (restoration);</span></span></p>\r\n\r\n<p style=\"text-align: justify; margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span><strong>&nbsp;</strong>Tô màu tranh ảnh (painting);</span></span></p>\r\n\r\n<p style=\"text-align: justify; margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span>&nbsp;Thiết kế quảng cáo;</span></span></p>\r\n\r\n<p style=\"text-align: justify; margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span><strong>&nbsp;</strong>Thiết kế trang web;</span></span></p>\r\n\r\n<p style=\"text-align: justify; margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span><strong>&nbsp;</strong>Vẽ các loại tranh (matte painting và nhiều thể loại khác);</span></span></p>\r\n\r\n<p style=\"text-align: justify; margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span>&nbsp;Vẽ texture cho các chương trình 3D...</span></span></p>\r\n\r\n<p style=\"text-align: justify\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Tất cả kỹ năng đó của photoshop gần như là mọi hoạt động liên quan đến ảnh bitmap.</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">3. Nội dung học</strong></span></span></span></strong></span></span></span></span></span><br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<br />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài 1:</strong><span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp;Giới thiệu chung về chương trình Adobe Photoshop</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các thao tác với giao diện của chương trình</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các công cụ làm việc với vùng chọn</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cách cắt ghép cơ bản</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài 2:</strong><span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp;Thao tác với Layer</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các lệnh Transform</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Ứng dụng Feather với vùng chọn</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài 3:&nbsp;</strong><span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Các thao tác Crop hình ảnh, các lệnh ImagerSize, CanvasSize</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các thao tác với màu sắc: Solid Color, Gradient Color</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Biên tập và sử dụng Pattern</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài 4:</strong><span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp;Các công cụ trong nhóm tô vẽ, chỉnh sửa</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài 5:</strong><span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp;Các lệnh chỉnh màu sắc, sáng tối trong bảng Adjustment</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Giới thiệu vè Adjustment Layer</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Một số ví dụ về Blend màu</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cách sử dụng Action chỉnh màu tự động</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài 6:</strong><span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp;Thao tác với chữ trong Photoshop</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Hiệu ứng Layer Style</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các ứng dụng tạo chất liệu cho chữ với Layer Style</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài 7: &nbsp;</strong><span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Cách sử dụng Pen Tool và Path</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Layer Mask và các ứng dụng</span><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Cách tạo và sử dụng Action</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài 8:&nbsp;</strong><span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bảng lệnh Filter</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Giới thiệu các Plug-in</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Giới thiệu các thanh công cụ trong pts</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Giới thiệu các phím tắt, các lệnh trong pts</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thực hành các lệnh và tác dụng của những phím tắt</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp;</span><br />\r\n<strong><span style=\"font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài kiểm tra cuối môn</span></strong><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n&nbsp;</p>\r\n\r\n<p style=\"padding-bottom: 0px; margin: 0px 0px 0px 40px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\">&nbsp;</p>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:20px;\"><strong style=\"font-family: \'times new roman\', times, serif;\">Thiết kế đồ họa Vector - ADOBE ILLUCTRATOR</strong></span><br />\r\n<br />\r\n<span style=\"text-align: justify; padding: 0px; margin: 0px;\"><span style=\"padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><em style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">Thời lượng :&nbsp;</em></span><em style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">27h&nbsp;</em></span></strong></span></strong></span></span></span><br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<span style=\"text-align: justify; padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">1. Giới thiệu&nbsp;</strong></span></span></span></strong></span></span></span></span></span><br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align: justify; padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Adobe Illustrator là chương trình đồ họa vector, tạo nền tảng cần thiết cho nghề thiết kế đồ họa. Illustrator tạo hình dạng cơ bản thông qua lệnh và công cụ, sao chép và kết hợp đối tượng để tạo hình dạng mới, chọn và thay đổi các phần trong đối tượng bằng công cụ Selection, tô vẽ đối tượng, cung cấp nhiều hiệu ứng 3D để vẽ các hình khối, vật thể, xử lý các hình ảnh liên quan đến công việc dàn trang báo chí.<br />\r\n<br />\r\nAdobe Illustrator là phần mềm không thể thiếu của các nhiếp ảnh gia, các nhà thiết kế đồ hoạ, thiết kế web và biên tập video. Đặc biệt, Illustrator có thế mạnh là dễ dàng thay đổi và trao đổi dữ liệu, kích thước file nhẹ, dễ dàng tương thích với Photoshop, Corel Draw và hỗ trợ in ấn rất tốt.</span></span></p>\r\n\r\n<p style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\">&nbsp;</p>\r\n\r\n<p style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0);\">&nbsp;</p>\r\n\r\n<div style=\"text-align: justify;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><b>2. Mục tiêu môn học</b></span></span></div>\r\n&nbsp;\r\n\r\n<p style=\"text-align: justify; padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Thành thạo Adobe Illustrator, bạn có thể hoàn thành tốt các công việc sau:</span></span></p>\r\n\r\n<p style=\"text-align: justify; margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span><strong>&nbsp;</strong>Minh họa sách báo;</span></span></p>\r\n\r\n<p style=\"text-align: justify; margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span>&nbsp;Thiết kế logo;</span></span></p>\r\n\r\n<p style=\"text-align: justify; margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span>&nbsp;Tạo các sản phẩm tờ rơi, Business Card, Brochure, Profile;</span></span></p>\r\n\r\n<p style=\"text-align: justify; margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span><strong>&nbsp;</strong>Vẽ hoạt hình;</span></span></p>\r\n\r\n<p style=\"text-align: justify; margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span><strong>&nbsp;</strong>Tích hợp chuyển động cho Flash...</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">3. Nội dung học</strong></span></span></span></strong></span></span></span></span></span><br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin:0in;margin-bottom:.0001pt;text-align:justify\"><strong><span style=\"color: black;\"><font face=\"times new roman, times, serif\"><span style=\"font-size: 16px;\">Bài 1:</span></font><font face=\"Arial, sans-serif\"><span style=\"font-size: 13.3333330154419px;\">&nbsp;</span></font></span></strong><span style=\"color: black; font-family: \'times new roman\', times, serif; font-size: 16px;\">Giới thiệu chung về chương trình Illustrator</span></p>\r\n\r\n<p style=\"margin: 0in 30pt 0.0001pt 0in; text-align: justify;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:black\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các công cụ tạo hình cơ bản<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Công cụ chọn Selection Tool<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Vẽ một số hình đơn giản bằng những công cụ đã học</span></span></span></p>\r\n\r\n<p style=\"margin: 0in 0in 0.0001pt; text-align: justify;\"><br />\r\n<strong><span style=\"color: black;\"><font face=\"times new roman, times, serif\"><span style=\"font-size: 16px;\">Bài 2:</span></font><font face=\"Arial, sans-serif\"><span style=\"font-size: 13.3333330154419px;\">&nbsp;</span></font></span></strong><span style=\"color: black; font-family: \'times new roman\', times, serif; font-size: 16px;\">Các công cụ chọn</span></p>\r\n\r\n<p style=\"margin: 0in 30pt 0.0001pt 0in; text-align: justify;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:black\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các công cụ biến đổi hình vẽ<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Vẽ các hình khối hộp và các hình cân xứng</span></span></span></p>\r\n\r\n<p style=\"margin: 0in 0in 0.0001pt; text-align: justify;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"color:black\">&nbsp;</span></strong><br />\r\n<b><span style=\"color:black\"><strong>Bài 3:</strong></span></b></span></span><span style=\"color: black; font-family: \'times new roman\', times, serif; font-size: 16px;\">&nbsp;Công cụ vẽ tự do: Pen Tool, Pencil Tool</span></p>\r\n\r\n<p style=\"margin: 0in 30pt 0.0001pt 0in; text-align: justify;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:black\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các lệnh hiệu chỉnh đường Path<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Sử dụng công cụ Pen Tool</span></span></span></p>\r\n\r\n<p style=\"margin: 0in 0in 0.0001pt; text-align: justify;\"><br />\r\n<strong><span style=\"color: black;\"><font face=\"times new roman, times, serif\"><span style=\"font-size: 16px;\">Bài 4:</span></font><font face=\"Arial, sans-serif\"><span style=\"font-size: 13.3333330154419px;\">&nbsp;</span></font></span></strong><span style=\"color: black; font-family: \'times new roman\', times, serif; font-size: 16px;\">Các cách sử dụng màu trong Illustrator</span></p>\r\n\r\n<p style=\"margin: 0in 30pt 0.0001pt 0in; text-align: justify;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:black\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các cách sao chép màu bằng công cụ EyeDropper Tool<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Vẽ các icon, nút website, hình vẽ sử dụng màu chuyển tạo khối, tạo hiệu quả bóng bẩy.</span></span></span></p>\r\n\r\n<p style=\"margin: 0in 0in 0.0001pt; text-align: justify;\"><br />\r\n<strong><span style=\"color: black;\"><font face=\"times new roman, times, serif\"><span style=\"font-size: 16px;\">Bài 5:</span></font><font face=\"Arial, sans-serif\"><span style=\"font-size: 13.3333330154419px;\">&nbsp;</span></font></span></strong><span style=\"color: black; font-family: \'times new roman\', times, serif; font-size: 16px;\">Các lệnh kết hợp hình vẽ trong bảng Pathfinder</span></p>\r\n\r\n<p style=\"margin: 0in 30pt 0.0001pt 0in; text-align: justify;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:black\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các lệnh căn, gióng hình vẽ trong bảng Align<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Bảng lệnh Transform<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Vẽ các logo</span></span></span></p>\r\n\r\n<p style=\"margin: 0in 0in 0.0001pt; text-align: justify;\"><br />\r\n<strong><span style=\"color: black;\"><font face=\"times new roman, times, serif\"><span style=\"font-size: 16px;\">Bài 6:</span></font><font face=\"Arial, sans-serif\"><span style=\"font-size: 13.3333330154419px;\">&nbsp;</span></font></span></strong><span style=\"color: black; font-family: \'times new roman\', times, serif; font-size: 16px;\">Công cụ Brush và cách biên tập</span></p>\r\n\r\n<p style=\"margin: 0in 30pt 0.0001pt 0in; text-align: justify;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:black\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Sử dụng công cụ Brush để vẽ khung trang trí, vẽ các sợi dây cáp, dây điện thoại</span></span></span></p>\r\n\r\n<p style=\"margin: 0in 0in 0.0001pt; text-align: justify;\"><strong><span style=\"color: black;\"><font face=\"Arial, sans-serif\"><span style=\"font-size: 13.3333330154419px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></font></span></strong><span style=\"color: black; font-family: \'times new roman\', times, serif; font-size: 16px;\">Làm việc với chữ,&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-family: \'times new roman\', times, serif; font-size: 16px;\">ảnh</span><span style=\"color: black; font-family: \'times new roman\', times, serif; font-size: 16px;\">&nbsp;trong Illustrator</span></p>\r\n\r\n<p style=\"margin: 0in 30pt 0.0001pt 0in; text-align: justify;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:black\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cách chuyển 1 hình ảnh thành hình vẽ vector<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thiết kế 1 tờ gấp với các kiến thức về dùng chữ và ảnh đã học</span></span></span></p>\r\n\r\n<p style=\"margin: 0in 0in 0.0001pt; text-align: justify;\"><br />\r\n<strong><span style=\"color: black;\"><font face=\"times new roman, times, serif\"><span style=\"font-size: 16px;\">Bài 7:</span></font><font face=\"Arial, sans-serif\"><span style=\"font-size: 13.3333330154419px;\">&nbsp;</span></font></span></strong><span style=\"color: black; font-family: \'times new roman\', times, serif; font-size: 16px;\">Cách sử dụng Clipping Mask, Opacity Mask</span></p>\r\n\r\n<p style=\"margin: 0in 30pt 0.0001pt 0in; text-align: justify;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:black\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cách sử dụng công cụ Blend Tool tạo hiệu quả biến đổi, phối trộn giữa các hình vẽ khác nhau<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cách sử dụng Symbol Tool tạo ra hàng loạt các hình vẽ có cùng thuộc tính</span></span></span></p>\r\n\r\n<p style=\"margin: 0in 0in 0.0001pt; text-align: justify;\"><br />\r\n<strong><span style=\"color: black;\"><font face=\"times new roman, times, serif\"><span style=\"font-size: 16px;\">Bài 8:</span></font><font face=\"Arial, sans-serif\"><span style=\"font-size: 13.3333330154419px;\">&nbsp;</span></font></span></strong><span style=\"color: black; font-family: \'times new roman\', times, serif; font-size: 16px;\">Vẽ các hình khối 3D với công cụ Perspective Grid Tool, các lệnh vẽ 3D trên trình đơn Effect</span></p>\r\n\r\n<p style=\"margin: 0in 30pt 0.0001pt 0in; text-align: justify;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:black\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các hiệu ứng còn lại trên trình đơn Effect</span><br />\r\n<span style=\"color:black\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các thiết lập chuẩn file in ấn, cách xuất file ra các định dạng khác nhau: pdf, eps, jpeg</span><br />\r\n<br />\r\n<strong><span style=\"color:black\">Bài kiểm tra cuối khóa</span></strong></span></span><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong style=\"color: rgb(255, 140, 0); font-family: \'times new roman\', times, serif; font-size: 16px; padding: 0px; margin: 0px;\"><span style=\"padding: 0px; margin: 0px;\">&nbsp;</span></strong></span></span></p>\r\n\r\n<p style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\"><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\"><strong style=\"font-family: \'times new roman\', times, serif; font-size: 20px;\">Thiết kế in ấn và Dàn trang</strong><span style=\"font-size: 20px; text-align: justify; padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><span style=\"font-size:22px;\">&nbsp;</span></span></strong></span></strong></span><span style=\"font-size: 20px; font-family: \'times new roman\', times, serif;\"><span style=\"text-align: justify; padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"> - ADOBE INDESIG</span></strong></span></strong></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; padding: 0px; margin: 0px;\"><span style=\"padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><em style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">Thời lượng : 18h&nbsp;</em></strong></span></span></span></strong></span></span></span><br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<strong>1. Giới thiệu</strong><br />\r\n<br />\r\n&nbsp; &nbsp;Adobe Indesign là một phần mềm dùng để thiết kế in ấn và dàn trang rất chuyên nghiệp. Adobe Indesign là một trong các sản phẩm của tập đoàn Adobe chuyên về dàn trang, tạo ra các sản phẩm như sách, báo, tạp chí, tờ bướm, catalogue, các biểu mẫu, nhãn CD...<br />\r\n<br />\r\n&nbsp; &nbsp;Lợi thế của&nbsp; Indesign là giao diện và hệ thống các phím tắt quen thuộc với những người dùng đã biết về Photoshop, Illustrator. Ngoài ra, Indesign cũng dễ dàng nhúng (embed) hay liên kết (link) với các định dạng file hình bibmap của Photoshop (tiff, jpeg, psp..) và cả file vector của illustrator (ai, eps, pdf). Đặc biệt, indesign cho phép chèn các tập tin văn bản định dạng như .doc, pdf.<br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<span style=\"text-align: justify; padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">2. Mục đích khóa học</strong></span></span></span></strong></span></span></span><br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n&nbsp; &nbsp;Sau khi hoàn thành nội dung Adobe Indesign, học viên có thiết kế được những sản phẩm:</span></span></p>\r\n\r\n<p style=\"margin-left: 40px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span>&nbsp;Sách (book)<br />\r\n<span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span>&nbsp;Tạp chí (Magazine)<br />\r\n<span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span>&nbsp;Biểu mẫu (Form)<br />\r\n<span style=\"text-align: justify; color: rgb(0, 0, 0);\">•&nbsp;</span>Brochure<br />\r\n<span style=\"text-align: justify; color: rgb(0, 0, 0);\">•&nbsp;</span>Catalog<br />\r\n<span style=\"text-align: justify; color: rgb(0, 0, 0);\">•</span>&nbsp;Newsletter<br />\r\n<span style=\"text-align: justify; color: rgb(0, 0, 0);\">•&nbsp;</span>Presentation</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,0,0); font-size: 13px; padding-top: 0px\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"text-align: justify; padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">&nbsp;3. Nội dung học</strong></span></span></strong></span></span></span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Buổi 1</strong>: Giới thiệu tổng quan, các ứng dụng của chương trình</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Các thao tác với giao diện của chương trình</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các công cụ tạo hình trong Indesign</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thao tác cơ bản với khung ảnh và khung chữ</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Bài tập kết thúc buổi 1:</span></span></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Buổi 2</strong>: Tìm hiểu về thanh Option</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Làm việc với hình ảnh</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Làm việc với màu sắc</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Bài tập kết thúc buổi 2</span></span></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Buổi 3: Các lệnh làm việc với văn bản</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Xử&nbsp;lý font chữ</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thiết lập lại phím tắt</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Bài tập kết thúc buổi 3</span></span></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Buổi 4</strong>: Làm việc với trang chủ</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Cách đặt số trang tự động</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Bài tập kết thúc buổi 4</span></span></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Buổi 5</strong>: Làm việc với Character Style và Paragraph Style</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Dàn trang sách</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các thiết lập khi xuất file ảnh và file in</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cách đóng gói và lưu file</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Bài tập kết thúc buổi 5</span></span></div>\r\n\r\n<p style=\"padding: 0px; margin: 0px; font-size: 13px;\">&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\"><br />\r\n<span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px; font-size: 20px;\"><span style=\"margin: 0px; padding: 0px;\"><strong style=\"margin: 0px; padding: 0px;\">Thiết kế đồ họa - CORELDRAW</strong></span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\n<em style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><strong style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><strong style=\"margin: 0px; padding: 0px;\">Thời lượng: 18h</strong></span></span></span></strong></span></span></span></em><br style=\"margin: 0px; padding: 0px;\" />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\n<span style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><strong style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><strong style=\"margin: 0px; padding: 0px;\">Giới thiệu</strong></span></span></span></strong></span></span></span></span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">CorelDraw là một công cụ dùng cho việc thiết kế đồ họa (graphics design). Giới họa sĩ trình bày và họa viên kỹ thuật ở nước ta cũng như trên thế giới dùng CorelDraw tương đối nhiều có lẽ vì khả năng tinh tế và tốc độ vượt trội của CorelDraw trong việc thực hiện những sản phẩm “văn hóa trực quan” (CorelDraw chiếm đến 85% thị phần so với các phần mềm cùng loại (theo dữ liệu của Unit Sales, PC Data).</span></span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\"><br style=\"margin: 0px; padding: 0px;\" />\r\n<span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><strong style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><strong style=\"margin: 0px; padding: 0px;\">Mục đích khóa học</strong></span></span></span></strong></span></span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;Khi hoàn thành nội dung Corel Draw, học viên có thể:</span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin: 0px 0px 0px 40px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px; text-align: justify;\">•&nbsp;</span>Sử dụng thành thạo các chức năng của Corel.</span></span></p>\r\n\r\n<p style=\"margin: 0px 0px 0px 40px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px; text-align: justify;\">•</span><strong style=\"margin: 0px; padding: 0px;\">&nbsp;</strong>Có khả năng làm việc tại các công ty chuyên thiết kế quảng cáo; in ấn; bao bì sản phẩm;</span></span></p>\r\n\r\n<p style=\"margin: 0px 0px 0px 40px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px; text-align: justify;\">•&nbsp;</span>Những sản phẩm có thể tạo bằng Corel Draw: Cardvisit, Lịch, Thiệp, Catalogue, Brochure...</span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><strong style=\"margin: 0px; padding: 0px;\">Nội dung học</strong></span></span></span></span></span></span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin: 0px 0px 0px 40px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">- Giới thiệu tổng quát về CorelDraw<br style=\"margin: 0px; padding: 0px;\" />\r\n- Công cụ chọn và các tính năng liên quan<br style=\"margin: 0px; padding: 0px;\" />\r\n- Công cụ tô màu và các vấn đề liên quan<br style=\"margin: 0px; padding: 0px;\" />\r\n- Nhóm lệnh Align – Order – Group<br style=\"margin: 0px; padding: 0px;\" />\r\n- Các lệnh Weld, Trim, Intersect, Simplify, Front Minus Back, …,&nbsp; Combine<br style=\"margin: 0px; padding: 0px;\" />\r\n- Nhóm công cụ vẽ và chỉnh sửa<br style=\"margin: 0px; padding: 0px;\" />\r\n- Nhóm lệnh Transformation, lệnh Covert to Curve, lệnh Convert Outline to Object<br style=\"margin: 0px; padding: 0px;\" />\r\n- Công cụ Text và các lệnh liên quan<br style=\"margin: 0px; padding: 0px;\" />\r\n- Nhóm công cụ tương tác (Interactive Tools), lệnh PowerClip, Add Perspective<br style=\"margin: 0px; padding: 0px;\" />\r\n- Ảnh trong CorelDraw<br style=\"margin: 0px; padding: 0px;\" />\r\n- Các ứng dụng mở rộng<br style=\"margin: 0px; padding: 0px;\" />\r\n- Tài liệu nhiều trang<br style=\"margin: 0px; padding: 0px;\" />\r\n- Quản lý đối tượng<br style=\"margin: 0px; padding: 0px;\" />\r\n- In ấn với CorelDraw<br style=\"margin: 0px; padding: 0px;\" />\r\n- Bài kiểm tra cuối môn</span></span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"margin: 0px; padding: 0px; font-size: 20px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><strong style=\"margin: 0px; padding: 0px;\">&nbsp;</strong></span></span></span></span></span></span></span></p>\r\n\r\n<div style=\"text-align: justify;\"><br />\r\n<b style=\"font-family: \'times new roman\', times, serif; font-size: 20px;\">M</b><b style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: \'times new roman\', times, serif; font-size: 20px;\">Ỹ</b><b style=\"font-family: \'times new roman\', times, serif; font-size: 20px;\">&nbsp;THUẬT&nbsp;</b><span style=\"font-size: 18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong style=\"text-align: center;\">THIẾT KẾ - XÂY DỰNG THƯƠNG HIỆU</strong></span></span></div>\r\n<br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"text-align: justify; padding: 0px; margin: 0px;\"><span style=\"padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><span style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><span style=\"padding: 0px; margin: 0px;\"><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><em style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">Thời lượng :&nbsp;</em></strong></span></span><strong style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\"><em style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">24h&nbsp;</em></strong></span></strong></span></span></span></span><br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<br style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" />\r\n<span style=\"color: rgb(0, 0, 0);\"><strong>Mục đích</strong><br />\r\n<br />\r\nHọc viên sẽ được trang bị kiến thức về đồ hoạ cơ bản, bao gồm sự hiểu biết về màu sắc, hình dáng, đường nét, kỹ thuật dùng font chữ,&nbsp; nguyên tắc sử dụng màu sắc, phương pháp phát triển ý tưởng BrainStoming, kỹ thuật vẽ phác thảo, kỹ thuật nhiếp ảnh số và nguyên tắc lựa chọn hình ảnh cho thiết kế.</span></span></span><br />\r\n<br />\r\n<strong style=\"color: rgb(0, 0, 0); font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify; padding: 0px; margin: 0px;\">Nội dung học</strong><br />\r\n&nbsp;\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Bài 1</strong>: Nguyên tắc thị giác:</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Giới thiệu về các yếu tố nguyên lý thị giác.</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Các yếu tố thị giác để có thể áp dụng trong quá trình dựng sắp xếp bố cục.</span></span></div>\r\n\r\n<div><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Bài thực hành h',[site_id],[user_id],'thiet-ke-do-hoa-chuyen-nghiep',[now],[now],[user_id],'','','','','');
INSERT INTO categorypage (id, title, content, site_id, user_id, `alias`, created_time, modified_time, modified_by, meta_keywords, meta_description, meta_title, image_path, image_name) VALUES (null,'Khóa học lập trình Android chuyên nghiệp','<h1 class=\"titleLeftRight\"><span>Khóa học lập trình Android chuyên nghiệp</span></h1>\r\n\r\n<div style=\"text-align: center;\"><span style=\"font-size:20px;\"><span style=\"font-family: \'times new roman\', times, serif;\">KHÓA HỌC LẬP TRÌNH ANDROID CHUYÊN NGHIỆP</span></span></div>\r\n\r\n<div style=\"text-align: center;\"><em><span style=\"font-size: 16px;\"><span style=\"font-family: \'times new roman\', times, serif;\">( Thời lượng : 105h ~ 5 tháng)</span></span></em></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div style=\"text-align: center;\"><img alt=\"\" src=\"http://vnskills.com/hinhanh/images/lap-trinh-vien-android.png\" style=\"width: 715px; height: 277px;\" /><br />\r\n<br />\r\n<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>Khóa học lập trình Andoid</em></span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><br />\r\n<span style=\"font-family:times new roman,times,serif;\"><strong style=\"font-size: 12px;\"><span style=\"font-size: 16px;\">1. GIỚI THIỆU KHÓA HỌC</span></strong><br />\r\n<br />\r\n<span style=\"font-size: 16px;\">Thiết bị Android đang thống lĩnh thị trường di dộng và có tốc độ tăng trưởng nhanh hơn bất ky nền tảng di động nào khác. Hiện nay, Android là hệ điều hành được sử dụng phổ biến nhất trên toàn cầu, chiếm khoảng 80% smartphone.</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Lập trình Android đang trở thành một sự lựa chọn tuyệt vời trong hành trình phát triển ứng dụng trên điện thoại di động với 1 tỉ người sử dụng , 1,5 triệu thiết bị mới được kích hoạt mỗi ngày…</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Sự phát triển của thiết bị Android cũng đồng nghĩa với việc có nhiều cơ hội lớn cho doanh số bán ứng dụng, thúc đẩy thị trường ứng dụng di động cho Android cũng ngày càng phát triển. Vì thế, phát triển ứng dụng cho Android cũng trở thành mục tiêu của các doanh nghiệp công nghệ, tạo ra nhu cầu tuyển dụng số lượng lớn các Lập trình viên Android.</span></span></div>\r\n\r\n<div><br />\r\n<span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">2. MỤC TIÊU ĐÀO TẠO.</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">- Khóa học Lậptrình android tại Vnskills Academy giúp cho học viên xây dựng được các ứng dụng trên hệ điều hành Android. Bên cạnh đó học viên cũng được cung cấp những kiến thức về ngôn ngữ lập trình Object C được sử dụng trong Android.&nbsp;</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Chương trình học gồm: Lập trình Java &nbsp;cơ bản, Lập trình Android cơ bản, Lập trình Android nâng cao và xây dựng các ứng dụng minh họa</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">3. ĐỐI TƯỢNG THAM GIA</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">- Học sinh đã tốt nghiệp THPT có định hướng theo đuổi lập trình phần mềm</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">- Sinh viên khoa CNTT các trường Trung cấp – Cao Đẳng – Đại học</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">- Nhân viên phát triển ứng dụng Mobile tại các doanh nghiệp</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">- Tất cả đối tượng yêu thích, đam mê và mong muốn thành công trong lĩnh vực phát triển ứng dụng cho thiết bị &nbsp;di động</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">4. THU HOẠCH KHÓA HỌC</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Trở thànhLập trình viên Android chuyên nghiệp</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Phát triển các ứng dụng mobile trên Android</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Chuyên viên phát triển ứng dụng di động( Mobile Application Develop)</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Học viên có thể sang tạo những ứng dụng của riêng mình để kinh doanh và tìm kiếm lợi nhuận</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">5. VĂN BẰNG - CHỨNG CHỈ</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-size: 16px; font-family: \'times new roman\', times, serif;\">&nbsp;Học viên hoàn thành khóa học đạt yêu cầu sẽ được cấp chứng nhận hoàn thành khóa học do Vnskills - Viện CNTT - Đại học Quốc gia Hà Nội cấp.</span></div>\r\n\r\n<div><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">6 .CƠ HỘI NGHỀ NGHIỆP</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Học viên tốt nghiệp chương trình đào tạo “ Lập trình Android chuyên nghiệp” tại Vnskills Academy sẽ được giới thiệu việc làm tại các công ty đối tác lớn của Vnskills</span></span><br />\r\n<br />\r\n&nbsp;</div>\r\n\r\n<div style=\"text-align: center;\"><span style=\"font-size:22px;\"><u><span style=\"font-family:times new roman,times,serif;\"><strong>NỘI DUNG KHÓA HỌC</strong></span></u></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 18px;\"><strong>JAVA CORE (Java cơ bản)</strong></span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><em><span style=\"font-size: 16px;\">Thời lượng: 30 h</span></em></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">Mục tiêu</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Nắm vững khái niệm cơ bản trong ngôn ngữ lập trình Java</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Thiết kế giao diện Java Form đơn giản</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Làm việc với tập tin, mảng, các dạng lưu trữ tập hợp</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Lập trình hướng đối tượng cơ bản</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Nắm vững kiến thức nền tảng để có thể xây dựng các ứng dụng nhỏ bằng Java</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">Nội dung</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 1: Tổng quan về Java, Các cấu trúc điều kiện</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 2: Biến, kiểu dữ liệu, các cấu trúc lặp</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 3: Làm việc với String (chuỗi), Array (mảng)</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 4: Làm với Collection (tập hợp)</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 5: Xây dựng các lớp (Class), các thành phần của lớp</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 6: Giao diện (interface), lớp lồng nhau (nested Class)</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 7: Lập trình Đa luồng (Multi Thread)</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 8: Bẫy lỗi ngoại lệ (Exception),</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 9: Làm việc với hệ thồng file: Tạo File, Đọc/ghi File</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><strong><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Kiểm tra cuối môn</span></span></strong><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 18px;\"><strong>LẬP TRÌNH ANDROID CƠ BẢN</strong></span></span></div>\r\n\r\n<div><strong><span style=\"font-family:times new roman,times,serif;\"><em><span style=\"font-size: 16px;\">Thời lượng:42h</span></em></span></strong><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">Mục tiêu</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Tiếp cận môi trường lập trình di động.</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Có kiến thức cơ bản về nền tảng Android.</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Thành tạo kỹ năng lập trình, phát triển ứng dụng mobile trên Android</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Làm việc với SQLLite</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Sử dụng các chuẩn dữ liệu JSON, XML.</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Xây dựng ứng dụng theo mô hình MVC</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">Nội dung</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 1: Tổng quan về lập trình Android Android</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 2: View &amp; Các điều khiển cơ bản</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 3: Tài nguyên ứng dụng cơ bản,Intent</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 4: Tài nguyên ứng dụng hình ảnh và giao diện</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 5: Quản lý Asset – SharePreference - Bộ nhớ thiết bị</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 6: Adapter và các điều khiển tập hợp</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 7: Xây dựng giao diện với Fragment</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 8: Quản lý dữ liệu với Content Provider</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 9: Lưu trữ dữ liệu ứng dụng với SQLite</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 10: Truy vấn và sắp xếp dữ liệu</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 11: Menu &amp; ActionBar</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 12: Action Provider &amp; Điều khiển tìm kiếm dữ liệu</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 13: Các chuyển hoạt trong ứng dụng</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 14: Sử dụng Cursor Adapter</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 15: AsyncTask – Thread và Handler</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 16: Service – Broadcast Receiver và Notification</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><strong><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Kiểm tra cuối khóa</span></span></strong><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 18px;\"><strong>LẬP TRÌNH ANDROID NÂNG CAO (Lập trình ứng dụng client và ứng dụng đa phương tiện SMS, CAMERA,...)</strong></span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><em><span style=\"font-size: 16px;\">Thời lượng: 21h</span></em></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">Mục tiêu:</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Khai thác các dịch vụ Internet</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Xây dựng ứng dụng bản đồ với Google Map</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Sử dụng các bộ cảm biến và các bộ kết nối không dây</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Xây dựng ứng dụng giải trí đa phương tiện</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">• Kết nối với các hệ thống khác thông qua WebService</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">Nội dung</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 1: Khai thác tài nguyên Internet, Giao thức kết nối WebService</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 2: Đọc ghi dữ liệu XML – JSON</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 3: Khảo sát và sử dụng GoogleMap</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 4: Các điều khiển đa truyền thông, Bộ cảm biến – Kết nối không dây</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 5: Telephony &amp; SMS, Google Cloud Messaging</span></span></div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Bài 6: Ứng dụng Widget</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><strong><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Kiểm tra cuối khóa</span></span></strong><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 18px;\"><strong>ĐỒ ÁN THỰC TẾ</strong></span><br />\r\n<em><span style=\"font-size: 16px;\">Thời lượng ( 12h~4buổi)</span></em></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">Mục tiêu</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Học viên xây dựng và hoàn thiện một dự án phần mềm Android làm theo nhóm, có giáo viên hướng dẫn. Việc này giúp học sinh có được kỹ năng thực tế, nắm được qui trình xây dựng phần mềm trên Android và triển khai trên chợ ứng dụng của Google Play Store.</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Đề tài do học viên đề xuất và tham khảo ý kiến của giáo viên, học viên vừa học vừa thiết kế phần mềm dưới sự hướng dẫn của giáo viên.</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">A. THÔNG TIN ĐÀO TẠO</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"font-size: 16px;\">Ngày học:&nbsp;</span></strong></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Thứ 2 – Thứ 6, Thứ 3 – Thứ 5, Thứ 4 – Thứ 7 : 18h – 21h</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Sáng thứ 7 – CN: 8h30 – 11h30</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size: 16px;\">Chiều thứ 7 – CN: 14h – 17h</span><br />\r\n<br />\r\n<span style=\"font-size:16px;\">Lịch khai giảng: <a href=\"http://vnskills.com/lich-khai-giang.html\">TẠI ĐÂY</a></span><br />\r\n<br />\r\n<strong style=\"font-family: \'times new roman\', times, serif; font-size: 16px;\">Địa điểm</strong><span style=\"font-size: 16px;\">: Phòng 1706 – Toà nhà A1 – 229 Phố Vọng, Hai Bà Trưng, HN</span></span><br />\r\n<br />\r\n<br />\r\n<strong style=\"font-size: 12px; font-family: \'times new roman\', times, serif;\"><span style=\"font-size: 16px;\">HỌC PHÍ VÀ CHÍNH SÁCH HỖ TRỢ</span></strong><br />\r\n&nbsp;</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Helvetica, sans-serif; font-size: 13px;\">\r\n<div style=\"margin: 0px; padding: 0px; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px;\">\r\n<div style=\"margin: 0px; padding: 0px;\">\r\n<p style=\"margin: 0px; padding: 0px;\"><b style=\"margin: 0px; padding: 0px; font-size: 20px; color: rgb(255, 0, 0); font-family: \'times new roman\', times, serif;\">CHÀO MỪNG NĂM HỌC MỚI&nbsp;</b><br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</p>\r\n\r\n<div style=\"margin: 0px; padding: 0px;\">\r\n<div style=\"margin: 0px; padding: 0px; text-align: left;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><font style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><b style=\"margin: 0px; padding: 0px;\"><em style=\"margin: 0px; padding: 0px;\">Vnskills chúc bạn một năm học mới gặt hái được thật nhiều thành công. Đồng hành cùng các bạn trên con đường chinh phục kiến thức, Vnskills Academy xin gửi tới bạn chương trình ưu đãi cực hấp dẫn trong mùa tựu trường này</em></b></span></font></span></span></div>\r\n\r\n<div style=\"margin: 0px; padding: 0px;\"><br style=\"margin: 0px; padding: 0px;\" />\r\n<img alt=\"\" src=\"http://vnskills.com/hinhanh/images/banner/baner-thang-9.jpg\" style=\"width: 715px; height: 238px;\" /></div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px;\">\r\n<div style=\"margin: 0px; padding: 0px; text-align: center;\">&nbsp;</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><strong style=\"margin: 0px; padding: 0px;\">Chi tiết chính sách hỗ trợ:</strong></span></span></span><br />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\n<font face=\"times new roman, times, serif\" style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\">Chương trình áp dụng khi đăng ký khóa học Lập trình Android tại các lớp</span></font>\r\n\r\n<div style=\"margin: 0px; padding: 0px;\">\r\n<div style=\"margin: 0px; padding: 0px;\"><br />\r\n<span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">&nbsp;- Khai giảng lớp 1: &nbsp;29/9/2015&nbsp;</span></span></div>\r\n\r\n<div style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">Lịch học : Thứ 2 - 4 hàng tuần<br style=\"margin: 0px; padding: 0px;\" />\r\nGiờ học: 18h - 21h<br />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;- Khai giảng lớp 2: 26/9/2015</span></span></div>\r\n\r\n<div style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">Lịch học: Chiều thứ 7 / CN<br style=\"margin: 0px; padding: 0px;\" />\r\nGiờ học: 14h - 17h</span></span><br />\r\n&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<p><strong style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">&nbsp;Học phí khóa học lập trình Android chuyên nghiệp</span></span></strong></p>\r\n',[site_id],[user_id],'khoa-hoc-lap-trinh-android-chuyen-nghiep',[now],[now],[user_id],'','','','','');
INSERT INTO categorypage (id, title, content, site_id, user_id, `alias`, created_time, modified_time, modified_by, meta_keywords, meta_description, meta_title, image_path, image_name) VALUES (null,'Khóa học thiết kế đồ họa cấp tốc','<h1 class=\"titleLeftRight\" style=\"margin: 0px; padding: 10px; color: rgb(224, 106, 36); font-size: 26px; line-height: 35px; border-radius: 5px 5px 0px 0px; font-family: arial, Helvetica, sans-serif;\"><span style=\"margin: 0px; padding: 0px;\">Khóa học thiết kế đồ họa cấp tốc</span></h1>\r\n\r\n<div class=\"boxmain\" style=\"margin: 0px; padding: 5px; font-family: arial, Helvetica, sans-serif; font-size: 13px;\">\r\n<p style=\"margin: 0px; padding: 0px; text-align: center;\"><img alt=\"\" src=\"http://vnskills.com/hinhanh/images/banner/tk%20do%20hoa%20phan%20mem1.jpg\" style=\"margin: 0px; padding: 0px; border: none; width: 715px; height: 272px;\" /><br style=\"margin: 0px; padding: 0px;\" />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px;\"><em style=\"margin: 0px; padding: 0px;\">Mã : PM.GSM</em></strong><em style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px;\">&nbsp;-&nbsp;&nbsp;<strong style=\"margin: 0px; padding: 0px;\">Thời lượng: 3 buổi/1 tuần</strong></em></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><strong style=\"margin: 0px; padding: 0px;\">1. MỤC TIÊU ĐÀO TẠO</strong><br style=\"margin: 0px; padding: 0px;\" />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\n<strong style=\"margin: 0px; padding: 0px;\">&nbsp; Khóa học thiết kế đồ họa cấp tốc&nbsp;</strong>được xây dựng nhằm cung cấp cho xã hội đội ngũ chuyên viên thiết kế sử dụng hiệu quả các phần mềm đồ họa như Adobe Photoshop, Adobe Illustrator, cũng như kiến thức về mỹ thuật thiết kế. Chương trình đào tạo dành cho những đối tượng muốn tham gia học nhanh và học công cụ chính hay sử dụng trong thiết kế.<br style=\"margin: 0px; padding: 0px;\" />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp; &nbsp;Kết thúc khoác học, học viên có thể ứng dụng các kiến thức trên để thực hiện dự án xây dựng bộ nhận dạng thương hiệu bao gồm thiết kế Logo, Business card, Poster, Quảng cáo, Brochure, Bảng hiệu, Trang phục, Bao bì, Nhãn đĩa, Album, Lịch, Catologue …</span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</p>\r\n\r\n<div style=\"margin: 0px; padding: 0px;\"><strong style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">2. ĐỐI TƯỢNG THAM GIA</span></span></strong><br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">- Bạn đang làm về thiết kế, muốn học nâng cao tay nghề hơn để sử dụng thành thạo các phần mềm Photoshop, illustrator, kiến thức về mỹ thuật.</span></span></div>\r\n\r\n<div style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">- Công việc của bạn chỉ đòi hỏi những phần mềm này, bạn muốn học nhanh nhất có thể mà vẫn ra làm được việc.</span></span></div>\r\n\r\n<div style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">- Hay đơn giản là bạn muốn thử sức xem có phù hợp với nghề thiết kế Đồ họa này không?<br style=\"margin: 0px; padding: 0px;\" />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\nCác bạn có thể lựa chọn những khóa học khác nhau của chúng tôi sao cho phù hợp nhất với nhu cầu của bạn.</span></span></div>\r\n\r\n<p style=\"margin: 0px; padding: 0px; text-align: justify;\"><br style=\"margin: 0px; padding: 0px;\" />\r\n<span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><strong style=\"margin: 0px; padding: 0px;\">3. THU HOẠCH SAU KHÓA HỌC</strong></span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\n<span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px;\">&nbsp; &nbsp;VnSkills đảm bảo rằng sau khi hoàn thành khóa học, học viên sẽ có đầy đủ kỹ năng và kiến thức để làm việc với môi trường thức tế, phức tạp và nhiều thử thách.</span><br style=\"margin: 0px; padding: 0px;\" />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\n<span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px;\">&nbsp; &nbsp; Sau khi hoàn thành khóa học, học viên sẽ làm chủ các công cụ thiết kế, có khả năng sáng tạo đồ họa, hiệu chỉnh hình ảnh, thiết kế quảng cáo 2D, thiết kế in ấn và xuất bản.</span><br style=\"margin: 0px; padding: 0px;\" />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\n<span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px;\">&nbsp; &nbsp; Sau khi kết thúc khóa học, học viên nắm được những kiến thức, kỹ năng, kỹ thuật áp dụng thực tế trong các chuyên ngành Mỹ thuật quảng cáo, Đồ họa chế bản, In ấn… Học viên có thể làm làm đồ họa cho các studio ảnh, các nhà xuất bản hay các đơn vị quảng cáo, các công ty thiết kế hay các nhà xuất bản.</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; text-align: justify;\">&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; font-size: 22px;\"><u style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><strong style=\"margin: 0px; padding: 0px;\">NỘI DUNG CHI TIẾT KHÓA HỌC</strong></span></u></span><br style=\"margin: 0px; padding: 0px;\" />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px; font-size: 20px;\"><strong style=\"margin: 0px; padding: 0px;\">Đồ họa xử lý ảnh - ADOBE PHOTOSHOP</strong></span><br style=\"margin: 0px; padding: 0px;\" />\r\n<span style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><strong style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><strong style=\"margin: 0px; padding: 0px;\"><em style=\"margin: 0px; padding: 0px;\">&nbsp;</em></strong></span></strong></span></span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n<strong style=\"margin: 0px; padding: 0px;\">1. Giới thiệu:&nbsp;</strong></span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">&nbsp; &nbsp;Addobe Photoshop được đánh giá là phần mềm dẫn đầu thị trường về sửa ảnh bitmap và bạn có thể làm được hơn nhiều điều với Photoshop. Những công cụ sáng tạo của nó giúp bạn làm được những kết quả thật phi thường. Đồng thời với nhiều hiệu ứng biên tập, xử lý và biến đổi hình ảnh công việc của bạn sẽ được giải quyết một cách thật sự nhanh chóng.<br style=\"margin: 0px; padding: 0px;\" />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp; &nbsp;Adobe Photoshop là phần mềm không thể thiếu của các nhiếp ảnh gia, các nhà thiết kế đồ hoạ, thiết kế web và biên tập video và&nbsp; nó có khả năng tương thích với hầu hết các chương trình đồ họa khác của Adobe như Adobe Illustrator, Adobe Premiere, After After Effects và Adobe Encore.</span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px;\">&nbsp;</p>\r\n\r\n<div style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><b style=\"margin: 0px; padding: 0px;\">2. Mục đích khóa học</b></span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</div>\r\n\r\n<p style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">Khóa học Photoshop trang bị cho học viên:</span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin: 0px 0px 0px 40px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px;\">•</span><strong style=\"margin: 0px; padding: 0px;\">&nbsp;</strong>Phương pháp sửa ảnh (retouching);</span></span></p>\r\n\r\n<p style=\"margin: 0px 0px 0px 40px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px;\">•</span>&nbsp;Ghép ảnh (composing);</span></span></p>\r\n\r\n<p style=\"margin: 0px 0px 0px 40px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px;\">•</span><strong style=\"margin: 0px; padding: 0px;\">&nbsp;</strong>Phục chế ảnh (restoration);</span></span></p>\r\n\r\n<p style=\"margin: 0px 0px 0px 40px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px;\">•</span><strong style=\"margin: 0px; padding: 0px;\">&nbsp;</strong>Tô màu tranh ảnh (painting);</span></span></p>\r\n\r\n<p style=\"margin: 0px 0px 0px 40px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px;\">•</span>&nbsp;Thiết kế quảng cáo;</span></span></p>\r\n\r\n<p style=\"margin: 0px 0px 0px 40px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px;\">•</span><strong style=\"margin: 0px; padding: 0px;\">&nbsp;</strong>Thiết kế trang web;</span></span></p>\r\n\r\n<p style=\"margin: 0px 0px 0px 40px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px;\">•</span><strong style=\"margin: 0px; padding: 0px;\">&nbsp;</strong>Vẽ các loại tranh (matte painting và nhiều thể loại khác);</span></span></p>\r\n\r\n<p style=\"margin: 0px 0px 0px 40px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px;\">•</span>&nbsp;Vẽ texture cho các chương trình 3D...</span></span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\">Tất cả kỹ năng đó của photoshop gần như là mọi hoạt động liên quan đến ảnh bitmap.</span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif;\"><span style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><strong style=\"margin: 0px; padding: 0px;\">3. Nội dung học</strong></span></span></span></span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n<br style=\"margin: 0px; padding: 0px;\" />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài 1:</strong><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp;Giới thiệu chung về chương trình Adobe Photoshop</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các thao tác với giao diện của chương trình</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các công cụ làm việc với vùng chọn</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cách cắt ghép cơ bản</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài 2:</strong><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp;Thao tác với Layer</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các lệnh Transform</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Ứng dụng Feather với vùng chọn</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài 3:&nbsp;</strong><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Các thao tác Crop hình ảnh, các lệnh ImagerSize, CanvasSize</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Các thao tác với màu sắc: Solid Color, Gradient Color</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Biên tập và sử dụng Pattern</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài 4:</strong><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&nbsp;Các công cụ trong nhóm tô vẽ, chỉnh sửa</span><br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\" />\r\n<strong style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">Bài 5:</strong><span style=\"margin: 0px; padding: 0px; font-family: \'times new roman\', times, serif; font-size: 16px; text-align: justify;\">&n