INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories14,'Thiết kế đồ họa chuyên nghiệp','thiet-ke-do-hoa-chuyen-nghiep',1500000.00,3000000.00,1,0,'/media/images/159/course/ava/','719_1442485613_12655fa956d33c73.jpg',[now], [now],0,'30 Buổi',29,'T2(8h),T4(4h),6(14h)',1442768400,1444928400,'    Khóa học Thiết kế Đồ họa chuyên nghiệp  được xây dựng nhằm cung cấp cho xã hội đội ngũ chuyên viên thiết kế sử dụng hiệu quả các phần mềm đồ họa như Adobe Photoshop, Adobe Illustrator, Adobe InDesign, Corel Draw...',0,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories14,'Khóa học photoshop','khoa-hoc-photoshop',1250000.00,2000000.00,1,0,'/media/images/159/course/ava/','330_1442486004_61755fa96f4d7ff2.jpg',[now], [now],0,'20 Buổi',19,'Thứ 3,5,chủ nhật',1442682000,1445706000,'Adobe Photoshop được đánh giá là phần mềm dẫn đầu thị trường về sửa ảnh bitmap và được coi là chuẩn cho các ngành liên quan tới chỉnh sửa ảnh. Từ phiên bản Photoshop 7.0 ra đời năm 2002 Photoshop đã làm nên một cuộc cách mạng về ảnh bitmap. Phiên bản mới nhất hiện nay là Adobe Photoshop CS6.',0,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories14,'Khóa học Adobe Illustrator','khoa-hoc-adobe-illustrator',1500000.00,2200000.00,1,0,'/media/images/159/course/ava/','760_1442486797_40555fa9a0d800ca.jpg',[now], [now],0,'20',19,'Thứ 2,4,6',1442768400,1445533200,'Adobe Illustrator là chương trình đồ họa vector, tạo nền tảng cần thiết cho nghề thiết kế đồ họa. Illustrator tạo hình dạng cơ bản: thông qua lệnh và công cụ, sao chép và kết hợp đối tượng để tạo hình dạng mới, chọn và thay đổi các phần trong đối tượng bằng công cụ Selection, tô vẽ đối tượng, cung cấp nhiều hiệu ứng 3D để vẽ các hình khối, vật thể, xử lý các hình ảnh liên quan đến công việc dàn trang báo chí.\r\n',0,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories14,'Khóa học thiết kế đồ họa cấp tốc','khoa-hoc-thiet-ke-do-hoa-cap-toc',1700000.00,2500000.00,1,0,'/media/images/159/course/ava/','402_1442487086_86855fa9b2e1143b.jpg',[now], [now],0,'20',14,'Thứ 3,5, chủ nhật',1443459600,1446224400,'Khóa học thiết kế đồ họa cấp tốc được xây dựng nhằm cung cấp cho xã hội đội ngũ chuyên viên thiết kế sử dụng hiệu quả các phần mềm đồ họa như Adobe Photoshop, Adobe Illustrator, cũng như kiến thức về mỹ thuật thiết kế. Chương trình đào tạo dành cho những đối tượng muốn tham gia học nhanh và học công cụ chính hay sử dụng trong thiết kế.',0,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories15,'Khóa học Thiết kế web chuyên nghiệp','khoa-hoc-thiet-ke-web-chuyen-nghiep',1500000.00,2200000.00,1,0,'/media/images/159/course/ava/','932_1443840287_408560f411f11faa.jpg',[now], [now],0,'',0,'',1442336400,1442336400,'Khóa học WEBPLUS cung cấp kỹ năng thiết kế web, học lập trình web, thành thạo ngôn ngữ lập trình PHP - MySQL, quản trị và phát triển website.\r\n\r\nNâng cao năng lực cạnh tranh toàn diện của học viên trong ngành Công nghệ web & Đa truyền thông là tiêu chí để khóa học WEB.PLUS được xây dựng. Học viên được trang bị các kiến thức:\r\n',0,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories15,'Khóa học WordPress','khoa-hoc-wordpress',999000.00,1500000.00,1,0,'/media/images/159/course/ava/','480_1442543899_93855fb791bb9c72.jpg',[now], [now],0,'20',14,'',1443114000,1446138000,'WordPress là gì?  WordPress là phần mềm mã nguồn mở được cung cấp miễn phí, sử dụng ngôn ngữ lập trình PHP và hệ cơ sở dữ liệu MySQL.\r\n\r\nWordPress có ưu điểm gì? Ưu điểm của WordPress là ai cũng có thể sử dụng để xây dựng một website hay blog. Bạn có thể xây dựng 1 trang web hoàn chỉnh, bảo mật cao bằng Wordpress mà không cần phải có kiến thức lập trình.\r\n \r\n\r\n',0,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories15,'Khóa học Thiết kế Giao diện Website','khoa-hoc-thiet-ke-giao-dien-website',1500000.00,2200000.00,1,0,'/media/images/159/course/ava/','645_1442544424_43555fb7b285bb3f.jpg',[now], [now],0,'20',9,'Thứ 2,4,6',1443373200,1447347600,'Khóa học HTML & CSS tại Trà My hướng dẫn học thiết kế giao diện web với Photoshop, HTML5, CSS3, jQuery. Bạn sẽ được học HTML cơ bản và nâng cao.\r\n',0,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories15,'Khóa học lập trình PHP & MySQL','khoa-hoc-lap-trinh-php--mysql',1500000.00,2000000.00,1,0,'/media/images/159/course/ava/','385_1442544722_99855fb7c5225bd6.jpg',[now], [now],0,'18',14,'Thứ 3, 5, chủ nhật ',1443459600,1445706000,'Khóa học lập trình PHP trang bị cho bạn kiến thức học lập trình web bằng ngôn ngữ php cơ bản và nâng cao uy tín chất lượng đào tạo tốt nhất Hà Nội.\r\n ',0,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories16,'3D đồ họa động quảng cáo và kỹ xảo','3d-do-hoa-dong-quang-cao-va-ky-xao',1500000.00,2000000.00,1,0,'/media/images/159/course/ava/','486_1442547344_54255fb86905b277.jpg',[now], [now],0,'25',19,'Thứ 2,4,6',1443718800,1447606800,'Khóa học nhằm trang bị cho học viên kỹ năng về các phần mềm thông dụng trong ngành thiết kế đồ họa động Autodesk Maya, Adobe After Effects và Adobe Premiere.\r\nHọc viên sẽ học và thực hành trong từng nội dung học để thực hiện tốt từ việc lên ý tưởng, phân cảnh, dàn dựng 3D, dàn dựng xử lý hậu kỳ cùng các công cục tiện ích 3D để tạo ra mô hình, kết cấu, âm thanh và hình ảnh chuyển động trong lĩnh vực hoạt hình, phim ảnh, trò chơi giải trí, truyền hình.',0,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories16,'3D Kiến trúc - Nội & Ngoại thất','3d-kien-truc---noi--ngoai-that',1800000.00,2500000.00,1,0,'/media/images/159/course/ava/','667_1442551737_67455fb97b9d5755.jpg',[now], [now],0,'2 tháng',14,'Thứ 3, 5, chủ nhật',1443286800,1446656400,'Khóa học trang bị cho học viên kiến thức và kỹ năng thiết kế trong Kiến trúc, Nội - Ngoại thất. Nói tới Thiết kế Kiến trúc hay Nội ngoại thất, trước đây người ta thường hình dung ra các bản vẽ thiết kế trên AutoCAD - công cụ giúp người thiết kế thể hiện chính xác về mặt kỹ thuật các ý tưởng của mình. Tuy nhiên, về phía khách hàng, không phải ai cũng có thể hình dung được chính xác về diện mạo, màu sắc của công trình tương lai khi xem bản vẽ thiết kế 2D.',24,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories16,'Chuyên đề 3Ds Max','chuyen-de-3ds-max',1450000.00,1800000.00,1,0,'/media/images/159/course/ava/','148_1442549368_25255fb8e78a4ae8.jpg',[now], [now],0,'20',14,'Thứ 2,4,6',1443978000,1446138000,'Khóa học giúp học viên có kỹ năng và kiến thức:  Xử lý trực quan trong môi trường 3D, dễ dàng thử nghiệm, thay đổi, tìm tòi các phương án thiết kế; Xử lý ánh sáng, gán chất liệu cho bản vẽ, đem lại hiệu quả cao về thẩm mỹ thị giác cho người xem, đem lại cảm giác thực cho bản vẽ',0,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories18,'Chuyên đề AutoCad','chuyen-de-autocad',1400000.00,1800000.00,1,0,'/media/images/159/course/ava/','231_1442549745_75155fb8ff1d3de5.jpg',[now], [now],0,'20',14,'Thứ 2,4,6',1443546000,1447002000,'  Khóa học AutoCad cung cấp cho học viên các lệnh thao tác trên bản vẽ kỹ thuật, phương pháp vẽ hình chiếu, tạo khối và mô hình, các lệnh hiệu chỉnh hình học, vẽ mặt bằng. . . cho các công trình nhà ở, biệt thự,  không gian làm việc, văn phòng trang trí. . .\r\n ',0,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories17,'Chuyên đề Adobe After Efects','chuyen-de-adobe-after-efects',1500000.00,2000000.00,1,0,'/media/images/159/course/ava/','393_1442549994_9055fb90eac4e51.png',[now], [now],0,'20',14,'Thứ 3,5, Chủ nhật',1444842000,1448125200,'Adobe After Effects  là phần mềm đồ họa tổng hợp được phát triển bởi hãng Adobe Systems. Phần mềm này được ứng dụng nhiều trong  đồ họa động và kỹ xảo chuyên nghiệp. Chức năng chính của After Effects là tạo ra được những chuyển động đồ họa ấn tượng, đẹp mắt. Đây là một công cụ tuyệt vời cho ngành thiết kế đồ họa, kỹ xảo trong quảng cáo hay game hoặc truyền hình. Đặc biệt, After Effects tương thích với nhiều phần mềm khác của Adobe như Illustrator, Photoshop, Premiere Pro, Encore hay Flash…',0,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories17,'Khóa học nhiếp ảnh dành cho mọi người','khoa-hoc-nhiep-anh-danh-cho-moi-nguoi',1200000.00,1600000.00,1,0,'/media/images/159/course/ava/','350_1442550413_97855fb928d7c4a5.jpg',[now], [now],0,'15',14,'Thứ 2, 4, 6 ',1442941200,1447347600,'Khóa học Nhiếp ảnh dành cho mọi người không chỉ đáp ứng nhu cầu giải trí hoặc  thỏa mãn niềm mê với bộ môn nghệ thuật nhiếp ảnh của người học. Khóa học Nhiếp ảnh dành cho mọi người còn trang bị cho học viên kiến thức nền tảng về thẩm mỹ nhiếp ảnh, kỹ năng chụp ảnh đời sống và nghề nhiếp ảnh nói chung.',0,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories18,'Khóa học tin học văn phòng chuyên nghiệp','khoa-hoc-tin-hoc-van-phong-chuyen-nghiep',1250000.00,1800000.00,1,0,'/media/images/159/course/ava/','802_1442550660_88355fb9384d6683.jpg',[now], [now],0,'1 tháng',12,'Thứ 3, 5, chủ nhật ',1443286800,1445706000,'Chương trình Tin học văn phòng đào tạo theo chuẩn của Microsoft Office. Học viên được cung cấp các kỹ năng tin học văn phòng chuyên nghiệp theo chuẩn, sử dụng các bộ công cụ văn phòng chuẩn:  Word, Excel, Power Point, Outlook, sử dụng và khai thác tài nguyên Internet,  làm chủ được PC, sử dụng hệ điều hành Window.',12,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories19,'Khóa học lập trình Android chuyên nghiệp','khoa-hoc-lap-trinh-android-chuyen-nghiep',1650000.00,2200000.00,1,0,'/media/images/159/course/ava/','204_1442550937_73055fb94997ac08.jpg',[now], [now],0,'2 tháng',18,'Thứ 2, 4 ,6',1444582800,1449853200,'Thiết bị Android đang thống lĩnh thị trường di dộng và có tốc độ tăng trưởng nhanh hơn bất ky nền tảng di động nào khác. Hiện nay, Android là hệ điều hành được sử dụng phổ biến nhất trên toàn cầu, chiếm khoảng 80% smartphone.\r\nLập trình Android đang trở thành một sự lựa chọn tuyệt vời trong hành trình phát triển ứng dụng trên điện thoại di động với 1 tỉ người sử dụng , 1,5 triệu thiết bị mới được kích hoạt mỗi ngày…\r\n ',24,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories19,'Khóa học lập trình Java cơ bản','khoa-hoc-lap-trinh-java-co-ban',1500000.00,2000000.00,1,0,'/media/images/159/course/ava/','34_1442551146_7555fb956aad486.jpg',[now], [now],0,'3 tháng',14,'Thứ 2, 4, 6',1445187600,1451581200,'Khóa học Java căn bản bao gồm tất cả nội dung kiến thức về java core (J2SE). Đáp ứng nhu cầu kiến thức và kỹ năng về ngôn ngữ lập trình java và là bước đệm cho các lập trình viên muốn tìm hiểu về ngôn ngữ java nâng cao hay học tiếp ngôn ngữ lập trình Android',36,1);
INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`, `ishot`) VALUES (null,[site_id],@edu_course_categories14,'Khóa học Adobe Illustrator','khoa-hoc-adobe-illustrator',1600000.00,2500000.00,1,0,'/media/images/159/course/ava/','615_1442551404_19455fb966ce53c0.jpg',[now], [now],0,'1 tháng',10,'Thứ 3, 5, chủ nhật',1445706000,1448730000,'Adobe Illustrator là gì?\r\n\r\nAdobe Illustrator là chương trình đồ họa vector, tạo nền tảng cần thiết cho nghề thiết kế đồ họa. Illustrator tạo hình dạng cơ bản: thông qua lệnh và công cụ, sao chép và kết hợp đối tượng để tạo hình dạng mới, chọn và thay đổi các phần trong đối tượng bằng công cụ Selection, tô vẽ đối tượng, cung cấp nhiều hiệu ứng 3D để vẽ các hình khối, vật thể, xử lý các hình ảnh liên quan đến công việc dàn trang báo chí.',12,1);
