INSERT INTO site_introduces (site_id,user_id, title, sortdesc, description, image_path, image_name, meta_keywords, meta_description, meta_title, created_time, modified_time) VALUES ([site_id],[user_id],'VỀ CHÚNG TÔI','Lời đầu tiên cho phép chúng tôi tập thể công ty sự kiện Đại Việt, được gửi tới quý khách hàng lời chào thân ái, lời kính chúc sức khỏe, gia đình hạnh phúc, kinh doanh thành đạt.','<p>Lời đầu tiên cho phép chúng tôi tập thể công ty sự kiện Đại Việt, được gửi tới quý khách hàng lời chào thân ái, lời kính chúc sức khỏe, gia đình hạnh phúc, kinh doanh thành đạt.</p>\r\n\r\n<p>Công ty CP giải trí &amp; tổ chức sự kiện Đại Việt được Sở kế hoạch &amp; Đầu tư tỉnh BRVT cấp chứng nhận kinh doanh lần đầu số 3501986364 cấp ngày 7/12/2011 có trụ sở chính tại 115 Ngô Đức Kế- P7- TP VT Qua ba năm hoạt động từ con số 6 nhân viên đến nay chúng tôi đã có hơn 100 nhân viên và cộng tác viên, chúng tôi luôn tự hào với đội ngũ nhân viên năng động, trẻ trung có trình độ chuyên môn cao là các chuyên gia kinh tế, các đạo diễn, họa sỹ, biên đạo, ca sỹ, nghệ sỹ nổi tiếng, với lực lượng nhân sự tinh thông và giàu kinh nghiệm Công ty Đại Việt đã và đang nhận được sự hợp tác của các doanh nghiệp lớn trong và ngoài tỉnh như: Liên doanh Việt Nga- Vietsovpetro - PTSC – DIC – BIDV – Agribank - Sân Bay Trực thăng Miền Nam, Các Trung Tâm Văn Hóa Tỉnh, TP,Huyện, các sở ban ngành, các cơ quan nhà nước, trường học ..., Các sự kiện khai trương, khánh thành, động thổ, ký kết đầu tư, hội thảo hội nghị, hội chợ, triển lãm, quảng bá thương hiệu – Sản phẩm, các chương trình nghệ thuật, ca nhạc- thời trang do CT Đại Việt thực hiện và tổ chức đều thành công, tạo được tiếng vang, để lại ấn tượng tốt đẹp trong lòng khách hàng cũng như người đến dự. Với phương châm “Giá cả cạnh tranh, dịch vụ chuyên nghiệp” chúng tôi luôn sẵn sàng cung cấp dịch vụ hoàn hảo mọi lúc, mọi nơi, bất kỳ sự kiện lớn hay nhỏ cho khách hàng.&nbsp;</p>\r\n\r\n<p>Qua lá thư ngỏ chân thành này, chúng tôi mong muốn được hợp tác, song hành trên hoạt động kinh doanh của quý khách hàng. Trân trọng cảm ơn!</p>\r\n\r\n<p style=\"float:right\">&nbsp; &nbsp; &nbsp; &nbsp;Tổng Giám đốc<br />\r\nNGUYỄN QUỐC CƯỜNG</p>\r\n','','','','','',[now],[now]);
