INSERT INTO categorypage (id, title, content, site_id, user_id, `alias`, created_time, modified_time, modified_by) VALUES (null,'Tổng quan','<p style=\"text-align: center;\"><img alt=\"\" src=\"http://vincent.bandonhadat.com/public/uploads/uploaded/Vincent/vincent_banner.jpg\" style=\"width: 600px; height: 108px;\" />&nbsp; &nbsp;</p>\r\n\r\n<p style=\"text-align: center;\">&nbsp; &nbsp; &nbsp;&nbsp;<span style=\"font-size:20px;\"><strong><span style=\"color:#FF0000;\">TỔNG QUAN</span></strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Công ty Cổ phần Tư vấn Bất động sản Hoàng Anh&nbsp;có trụ sở chính tại Hà Nội, hiện là đơn vị uy tín hàng đầu trong lĩnh vực tư vấn, phân phối và tiếp thị Bất động sản trên lãnh thổ Việt Nam. Với đội ngũ nhân viên chuyên nghiệp, năng động, nhiệt tình và đầy sáng tạo - Venusland càng khẳng định uy tín mang đến cho Quý khách hàng các sản phẩm và dịch vụ tốt nhất.</span></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><img alt=\"Công ty CP Tư vấn Bất Động Sản Venusland\" src=\"http://venusland.vn/images/logo-vnl.png\" />&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Lĩnh vực bất động sản đang ngày càng phát triển mạnh mẽ với sự gia tăng nhanh chóng của các loại hình bất động sản cùng với một mức giá hợp lý hơn. Tuy nhiên, thị trường vẫn luôn tiềm ẩn những bất lợi cho người mua và người bán. Cùng với đó là câu hỏi làm thế nào để tìm kiếm được thông tin bất động sản, mua bán nhà đất, thuê và cho thuê nhanh chóng, tin cậy, chính xác giúp cho các giao dịch bất động sản trở nên đơn giản, thuận tiện và hiệu quả hơn.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Thấu hiểu được những tồn tại của thị trường, nguyện vọng và tâm lý của khách hàng cùng nhiều năm kinh nghiệm hoạt động trong lĩnh vực tư vấn bất động, với tâm niệm bảo vệ tối đa lợi ích cho khách hàng của mình, chắc chắn Venusland sẽ mang đến cho quý khách hàng sự hài lòng tuyệt đối!</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Venusland vinh dự được làm việc với các đối tác, các chủ đầu tư lớn trên toàn quốc nhằm phân phối các dự án tốt nhất đến Quý khách hàng.</span></span></p>\r\n',[site_id],[user_id],'tong-quan',[now],[now],[user_id]);
