INSERT INTO albums (album_id, album_name, album_description, album_type, photocount, `alias`, site_id, user_id, meta_keywords, meta_description, avatar_path, avatar_name, avatar_id, created_time, modified_time, cat_id) VALUES (null,'BIỆT THỰ VINHOMES CENTRAL PARK','<h2 style=\"margin: 5px 5px 10px; padding: 0px;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"color:#FF0000;\"><font style=\"line-height: 20px;\"><b>Biệt thự Vinhomes Central Park tại Tân Cảng - khu đô thị hiện đại và cao cấp Việt Nam đã chính thức khởi công. với Quy mô trên 10.000 căn hộ và 93 căn biệt thự cao cấp. đã mở bán tại thời điểm 2015&nbsp;</b></font></span></span></span></h2>\r\n\r\n<h2 style=\"margin: 5px 5px 10px; padding: 0px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><strong><span style=\"font-size:16px;\"><font style=\"line-height: 20px; font-size: small; color: rgb(0, 0, 0);\">Quý khách có nhu cầu đặt chỗ mua biệt thự vui lòng liên hệ phòng kinh doanh</font></span></strong></span></h2>\r\n\r\n<div>\r\n<h2 style=\"text-align: center; margin-right: auto; margin-left: auto;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><a href=\"http://www.duanvinhomes.com.vn/biet-thu-vinhomes/hinh%20biet%20thu%20khu%20central%20park.jpg?attredirects=0\" imageanchor=\"1\" style=\"color: rgb(0, 85, 150) !important; border-bottom-style: none; background: none;\"><img border=\"0\" src=\"http://www.duanvinhomes.com.vn/_/rsrc/1418958578349/biet-thu-vinhomes/hinh%20biet%20thu%20khu%20central%20park.jpg\" style=\"border-width: 0px; border-style: initial; padding: 0px;\" /></a></span></span></h2>\r\n</div>\r\n\r\n<h2 style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><i><font color=\"#444444\">Khu biệt thự Vinhomes&nbsp;</font></i></span></span></h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<div>\r\n<h2 style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"color: rgb(0, 0, 0);\">Làm chủ tại khu biệt thự cao cấp tại Vinhomes bạn không chỉ sở hữu về giá trị Bất Động Sản giữa trung tâm của Thành phố Hồ Chí Minh với hệ thống tiện ích, tiện nghi cao cấp Trung tâm mua sắm mà nơi đây còn cho bạn một cuộc sống sang trọng, đẳng cấp,yên bình và một bầu không khí trong lành giữa một thành phố lớn nhất Việt Nam.</span></span></span></h2>\r\n\r\n<h2 style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"color: rgb(0, 0, 0);\">Khu&nbsp;<b>biệt thự Vinhomes Central Park</b>&nbsp;được đầu tư hệ thống hạ tầng tiện ích và dịch vụ đồng bộ, hiện đại như khu phức hợp thể thao, bể bơi, sân chơi trẻ em, hầm gửi xe thông minh, sảnh Lounge sang trọng, khu Club-House 5 sao, hệ thống giáo dục chất lượng cao và y tế đẳng cấp. Các hạng mục này đều do những thương hiệu đã được khẳng định về uy tín và chất lượng, thuộc Tập đoàn Vingroup triển khai như hệ thống trường học liên cấp Vinschool; bệnh viện đa khoa quốc tế Vinmec; hệ thống quản lý và dịch vụ bất động sản Vinhomes</span></span></span></h2>\r\n</div>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h2><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<a href=\"http://www.duanvinhomes.com.vn/biet-thu-vinhomes/mat-bang-biet-thu-vinhomes-central-park/so%20do%20mat%20bang.gif?attredirects=0\" imageanchor=\"1\" style=\"color: rgb(0, 85, 150) !important; border-bottom-style: none; background: none;\" target=\"_blank\"><img alt=\"sơ đồ mặt bằng biệt thự vinhomes\" border=\"0\" src=\"http://www.duanvinhomes.com.vn/_/rsrc/1430542928994/biet-thu-vinhomes/mat-bang-biet-thu-vinhomes-central-park/so%20do%20mat%20bang.gif\" style=\"border-width: 0px; border-style: initial; padding: 0px; width: 807px;\" /></a></span></span></h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<div>\r\n<div style=\"color: rgb(0, 85, 150); font-family: arial, sans-serif; line-height: normal;\">\r\n<ul style=\"color: rgb(0, 0, 0); margin: 0px; padding-right: 0px; padding-left: 0px;\">\r\n	<li style=\"color: rgb(51, 51, 51); text-align: left; padding: 0px; margin: 0px; list-style: none; border-bottom-width: 1px; border-bottom-color: rgb(204, 204, 204); border-bottom-style: dashed; width: 807px; background-image: url(http://www.duanvinhomes.com.vn/biet-thu-vinhomes);\">\r\n	<h2>&nbsp;</h2>\r\n	</li>\r\n</ul>\r\n</div>\r\n\r\n<h2 style=\"color: rgb(0, 85, 150); font-family: arial, sans-serif; line-height: normal;\">&nbsp;</h2>\r\n</div>\r\n',0,4,'biet-thu-vinhomes-central-park',[site_id],[user_id],'','','/media/images/albums/145/1/','874_1440666959_89155ded54f9ec92.jpg',800,[now],[now],@albums_categories25);
