INSERT INTO albums (album_id, album_name, album_description, album_type, photocount, `alias`, site_id, user_id, meta_keywords, meta_description, avatar_path, avatar_name, avatar_id, created_time, modified_time, cat_id) VALUES (null,'Biệt thự tại khu đô thị Phú Mỹ','<p><span style=\"color: rgb(68, 68, 68); font-family: arial; font-size: 14px; font-weight: bold; line-height: 18px;\">Chủ đầu tư Phú Mỹ Hưng mở bán dự án biệt thự lâu đài Chateau vào ngày 8/2 với mức giá từ khoảng 30 tỷ đồng một căn.</span></p>\r\n\r\n<p><span style=\"color: rgb(68, 68, 68); font-family: arial; font-size: 14px; font-weight: bold; line-height: 18px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"http://m.f25.img.vnecdn.net/2015/01/09/Chateau-hinh-21-8509-1420791807.jpg\" style=\"width: 800px; height: 592px;\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Thuộc&nbsp;khu Nam Viên của Phú Mỹ Hưng&nbsp;(quận 7, TP HCM), biệt thự lâu đài Chateau gồm 131 căn được xây dựng trên tổng diện tích 11,6ha. Mật độ xây dựng chưa đến 20%, hơn 80% diện tích đất còn lại dành cho không gian xanh,&nbsp;giao thông nội khu và tiện ích. &nbsp;</span></span></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"http://m.f25.img.vnecdn.net/2015/01/09/Chateau-hinh-1-7325-1420791808.jpg\" style=\"width: 800px; height: 400px;\" /></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"line-height: normal; background-color: rgb(245, 245, 245);\">&nbsp;Biệt thự lâu đài Chateau kiểu đơn lập có diện tích sàn xây dựng từ khoảng 517m2 đến 700m2, gồm một&nbsp;tầng hầm và 3 tầng nổi. Mỗi biệt thự đều có vườn trước hoặc sau nhà.&nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">&nbsp;Khu bể bơi&nbsp; </span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;<img alt=\"\" src=\"http://m.f25.img.vnecdn.net/2015/01/09/Chateau-hinh-9-2858-1420791812.jpg\" style=\"width: 800px; height: 593px;\" /> &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n',0,3,'biet-thu-tai-khu-do-thi-phu-my',[site_id],[user_id],'','','/media/images/albums/145/1/','247_1440660272_78755debb30ea410.jpg',790,[now],[now],@albums_categories25);
INSERT INTO albums (album_id, album_name, album_description, album_type, photocount, `alias`, site_id, user_id, meta_keywords, meta_description, avatar_path, avatar_name, avatar_id, created_time, modified_time, cat_id) VALUES (null,'Biệt thự liền kề khu đô thị Vincom Villege','<p><strong><span style=\"color:#FF0000;\"><span style=\"font-size:14px;\">Khu biệt thự&nbsp;Hoa Lan Vinhomes Riverside&nbsp;nằm dọc trục đường Hoa Lan, tiếp giáp với dòng sông Đuống thơ mộng. Nếu bạn và gia đình muốn sở hữu một không gian riêng biệt, mật độ biệt thự không quá dày thì đây chắc chắn sẽ là một lựa chọn hoàn hảo.</span></span></strong></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0.85em 0px; padding: 0px; border: 0px rgb(255, 255, 255); font-family: Arial, Helvetica, Verdana, sans-serif; font-size: 14px; font-stretch: inherit; line-height: 23.1000003814697px; vertical-align: baseline; color: rgb(64, 63, 64); text-align: justify;\">Mùa hè, cư dân nơi đây luôn được hưởng không khí trong lành, thoáng đãng cùng hương thơm ngào ngạt của hoa lan từ làn gió mát lạnh phía dòng sông thổi về.</p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0.85em 0px; padding: 0px; border: 0px rgb(255, 255, 255); font-family: Arial, Helvetica, Verdana, sans-serif; font-size: 14px; font-stretch: inherit; line-height: 23.1000003814697px; vertical-align: baseline; color: rgb(64, 63, 64); text-align: justify;\"><a class=\"lightbox-added aligncenter fancybox image\" href=\"http://vinhomesriverside.villas/wp-content/uploads/sites/127/2015/06/biet-thu-hoa-lan-Vinhomes-Riverside.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px rgb(255, 255, 255); font-family: inherit; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; outline: 0px; max-width: 100%; clear: both; display: block; position: relative; color: rgb(45, 92, 136); float: none; text-align: center;\"><img alt=\"biet-thu-hoa-lan-Vinhomes-Riverside\" class=\"aligncenter wp-image-18\" height=\"431\" src=\"http://vinhomesriverside.villas/wp-content/uploads/sites/127/2015/06/biet-thu-hoa-lan-Vinhomes-Riverside.jpg\" style=\"box-sizing: border-box; margin: 10px auto; padding: 10px 0px; border-color: rgb(255, 255, 255); font-family: inherit; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; display: block; max-width: 100%; height: auto; clear: both; position: relative;\" width=\"700\" /></a></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0.85em 0px; padding: 0px; border: 0px rgb(255, 255, 255); font-family: Arial, Helvetica, Verdana, sans-serif; font-size: 14px; font-stretch: inherit; line-height: 23.1000003814697px; vertical-align: baseline; color: rgb(64, 63, 64); text-align: justify;\">Vinhomes Riverside Hoa Lan nằm trong quần thề&nbsp;<strong><span style=\"color:#FF0000;\">Vinhomes Riverside</span></strong><strong style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px rgb(255, 255, 255); font-family: inherit; font-size: inherit; font-style: inherit; font-variant: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; color: rgb(232, 154, 9);\">&nbsp;</strong>có tổng cộng 219 lô biệt thự, từ Hoa Lan 1 đến Hoa Lan 9, với diện tích đa dạng, từ 190-526m2.</p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0.85em 0px; padding: 0px; border: 0px rgb(255, 255, 255); font-family: Arial, Helvetica, Verdana, sans-serif; font-size: 14px; font-stretch: inherit; line-height: 23.1000003814697px; vertical-align: baseline; color: rgb(64, 63, 64); text-align: justify;\">Đầu tiên phải kể đến khu Hoa Lan 1 gồm 36 căn biệt thự gần vườn BBQ, sân tennies, Vincom Center. Thiết kế tại đây chủ yếu là những căn biệt thự song lập diện tích 250m2, với mặt trước là khu ẩm thực Nhật Bản Kimono Village và nhà trẻ. Một nửa mặt sau từ HL1 ô số 1 đến 18 nhìn sang khu Vườn Bốn Mùa và khoảng xanh mênh mông của mặt hồ rộng và trải dài tạo không gian rộng rãi, thoáng mát. Mặt trước của HL1 từ ô số 1 đến ô 18 có không gian sang khu ẩm thực Kimono, đây cũng là vị trí rất thuận tiện khi đến những khu tiện ích khác.</p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0.85em 0px; padding: 0px; border: 0px rgb(255, 255, 255); font-family: Arial, Helvetica, Verdana, sans-serif; font-size: 14px; font-stretch: inherit; line-height: 23.1000003814697px; vertical-align: baseline; color: rgb(64, 63, 64); text-align: justify;\">Nằm trên trục đường Hoa Lan trải dọc tiếp đến là khu biệt thự Hoa Lan 2 và Hoa Lan 3, ôm mặt trước trường quốc tế BVIS là những biệt thự diện tích trung bình từ 225m2. Đây sẽ là một lựa chọn tối ưu cho những gia đình muốn sở hữu biệt thự view đẹp, không gian thông thoáng.</p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0.85em 0px; padding: 0px; border: 0px rgb(255, 255, 255); font-family: Arial, Helvetica, Verdana, sans-serif; font-size: 14px; font-stretch: inherit; line-height: 23.1000003814697px; vertical-align: baseline; color: rgb(64, 63, 64); text-align: justify;\">Tiếp theo, dần xuống cuối là khu HL 4, 5, 6, 7 và 8 với biệt thự diện tích 225m2 tạo cảm giác yên tĩnh, phù hợp với những gia đình mong muốn có cảm giác yên bình trong ngôi nhà của mình.</p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0.85em 0px; padding: 0px; border: 0px rgb(255, 255, 255); font-family: Arial, Helvetica, Verdana, sans-serif; font-size: 14px; font-stretch: inherit; line-height: 23.1000003814697px; vertical-align: baseline; color: rgb(64, 63, 64); text-align: justify;\">Hiện tại các căn biệt thự tại Hoa Lan Vinhomes Riverside đã cơ bản hoàn thiện, quý khách hàng chỉ cần chuẩn bị nội thất bên trong là có thế dọn ngay về ở. Những khách hàng quan tâm muốn tìm hiểu thêm thông tin cũng như có như cầu&nbsp;mua bán Hoa Lan Vinhomes Riverside, vui lòng liên hệ:</p>\r\n\r\n<p>&nbsp;\r\n<div class=\"optin-monster-overlay\" id=\"om-wfvodguvg8-post\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, Verdana, sans-serif; font-size: 14px; font-stretch: inherit; line-height: 1; vertical-align: baseline; border-radius: 0px; float: none; -webkit-font-smoothing: antialiased; height: auto; outline: none; position: static; text-shadow: none; width: auto; visibility: visible; overflow: visible; box-shadow: none; -webkit-appearance: none; color: rgb(64, 63, 64); background-image: none; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">\r\n<div class=\"om-post-sample om-clearfix om-theme-sample om-custom-html-form\" id=\"om-post-sample-optin\" style=\"box-sizing: border-box; margin: 0px auto; padding: 20px; border: 0px rgb(255, 255, 255); font-family: inherit; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; position: relative; text-align: center; max-width: 100%; width: 833.296875px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">\r\n<div class=\"om-clearfix\" id=\"om-post-sample-optin-wrap\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px rgb(255, 255, 255); font-family: inherit; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both;\">\r\n<div class=\"om-clearfix\" data-om-action=\"selectable\" id=\"om-post-sample-header\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px rgb(255, 255, 255); font-family: inherit; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit;