INSERT INTO edu_course (`id`, `site_id`, `cat_id`, `name`, `alias`, `price`, `price_market`, `status`, `order`, `image_path`, `image_name`, `created_time`, `modified_time`, `viewed`, `time_for_study`, `number_of_students`, `school_schedule`, `course_open`, `course_finish`, `sort_description`, `number_lession`) VALUES (null,[site_id],@edu_course_categories21,'MÓN HÀN','mon-han',25.00,0.00,1,0,'/media/images/138/course/ava/','23_1439962455_91755d4155712e2a.png',[now], [now],0,'3 tháng',14,'chiều 3, 5, 7',1440608400,1450976400,'Món ăn Hàn Quốc luôn luôn có các loại gia vị như: dầu mè, toenchang (bột đậu nành lên men), nước tương (xì dầu), muối ăn, tỏi, gừng và koch’uchang (bột ớt đỏ).\r\n\r\nCách nấu nướng cũng thay đổi theo mùa, đặc biệt là trong mùa đông, thường hay dựa vào kim chi và các loại rau củ ngâm được bảo quản trong những lọ gốm chôn dưới nền đất ở trong sân nhỏ. Việc chuẩn bị món ăn thường rất công phu.',0);
