INSERT INTO site_introduces (site_id,user_id, title, sortdesc, description, image_path, image_name, meta_keywords, meta_description, meta_title, created_time, modified_time) VALUES ([site_id],[user_id],'Giới thiệu','MOTORSHIP \r\nNhanh nhất - Tiện lợi nhất - Tiết kiệm nhất - Thân thiện nhất','<p style=\"text-align: center;\"><img alt=\"\" src=\"http://w3ni230.web3nhat.net/data/media/167/files/4-y-tuong-kinh-doanh-online-doc-la-khong-lo-dung-hang-6.jpg\" style=\"width: 700px; height: 400px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(255, 0, 0);\"><strong style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-size: 14px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: tahoma, geneva, sans-serif;\">MOTORSHIP&nbsp;XIN GỬI LỜI CHÀO TRÂN TRỌNG NHẤT ĐẾN QUÝ KHÁCH HÀNG</span></span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Motorship&nbsp;thành lập&nbsp;với mong muốn khai thác, phát triển&nbsp;lĩnh vực&nbsp;giao hàng và thu tiền hộ của thị trường thương mại điện tử.&nbsp;Chúng tôi luôn nỗ lực cố gắng đẻ cung cấp cho Khách hàng những dịch vụ tốt nhất với phương châm: Nhanh nhất - Tiện lợi nhất &nbsp;- Giá cả hợp lý nhất.</span></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><img alt=\"\" src=\"http://w3ni230.web3nhat.net/data/media/167/images/image_1409707805.jpg\" style=\"width: 600px; height: 211px;\" /></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#FF0000;\"><strong><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Tầm Nhìn</span></span></strong></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Motorship mong muốn&nbsp;trở thành doanh nghiệp hàng đầu tại Việt Nam và khu vực Đông Nam Á trong việc cung cấp các giải pháp hoàn chỉnh tạo ra trải nghiệm mua bán thương mại vượt trội cho khách hàng.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Trở thành nền tảng xử lý và vận chuyển hàng hóa hàng đầu Đông Nam Á đến năm 2020.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#FF0000;\"><strong><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Sứ Mệnh</span></span></strong></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Xây dựng nền tảng vượt trội trong việc xử lý hàng hoá và giao nhận tập trung vào các yếu tố nhanh hơn, thông minh hơn, tối ưu chi phí hơn và gắn kết chặt chẽ với cuộc cách mạnh nguyên thương mại điện tử.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Xây dựng môi trường doanh nghiệp trong đó mỗi cá nhân được phát triển toàn diện để thành công.</span></span></p>\r\n\r\n<p><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\">Đem lại mức thu nhập và cuộc sống tốt cho đội ngũ nhân viên nhiệt tình và tâm huyết với Công Ty.</span></font></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#FF0000;\"><strong><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Triết lý kinh doanh</span></span></strong></span></li>\r\n</ul>\r\n\r\n<p><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\">Nỗ lực không ngừng - Đoàn Kết - Tăng trưởng - Vươn lên</span></font></p>\r\n\r\n<p style=\"text-align: center;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\"><img alt=\"\" src=\"http://w3ni230.web3nhat.net/data/media/167/images/homeart_hoan_tien.jpg\" style=\"width: 600px; height: 234px;\" /></span></font></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#FF0000;\"><strong><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\">Cam kết</span></font></strong></span></li>\r\n</ul>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Nhanh nhất</span></span></li>\r\n	<li><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\">Tiện lợi nhất</span></font></li>\r\n	<li><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\">Tiết kiệm nhất</span></font></li>\r\n	<li><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\">Hoàn lại 100% nêu không&nbsp;hài lòng về dịch vụ</span></font></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"color:#FF0000;\"><strong><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Giá trị cốt lõi</span></span></strong></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Với mong muốn mang đến kết quả tốt nhất cho khách hàng, văn hoá và con người Motorship&nbsp;được xây dựng dựa trên nền tảng vững chắc của INTEGRITY. Motorship luôn tuân thủ nguyên tắc 5 Right trong vận chuyển, vận tải hàng hóa nhằm đem lại sự hài&nbsp;lòng tuyệt đối cho Khác Hàng.</span></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Right cost (đúng giá)</span></span></li>\r\n	<li><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Right product (đúng sản phẩm)</span></span></li>\r\n	<li><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Right condition (đúng điều kiện)</span></span></span></span></li>\r\n	<li><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\">Right place (đúng địa điểm)</span></font></span></span></span></span></li>\r\n	<li><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\">Right quality (đúng chất lượng)</span></font></span></font></span></span></span></span></li>\r\n	<li><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Commitment (Luôn giữ cam kết với khách hàng)</span></span></span></font></span></font></span></span></span></span></li>\r\n	<li><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Respect (Luôn tôn trọng và hợp tác với khách hàng)</span></span></span></span></span></font></span></font></span></span></span></span></li>\r\n	<li><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">Innovation (Đổi mới sáng tạo để luôn mang lại giải pháp tốt nhất cho khách hàng)</span></span></span></span></span></span></span></font></span></font></span></span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">​​​</span></span></span></span></span></span></span></font></span></font></span></span></span></span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: tahoma, geneva, sans-serif;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-size: 14px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(0, 0, 0);\">Cuối cùng, MOTORSHIP&nbsp;xin chân thành cảm ơn tất cả các khách hàng đã tin tưởng chúng tôi. Những đánh giá và góp ý của quý khách đã&nbsp;giúp chúng tôi đã từng bước trưởng thành hơn và nỗ lực hơn nữa.</span></span></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: tahoma, geneva, sans-serif;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-size: 14px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(0, 0, 0);\"><img alt=\"\" src=\"http://w3ni230.web3nhat.net/data/media/167/images/camon2.jpg\" style=\"width: 599px; height: 350px;\" /></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\"><font face=\"tahoma, geneva, sans-serif\"><span style=\"font-size: 14px; line-height: 22.4px;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><span style=\"font-family:tahoma,geneva,sans-serif;\">​</span></span></span></span></span></span></span></font></span></font></span></span></span></span></p>\r\n','/media/images/167/intro/ava/','231_1444203391_2285614cb7fc1d45.jpg','','','',[now],[now]);
