INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories429,'Dịch vụ ship hàng - Ý tưởng kinh doanh độc đáo','Dịch vụ ship hàng thuê đang thu hút giới trẻ, đặc biệt là sinh viên tham gia. Bạn có thể mở một dịch vụ shipper riêng, hỗ trợ các shop vận chuyển hàng hóa.','<p></p>\r\n\r\n<p><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><a><span style=\"color:#000000;\">Kinh doanh online trở thành một xu thế mới của giới trẻ nhờ sự phát triển của mạng xã hội facebook, cùng sự phổ biến của các công cụ truy cập Internet như smartphone, laptop, tablet, ipad… Kinh doanh quần áo, đồ mỹ phẩm, giầy dép là những lĩnh vực thu hút sự chú ý của giới trẻ nhất, đồng thời cũng là mặt hàng có nhiều người cạnh tranh nhất.</span></a></span></span></p>\r\n\r\n<p><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><a><span style=\"color:#000000;\">&nbsp;</span></a></span></span><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><a><span style=\"color:#000000;\">Tuy nhiên việc các shop thời trang được mở ra quá nhiều khiến đây không phải là sự lựa chọn hàng đầu trong việc khởi nghiệp hiện nay, việc tìm ra những ý tưởng kinh doanh độc, lạ, không đụng hàng đang là xu hướng mới.</span></a></span></span></p>\r\n\r\n<p><span style=\"color:#000000;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\">Dịch vụ ship hàng thuê đang thu hút giới trẻ, đặc biệt là sinh viên tham gia</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\">Bạn có thể mở một dịch vụ shipper riêng, hỗ trợ các shop vận chuyển hàng hóa. Để trờ thành shipper rất đơn giản, bạn chỉ cần có xe máy, điện thoại, chịu khó, thông thuộc đường xá và thật thà. Mở một group và fanpage trên mạng xã hội facebook, thường xuyên truy cập vào các page quần áo để quảng bá cho dịch vụ của mình.</span></span></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color:#000000;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><img alt=\"\" src=\"http://w3ni230.web3nhat.net/data/media/167/files/4-y-tuong-kinh-doanh-online-doc-la-khong-lo-dung-hang-6.jpg\" style=\"width: 700px; height: 400px;\" />&nbsp;&nbsp;&nbsp;</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n','dich-vu-ship-hang---y-tuong-kinh-doanh-doc-dao',1,'','',[site_id],[user_id],'/media/images/167/news/ava/','301_1444200232_1105614bf288f350.jpeg',[now], [now],[user_id],0,'','',[now]);set @news720 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories429,'Chuyển hàng nội thành Hà Nội, những vấn đề shipper còn thiếu','Dịch vụ chuyển hàng Hà Nội là một vấn đề được rất nhiều người quan tâm, giữa một thế giới internet bùng nổ, việc giao dịch online đã trở nên phổ cập hơn bao giờ hết trong xã hội của chúng ta. Mọi thứ chỉ cần giải quyết bằng 1 cú click chuột, nhưng ship hàng nội thành, mà điển hình là ship hàng Hà Nội đôi khi không hề đơn giản như nhiều người nghĩ','<p style=\"text-align: center;\"><img alt=\"\" src=\"http://w3ni230.web3nhat.net/data/media/167/files/14290920482829609695.png\" style=\"width: 436px; height: 411px;\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><b style=\"text-decoration: none; color: rgb(77, 70, 156); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">Dịch vụ chuyển hàng</b><span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">&nbsp;Hà Nội là một vấn đề được rất nhiều người quan tâm, giữa một thế giới internet bùng nổ, việc giao dịch online đã trở nên phổ cập hơn bao giờ hết trong xã hội của chúng ta. Mọi thứ chỉ cần giải quyết bằng 1 cú click chuột, nhưng ship hàng nội thành, mà điển hình là ship hàng Hà Nội đôi khi không hề đơn giản như nhiều người nghĩ:</span><br style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\" />\r\n<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">Câu hỏi của mọi người đặt ra là:</span><br style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\" />\r\n<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">+ Tôi có thể tìm&nbsp;</span>dịch vụ<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">&nbsp;ship hàng Hà Nội ở đâu?</span><br style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\" />\r\n<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">+ Lý do gì để tôi có thể tin đây là một dịch vụ chuyển hàng nội thành&nbsp;</span>uy tín<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">.</span><br style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\" />\r\n<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">+ Đơn vị Ship hàng có thái độ như thế nào để làm hài lòng khách hàng của chúng tôi?</span><br style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\" />\r\n<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">+&nbsp;</span><a href=\"http://www.shipchuyennghiep.com/p/bang-gia_2.html\" style=\"text-decoration: none; color: rgb(77, 70, 156); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\"><b>Giá trị</b></a><span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">&nbsp;một đơn hàng bao nhiêu là hợp lý cho một đơn hàng phát trong nội thành.</span><br style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\" />\r\n<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">Đây là những câu hỏi, những tiêu chí rất sát thực tế của những người mong muốn sử dụng một dịch vụ chuyển hàng hoàn hảo trong nội thành Hà Nội. Vậy liệu tất cả các đơn vị ship hàng có đáp ứng được những yêu cầu mà khách hàng đưa ra không!</span><br style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\" />\r\n<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">Ví dụ điển hình:</span><br style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\" />\r\n<i style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">Chị Lan: Một người bán quần áo online trên mạng chia sẻ: Lúc đầu, chưa biết đến dịch vụ ship hàng Hà Nội, ra gọi mấy bác xe ôm thì bị các bác ý chém đẹp, toàn 50k/đơn hàng. Nhưng phần lớn các bác xe ôm đi giao hàng cũng ú ớ, chẳng rõ là giao hàng gì? Của ai? Nên nhiều khi khách hàng cũng tỏ ra bức xúc do cách các bác ý nói chuyện.<br />\r\nAnh Trung: Chuyên giầy nam ở Trương Định cũng là một con buôn online có số má. Lúc đầu, cũng hay gọi mấy bác xe ôm sau thấy có dịch vụ chuyển hàng nội thành Hà Nội nở rộ, anh cũng tham khảo giá và cách thức làm việc của một vài đơn vị, xong lại chưa ưng ý, vì phần nhiều các bạn ý chưa được chuẩn bị kĩ càng, thiếu những kĩ năng cần có khi tiếp xúc với chủ shop hàng hay khách hàng của shop đó nên nhiều khi không chiều lòng được khách hàng, hay những lúc bị khách hẹn sai ý muốn là tỏ thái độ càu nhàu và lập tức những phản hồi không tốt cứ bay về shop và các bạn ý mất toi luôn một khách hàng&nbsp;</i></span></p>\r\n','chuyen-hang-noi-thanh-ha-noi-nhung-van-de-shipper-con-thieu',1,'','',[site_id],[user_id],'/media/images/167/news/ava/','862_1444199838_9265614bd9e16d7d.jpg',[now], [now],[user_id],0,'','',[now]);set @news719 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories429,'5 bí quyết giúp Shipper Hà Nội chiếm trọn niềm tin khách hàng','Ship Hà Nội có thể xem là dịch vụ chuyển hàng nội thành phổ biến nhất, xong để có được một lượng khách hàng ổn định thì hoàn toàn không dễ dàng chút nào. Ngoài việc tranh giành thị trường, các shipper còn phải trang bị những kĩ năng sau, để giúp khách hàng tin tưởng tuyệt đối vào dịch vụ chuyển hàng nội thành của mình','<p style=\"text-align: right;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\"><img alt=\"\" src=\"http://w3ni230.web3nhat.net/data/media/167/images/4-y-tuong-kinh-doanh-online-doc-la-khong-lo-dung-hang-6.jpg\" style=\"width: 700px; height: 400px;\" /></span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\">Ship&nbsp;Hà Nội có thể xem là dịch vụ chuyển hàng nội thành phổ biến nhất, xong để có được một lượng khách hàng ổn định thì hoàn toàn không dễ dàng chút nào. Ngoài việc tranh giành thị trường, các&nbsp;shipper&nbsp;còn phải trang bị những kĩ năng sau, để giúp khách hàng tin tưởng tuyệt đối vào dịch vụ chuyển hàng nội thành của mình<br />\r\n<strong><span style=\"color:#000080;\">1. Hình ảnh</span></strong>: Liệu khách hàng có muốn tiếp 1 shipper ăn mặc luộm thuộm,, đầu tóc bù xù ko? Hình thức bề ngoài là ấn tượng đầu tiên cho khách hàng của bạn, hãy chú ý tới nó.<br />\r\n<strong><span style=\"color:#000080;\">2. Sự nhiệt tình:</span></strong> Chắc chắn rồi, chẳng khách hàng nào muốn làm việc với 1 shipper cứ lù đà lù đù, không những chậm chạp nó còn ảnh hưởng rất nhiều đến quá trình giao tiếp với khách hàng của họ<br />\r\n<strong><span style=\"color:#000080;\">3. Sự cẩn thận:</span></strong> Khách hàng có gọi bạn lần tiếp theo? Khi họ nhìn thấy bạn cầm món hàng của họ quăng quật lên xe của mình? Hãy nâng niu nó, phải luôn thể hiện sự tôn trọng đối với khách hàng của mình<br />\r\n<strong><span style=\"color:#000080;\">4. Sự trung thành</span></strong>: Họ cần người&nbsp;tin tưởng, hãy tạo cảm giác như người nhà vượt xa mối quan hệ shipper và khách hàng.<br />\r\n<strong><span style=\"color:#000080;\">5. Sự&nbsp;chuyên nghiệp</span></strong>: Luôn có sẵn&nbsp;bảng giá, hóa đơn, chứng từ đầy đủ các shipper nên tạo cho mình tính chuyên nghiệp, tránh việc mập mờ về giá ship và tiền của khách hàng. Chính điều này sẽ làm nên thương hiệu của các bạn.<br />\r\nKết luận: Đây là 5 yếu tố hàng đầu một shipper cần phải có, không những làm tăng doanh thu lợi nhuận cho chính bản thân các bạn mà còn giúp bạn xây dựng được những mối quan hệ xã hội mà không phải lúc nào cũng có được!</span></span></p>\r\n','5-bi-quyet-giup-shipper-ha-noi-chiem-tron-niem-tin-khach-hang',1,'','',[site_id],[user_id],'/media/images/167/news/ava/','562_1444199411_5265614bbf352c78.jpg',[now], [now],[user_id],0,'','',[now]);set @news718 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories429,'Lý do shop bán hàng online nên có một đội ship hàng nội thành chuyên nghiệp','Sau một thời gian triển khai dịch vụ ship hàng, chuyển hàng nội thành Hà Nội, chúng tôi cũng đã có những kinh nghiệm nhất định trong lĩnh vực này. Những đối tác lớn của chúng tôi có thể kể đến: Hằng Túi, Việt Anh Hoàng ( chuyên mỹ phẩm handmade cao cấp), EU Store( chuyên hàng nhập quần áo nhập khẩu Châu Âu)…….và một bộ phận khách lẻ.','<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">Sau một thời gian triển khai dịch vụ ship hàng, chuyển hàng nội thành Hà Nội, chúng tôi cũng đã có những kinh nghiệm nhất định trong lĩnh vực này. Những đối tác lớn của chúng tôi có thể kể đến: Hằng Túi, Việt Anh Hoàng ( chuyên mỹ phẩm handmade cao cấp), EU Store( chuyên hàng nhập quần áo nhập khẩu Châu Âu)…….và một bộ phận khách lẻ.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">Nhìn chung, hiện nay do nhu cầu mua hàng online bùng nổ nên dịch vụ ship hàng nội thành mọc lên như nấm, nghĩa là kể cả những sinh viên, hay những người làm xe ôm cũng có thể dựa vào facebook đăng quảng cáo để làm chuyển hàng trong nội thành, đây cũng là lý do mà giá ship hàng nội thành bị phá giá liên tục, khách hàng có thể rất hài lòng về giá ship khi hợp tác với nhóm ship không chuyên này, xong bên cạnh những cái lợi thì tác hại của nó cũng không hề nhỏ. Chúng ta cùng điểm qua nhé!</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\"><img alt=\"\" src=\"http://w3ni230.web3nhat.net/data/media/167/files/14290920482829609695.png\" style=\"width: 436px; height: 411px;\" /></span><br style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\" />\r\n<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">1.</span> <span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">Phần lớn những bạn sinh viên làm shipper chủ yếu là chộp giật là chính: Nghĩa là khi group xuất hiện một đơn hàng thì họ nhao vào nhận ship, dẫn đến vấn đề cạnh tranh về giá, nhưng sau khi đấu giá thành công………..thì chủ shop gọi cho bạn shipper đó thì ò e í, mặc dù đã fix thời gian chuyển hàng cho khách!</span><br style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\" />\r\n<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">2.</span> <span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">Chính việc xuất hiện Group “Người tìm ship, ship tìm người” dẫn đến tình trạng lừa đảo tăng cao. Cụ thể: một bạn sinh viên chân ướt, chân ráo vào nghề, nhận được một bài đăng “ Chuyển laptop từ địa chỉ ngõ 53 Giáp Bát đi Hoàng Công Chất, Từ Liêm, ứng 7tr500, ship 200k”. Bạn sinh viên nhanh nhẩu lên đường, mừng rỡ vì giá ship quá cao so với mặt bằng chung, ứng đủ 7tr500k cho khách hàng, nhưng khi đên nơi ko tài nào gọi được khách, mở laptop ra thì là 1 cái vỏ…………….</span><br style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\" />\r\n<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">3.</span> <span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">Shop online thì lại ham tiền ship rẻ: Tôi xin khẳng định”Của rẻ là của ôi”, shipper nào cũng khẳng định với giá 15k – 20k, ship bất kể vị trí nào trong bán kinh 10km……….Xin thưa với các shop là với giá đó mà không gom từ 3 -4 đơn thì chắc đổ nước lã vào bình xăng mà chạy chưa kể tiền điện thoại liên lạc với khách.</span><br style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\" />\r\n<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">4.</span> <span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\">Còn với một đội ship uy tín và chuyên nghiệp khi làm việc với shop sẽ có những đơn hàng giải quyết dễ dàng, nhưng những đơn khó họ cũng phải cố gắng vì đó là mối làm ăn lâu dài, không phải kiểu chộp giật, thích thì nhận mà không thích thì thôi.</span></span><br style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\" />\r\n<span style=\"color: rgb(68, 68, 68); font-family: Arial, Tahoma, Helvetica, FreeSans, sans-serif; line-height: 18.2px;\"><span style=\"font-size:14px;\">Đây là những lý do chính để những shop bán hàng online chuyên nghiệp nhìn nhận, tìm kiếm cho mình một đội shipper đáp ứng đầy đủ những yêu cầu của họ, dù là khắt khe nhất. Đầu tuần, chúc các bạn buôn may bán đắt, nếu có nhu cầu chuyển hàng hãy thử call ngay cho dịch vụ Ship Chuyên Nghiệp để có được dịch vụ tốt nhất!</span></span></p>\r\n','ly-do-shop-ban-hang-online-nen-co-mot-doi-ship-hang-noi-thanh-chuyen-nghiep',1,'','',[site_id],[user_id],'/media/images/167/news/ava/','952_1444203858_3755614cd52382e4.jpg',[now], [now],[user_id],0,'','',[now]);set @news717 = LAST_INSERT_ID();
INSERT INTO posts (id, category_id, title, sortdesc, description, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, publicdate) VALUES (null,0,'Biển tấm lớn trên Quốc lộ 2 Việt Trì','Quốc lộ 2 dài 312 km, chạy qua địa bàn năm tỉnh và thành phố: Hà Nội, Vĩnh Phúc, Phú Thọ, Tuyên Quang và Hà Giang. Chiều dài toàn tuyến là 315 km. Đoạn từ sân bay Nội Bài (Hà Nội) đến thành phố Vĩnh Yên còn được gọi là Quốc lộ 2A. Đoạn từ Vĩnh Yên đến thành phố Tuyên Quang và một ngả rẽ lên thị trấn Tam Đảo còn được gọi là Quốc lộ 2B. Đoạn từ thành phố Tuyên Quang đến cửa khẩu Thanh Thủy còn được gọi là Quốc lộ 2C.\r\n','<p>&nbsp;Biển được xây dựng bên cạnh đường cao tốc quốc lộ số 2 và cạnh tuyến đường sắt, thuộc phường Bạch Hạc, TP. Việt Trì. Bên phải đường theo hướng Hà Nội đi Yên Bái.</p>\r\n\r\n<p><object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"440\" hspace=\"100\" vspace=\"100\" width=\"660\"><param name=\"quality\" value=\"high\" /><param name=\"movie\" value=\"http://w3ni0006.web3nhat.net/data/media/53/flash/viet%20tri.swf\" /><embed height=\"440\" hspace=\"100\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" quality=\"high\" src=\"http://w3ni0006.web3nhat.net/data/media/53/flash/viet%20tri.swf\" type=\"application/x-shockwave-flash\" vspace=\"100\" width=\"660\"></embed></object></p>\r\n','bien-tam-lon-tren-quoc-lo-2-viet-tri',1,'','',[site_id],[user_id],'/media/images/53/post/ava/','557_1415507554_148545eee62abbd2.jpg',[now], [now],[user_id],[now]);set @posts27 = LAST_INSERT_ID();
INSERT INTO posts (id, category_id, title, sortdesc, description, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, publicdate) VALUES (null,0,'Biển tấm lớn trong bến xe Gia Lâm','Bến xe Gia Lâm là đầu mối giao thông quan trọng của các tỉnh phía bắc. Mật độ giao thông ở đây khoảng từ 10.000 - 13.000 lượt khách/ ngày, những ngày lễ lớn mật độ lên đến 13.000 - 15.000 lượt khách/ ngày. Hệ thống biển của chúng tôi ở đây gồm 2 vị trí, án ngữ hai bên cửa ra vào nhà chờ xe khách của bến. Biển được đặt ở vị trí trên cao, tầm nhìn tốt. \r\n','<p><object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"440\" hspace=\"100\" vspace=\"100\" width=\"660\"><param name=\"quality\" value=\"high\" /><param name=\"movie\" value=\"http://w3ni0006.web3nhat.net/data/media/53/flash/ben%20xe%20gia%20lam.swf\" /><embed height=\"440\" hspace=\"100\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" quality=\"high\" src=\"http://w3ni0006.web3nhat.net/data/media/53/flash/ben%20xe%20gia%20lam.swf\" type=\"application/x-shockwave-flash\" vspace=\"100\" width=\"660\"></embed></object></p>\r\n','bien-tam-lon-trong-ben-xe-gia-lam',1,'','',[site_id],[user_id],'/media/images/53/post/ava/','626_1415506854_247545eeba6d6f24.jpg',[now], [now],[user_id],[now]);set @posts28 = LAST_INSERT_ID();
INSERT INTO posts (id, category_id, title, sortdesc, description, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, publicdate) VALUES (null,0,'Biển tấm lớn trong bến xe Mỹ Đình','Bến xe Mỹ Đình được xây dựng cách nay hơn 10 năm, bình quân mỗi ngày có trên 11.000 lượt xe khách hoạt động. Nằm cạnh trục đường chính Phạm Hùng, số lượng hành khách ngày càng tăng, các phương tiện đưa đón khách cũng được bổ xng thường xuyên để đáp ứng đầy đủ nhu cầu đi lại của người dân.\r\n','<p style=\"text-align: justify;\">&nbsp;Hệ thông biển quảng cáo của Đông Quang tại đây gồm 11 biển, trong đó có 3 biển được lắp đặt phía trong nhà chờ xe ( biển số 9, 10, 11 ) và 8 biển được lắp đặt phía trước sân đỗ của bến xe, phía bên phải đường Phạm Hùng ( theo hướng Phạm Hùng - Nguyễn Xiển ).&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\"><object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"440\" hspace=\"100\" vspace=\"100\" width=\"660\"><param name=\"quality\" value=\"high\" /><param name=\"movie\" value=\"http://w3ni0006.web3nhat.net/data/media/53/flash/ben%2520xe%2520my%2520dinh.swf\" /><embed height=\"440\" hspace=\"100\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" quality=\"high\" src=\"http://w3ni0006.web3nhat.net/data/media/53/flash/ben%2520xe%2520my%2520dinh.swf\" type=\"application/x-shockwave-flash\" vspace=\"100\" width=\"660\"></embed></object></p>\r\n\r\n<p>&nbsp;</p>\r\n','bien-tam-lon-trong-ben-xe-my-dinh',1,'','',[site_id],[user_id],'/media/images/53/post/ava/','517_1415506174_383545ee8fe73e2d.jpg',[now], [now],[user_id],[now]);set @posts29 = LAST_INSERT_ID();
INSERT INTO categorypage (id, title, content, site_id, user_id, `alias`, created_time, modified_time, modified_by, meta_keywords, meta_description, meta_title, image_path, image_name) VALUES (null,'Bảng giá','<div style=\"text-align: center;\"><span style=\"color:#FF0000;\"><strong><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\">DỊCH VỤ GIAO HÀNG CHUYÊN NGHIỆP TRONG NGÀY TẠI NỘI THÀNH HÀ NỘI<br />\r\nHotline : 01697 145 678</span></span></strong></span></div>\r\n\r\n<p><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:14px;\">• Thời gian nhận cuộc gọi : tất cả các ngày trong tuần&nbsp;<br />\r\n• Thời gian nhận hàng : Từ 8h - 16h00 thu 2-7&nbsp;<br />\r\n• Cách thức nhận hàng: fastship24h nhận hàng và ứng 100% giá trị tiền hàng ( cho các shop có cửa hàng hoặc web bán hàng ), ứng 50% tiền hàng hoặc ghi biên nhận ( cho các shop bán hàng online )&nbsp;<br />\r\n• Khu vực nhận hàng: Quận 1, 3, 5, 10, 11, Tân Bình, Bình Thạnh, Phú Nhuận.( Hiệu quả tối đa cho khu vực này ).<br />\r\n• Cách thức giao hàng: Giao hàng trong ngày chia làm 2 đợt : xuất phát lúc 9h30 và 14h30&nbsp;<br />\r\n• Cách thức thanh toán: fastship24h thanh toán tiền hàng cho shop trong lần nhận hàng tiếp theo - đi ngang shop - chuyển khoản&nbsp;<br />\r\n• Hỗ trợ bán hàng: Hổ trợ mang nhiều mẫu cho khách xem ( tăng cơ hội bán được hàng ), miễn phí trả hàng , hổ trợ giao hàng tới khi nào được thì thôi ( quá dữ )&nbsp;<br />\r\n• Riêng đối với hàng hóa to, cồng kềnh - các shop liên hệ trước để dịch vụ mang baga ( phí + 10k )&nbsp;<br />\r\n• Hoạt động từ 7/2012 đến nay, dịch vụ đã có nhiều kinh nghiệm cũng như tiếp xúc với các phát sinh như: khách thay đổi địa điểm hoặc thời gian nhận hàng, giao cho học sinh, sinh viên vào thời gian cụ thể, giao hàng sau giờ hành chính, giao hàng gấp trong vòng 1h kể từ khi nhận cuộc gọi...........v.v.v.............<br />\r\n• Đối với những phát sinh trên FASTSHIP24H sẽ không tính thêm phí dịch vụ.<br />\r\n• Đối với các shop thực hiện chính sách free ship cho khách sẽ nhận được ưu đãi hợp lý của dịch vụ trong bảng giá, các shop có thể yên tâm. Phí dịch vụ, thời gian giao hàng sẽ được dịch vụ và shop hội ý nhằm đạt được sự hợp tác vui vẻ và lâu dài trong quá trình làm việc.<br />\r\n• Với tâm lý luôn muốn nhận được hàng trong thời gian sớm nhất, ở nhà hoặc công ty mà vẫn có thể có thể mua sắm 1 cách vừa ý, dịch vụ hoan nghênh các shop thông báo đơn hàng sớm nhất có thể để có thể chủ động trong việc nhận hàng giao cho khách và tránh tình trạng khách hàng thay đổi ý định vì đợi chờ quá lâu, hotline luôn hoạt động suốt 24h các shop nhé.<br />\r\n<strong>Bảng giá dịch vụ :</strong><br />\r\n- Khu vực giao / nhận : Các quận trong nội thành Hà Nội<br />\r\n- Khối lượng hàng hóa : 3kg ( thêm 0.5kg thêm 2.000 vnd/kg)<br />\r\n- Các gói cước:<br />\r\nGói cước tiết kiệm ( Giao trong vong 2-3 ngày ) : 10,000 vnd / đơn hàng&nbsp;<br />\r\nGói cước qua ngày ( Hàng hóa giao trong ngày kế tiếp) : 15.000 vnd/đơn hàng<br />\r\nGói cước nhanh ( Yêu cầu trước 12h trưa: giao hàng trong ngày, yêu cầu sau 12h trưa: giao hàng trước 12h trưa hôm sau ) : 20,000 vnd/đơn hàng</span></span></p>\r\n',[site_id],[user_id],'bang-gia',[now],[now],[user_id],'','DỊCH VỤ GIAO HÀNG CHUYÊN NGHIỆP TRONG NGÀY TẠI NỘI THÀNH HÀ NỘI\r\nHotline : 01697 145 678 - Thời gian nhận cuộc gọi : tất cả các ngày trong tuần \r\n\r\n','','/media/images/167/categorypage/ava/','149_1444216513_9255614fec164585.jpg');
