INSERT INTO site_introduces (site_id,user_id, title, sortdesc, description, image_path, image_name, meta_keywords, meta_description, meta_title, created_time, modified_time) VALUES ([site_id],[user_id],'','','<p style=\"text-align: center;\"><img alt=\"\" src=\"http://w3ni238.web3nhat.net/data/media/175/images/wall-e.jpg\" style=\"width: 1250px; height: 628px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 14px; line-height: 25.2px;\">Robofun&nbsp;ra đời với mục tiêu tập hợp và&nbsp;tạo nên một cộng đồng&nbsp;đam mê kĩ thuật, nghiên cứu khoa học. Nơi các bạn có thể giao lưu, chia sẻ các kiến thức và hiểu biết của mình về kĩ thuật và đồng thời cũng giúp đỡ lẫn nhau trong công việc nghiên cứu và học tập. Ngoài ra, Robofun cũng hỗ trợ làm đồ án và tư vấn đồ án cho các bạn sinh viên khối ngành cơ - điện tử, điện điện tử, cơ khí chế tạo máy.&nbsp;Robofun cung cấp những linh kiện điện tử cơ bản, module điều khiển động cơ, linh kiện chế tạo robot&nbsp;chất lượng tốt và giá cả hợp lý cho các bạn.</span></p>\r\n','','','','','',[now],[now]);
