INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product1129,'Cảm biến la bàn số HMC5883L được dùng để đo từ trường của trái đất nhằm xác định phương hướng của vị trí hiện tại với độ chính xác lên đến 1 hoặc 2 độ. Nó cung cấp các cách đo riêng biệt cho từng trục và có thể kết hợp lại để tính toán 3D.\r\nCó thể dùng để đo từ trường thô hoặc các nguồn từ trường mạnh hơn gần nó. Mạch có thể cảm nhận được nguồn từ trường xung quanh nó như của nam châm hoặc điện trường. Khi phát hiện được từ trường từ bên ngoài, nó có thể xác định được khoảng cách tương đối hoặc chiều đến vật phát ra từ trường đó.\r\n','<h2 style=\"-webkit-font-smoothing: antialiased; -webkit-tap-highlight-color: transparent; margin: 0px; padding: 0px; border: 0px; outline: 0px; color: rgb(51, 55, 69); font-family: Tahoma, Geneva, sans-serif; line-height: normal;\">Thông số kỹ thuật:</h2>\r\n\r\n<ul style=\"-webkit-font-smoothing: antialiased; -webkit-tap-highlight-color: transparent; color: rgb(51, 55, 69); font-family: Tahoma, Geneva, sans-serif; line-height: normal;\">\r\n	<li style=\"-webkit-font-smoothing: antialiased; -webkit-tap-highlight-color: transparent;\">\r\n	<h3 style=\"-webkit-font-smoothing: antialiased; -webkit-tap-highlight-color: transparent; margin: 0px; padding: 0px; border: 0px; outline: 0px;\">Điện áp cung cấp: 3 ~ 5VDC.</h3>\r\n	</li>\r\n	<li style=\"-webkit-font-smoothing: antialiased; -webkit-tap-highlight-color: transparent;\">\r\n	<h3 style=\"-webkit-font-smoothing: antialiased; -webkit-tap-highlight-color: transparent; margin: 0px; padding: 0px; border: 0px; outline: 0px;\">Chuẫn giao tiếp: I2C (3 ~ 5 TTL).</h3>\r\n	</li>\r\n	<li style=\"-webkit-font-smoothing: antialiased; -webkit-tap-highlight-color: transparent;\">\r\n	<h3 style=\"-webkit-font-smoothing: antialiased; -webkit-tap-highlight-color: transparent; margin: 0px; padding: 0px; border: 0px; outline