INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product1135,'+ Test Board giúp bạn dễ dàng thực hiện các mạch điện thực tế trước khi hàn trực tiếp linh kiện lên board mạch đồng.\r\n+ Test Board với 170 lỗ cắm là loại chất lượng tốt, màu sắc đẹp, được làm từ nhựa ABS bền và dẻo dai.\r\n+ Các hàng cắm trên board có đánh số giúp bạn dễ theo dõi vị trí cắm linh kiện. \r\n+ Dọc theo 2 bên đường rãnh ở giữa Board, các chân nằm trên cùng 1 hàng thì nối với nhau. Các chân khác hàng không nối với nhau. Đây là quy tắc sử dụng Test Board nói chung.\r\n+ Ngoài ra, với các Test Board cùng kích thước như nhau, bạn có thể dùng nhiều Test Board gắn lại để có được Test Board lớn hơn, với nhiều lỗ cắm hơn.\r\n','<h2 style=\"color: rgb(0, 0, 0); font-size: 12.960000038147px; margin: 0px; padding: 0px; font-family: Tahoma, Arial, Helvetica, sans-serif; line-height: 18.1440010070801px;\">+ Test Board giúp bạn dễ dàng thực hiện các mạch điện thực tế trước khi hàn trực tiếp linh kiện lên board mạch đồng.<br />\r\n+ Test Board với 170 lỗ cắm là loại chất lượng tốt, màu sắc đẹp, được làm từ nhựa ABS bền và dẻo dai.<br />\r\n+ Các hàng cắm trên board có đánh số giúp bạn dễ theo dõi vị trí cắm linh kiện.&nbsp;<br />\r\n+ Dọc theo 2 bên đường rãnh ở giữa Board, các chân nằm trên cùng 1 hàng thì nối với nhau. Các chân khác hàng không nối với nhau. Đây là quy tắc sử dụng Test Board nói chung.<br />\r\n+ Ngoài ra, với các Test Board cùng kích thước như nhau, bạn có thể dùng nhiều Test Board gắn lại để có được Test Board lớn hơn, với nhiều lỗ cắm hơn.<br />\r\n</h2>\r\n','','','','','',[site_id]);
