INSERT INTO site_introduces (site_id,user_id, title, sortdesc, description, image_path, image_name, meta_keywords, meta_description, meta_title, created_time, modified_time) VALUES ([site_id],[user_id],'VỀ CHÚNG TÔI','Với phương thức gia truyền từ nhiều đời nay, gia đình tôi đã sang tạo ra bài thuốc quý điều trị bệnh lậu bằng Đông Y. Là bài thuốc gia truyền chiết xuất hoàn toàn từ các loại cây quý hiếm của thiên nhiên tôi đã chữa khỏi cho rất nhiều bệnh nhân bị bệnh lậu cấp, mạn thậm chí cả bệnh lậu đã kháng thuốc trên khắp mọi miền đất nước.','<p style=\"box-sizing: border-box; margin: 0px 0px 26px; padding: 0px; color: rgb(165, 165, 163); font-family: \'Droid Sans\', sans-serif; font-size: 16px; line-height: 26px; background-color: rgb(255, 255, 255);\"><span style=\"color:#000000;\">Với phương thức gia truyền từ nhiều đời nay, gia đình tôi đã sang tạo ra bài thuốc quý điều trị bệnh lậu bằng Đông Y. Là bài thuốc gia truyền chiết xuất hoàn toàn từ các loại cây quý hiếm của thiên nhiên tôi đã chữa khỏi cho rất nhiều bệnh nhân bị bệnh lậu cấp, mạn thậm chí cả bệnh lậu đã kháng thuốc trên khắp mọi miền đất nước.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 26px; padding: 0px; color: rgb(165, 165, 163); font-family: \'Droid Sans\', sans-serif; font-size: 16px; line-height: 26px; background-color: rgb(255, 255, 255);\"><span style=\"color:#000000;\">Với tình trạng kháng thuốc cao như hiện nay việc điều trị lậu bằng Tây Y trở nên kém hiệu quả và tốn nhiều chi phí cho người bệnh. Điều trị bằng bài thuốc Đông Y gia truyền của gia đình tôi người bệnh sẽ khỏi hoàn toàn trong thời gian ngắn ( 3-6 ngày đối với lậu cấp, 9 – 12 ngày đối với lậu mạn) và chi phí tiết kiệm nhất.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 26px; padding: 0px; color: rgb(165, 165, 163); font-family: \'Droid Sans\', sans-serif; font-size: 16px; line-height: 26px; background-color: rgb(255, 255, 255);\"><span style=\"color:#000000;\">Sau điều trị &nbsp;bệnh nhân được xét nghiệm đầy đủ để đảm bảo khỏi bệnh.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 26px; padding: 0px; color: rgb(165, 165, 163); font-family: \'Droid Sans\', sans-serif; font-size: 16px; line-height: 26px; background-color: rgb(255, 255, 255);\"><span style=\"color:#000000;\"><span style=\"box-sizing: border-box;\">Cam kết nếu không điều trị khỏi sẽ hoàn tiền 100% cho bệnh nhân.</span></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 26px; padding: 0px; color: rgb(165, 165, 163); font-family: \'Droid Sans\', sans-serif; font-size: 16px; line-height: 26px; background-color: rgb(255, 255, 255);\"><span style=\"color:#000000;\">Tư vấn điều trị tận tình trong suốt quá trình điều trị bệnh./.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 26px; padding: 0px; color: rgb(165, 165, 163); font-family: \'Droid Sans\', sans-serif; font-size: 16px; line-height: 26px; background-color: rgb(255, 255, 255);\"><span style=\"color:#000000;\">Hotline: 0986.141.071</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 26px; padding: 0px; color: rgb(165, 165, 163); font-family: \'Droid Sans\', sans-serif; font-size: 16px; line-height: 26px; text-align: right; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; font-weight: 700;\">Bác sĩ &nbsp;Hà Xuân Minh</span><span style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; line-height: 1.6;\">&nbsp;&nbsp;</span></p>\r\n','','','','','',[now],[now]);
