INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories502,'6 điều cần biết về bệnh nấm da','6 điều cần biết về bệnh nấm da<br /><br />\r\nVi nấm ẩn nấp khắp nơi trong môi trường xung quanh, gây ra những căn bệnh nấm da phiền toái và cũng không kém phần nguy hiểm. Sau đây là 6 điều bạn cần biết về chứng bệnh da liễu này để điều trị và phòng tránh hiệu quả.','<div class=\"title_news\" style=\"margin: 10px 0px 0px; padding: 0px 0px 10px; width: 660.015625px; float: left; color: rgb(51, 51, 51); font-family: arial; font-size: 12px; background-color: rgb(255, 255, 255);\">\r\n<h1 style=\"margin: 0px; padding: 0px; font-weight: 400; font-stretch: normal; font-size: 28px; line-height: 32px; text-rendering: geometricPrecision; text-align: justify;\">6 điều cần biết về bệnh nấm da</h1>\r\n</div>\r\n\r\n<div class=\"short_intro txt_666\" style=\"margin: 0px; padding: 0px 0px 10px; color: rgb(68, 68, 68); font-weight: 700; font-stretch: normal; font-size: 14px; line-height: 18px; font-family: arial; width: 660.015625px; float: left; text-rendering: geometricPrecision; text-align: justify; background-color: rgb(255, 255, 255);\">Vi nấm ẩn nấp khắp nơi trong môi trường xung quanh, gây ra những căn bệnh nấm da phiền toái và cũng không kém phần nguy hiểm.&nbsp;Sau đây là 6 điều bạn cần biết về chứng bệnh da liễu này để điều trị và phòng tránh hiệu quả.</div>\r\n\r\n<div class=\"fck_detail width_common\" style=\"margin: 0px; padding: 0px 13.1875px 10px 0px; width: 646.8125px; float: left; overflow: hidden; font-stretch: normal; font-size: 14px; font-family: arial; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">\r\n<p class=\"subtitle\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricPrecision; font-weight: 700; text-align: justify;\">Mùa mưa tạo điều kiện lý tưởng cho nấm da</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricPrecision; text-align: justify;\">Vào mùa mưa, khí hậu ẩm ướt tạo môi trường thuận lợi cho sự sinh sôi, phát triển của nấm, đặc biệt là nấm kẽ tay, chân, nấm bẹn, nấm thân (hắc lào) và nấm móng. Bệnh nấm da khiến người bệnh cảm thấy ngứa ngáy, trên da xuất hiện những tổn thương như mẩn đỏ, bong tróc, vùng da không đều màu ….</p>\r\n\r\n<p class=\"subtitle\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricPrecision; font-weight: 700; text-align: justify;\">Ngập lụt - thủ phạm của nấm kẽ chân, nấm móng</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricPrecision; text-align: justify;\">Sau cơn mưa, tình trạng nước ngập lụt, nước tù đọng dơ bẩn tràn trên nhiều tuyến đường diễn ra ở nhiều nơi làm tăng nguy cơ nhiễm nấm kẽ chân cho người đi đường. Đối với người bị bệnh nấm kẽ chân (hay còn gọi là nước ăn chân), vi nấm tấn công các kẽ hẹp giữa các ngón chân khiến lớp da thượng bì bị mủn trắng và có kẽ nứt, lộ nền da đỏ ướt phía bên trong, gây cảm giác ngứa ngáy, đau rát, khó chịu.</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"margin: 0px auto 10px; padding: 0px; max-width: 100%; width: 500px;\">\r\n	<tbody style=\"margin: 0px; padding: 0px;\">\r\n		<tr style=\"margin: 0px; padding: 0px;\">\r\n			<td style=\"margin: 0px; padding: 0px; line-height: 0; text-align: justify;\"><img alt=\"Giày, vớ bị ẩm ướt mùa mưa là môi trường thuận lợi cho nấm kẽ chân phát triển.\" data-natural-width=\"500\" data-pwidth=\"646.8125\" data-width=\"500\" src=\"http://m.f13.img.vnecdn.net/2014/08/07/nam-1-6459-1407403407.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; font-size: 0px; line-height: 0; max-width: 100%;\" /></td>\r\n		</tr>\r\n		<tr style=\"margin: 0px; padding: 0px;\">\r\n			<td style=\"margin: 0px; padding: 0px; line-height: 0;\">\r\n			<p class=\"Image\" style=\"margin: 0px; padding: 10px; line-height: normal; text-rendering: geometricPrecision; font-stretch: normal; font-size: 12px; color: rgb(51, 51, 51); text-align: justify; background: rgb(245, 245, 245);\">Giày, vớ bị ẩm ướt mùa mưa là môi trường thuận lợi cho nấm kẽ chân phát triển.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p class=\"subtitle\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricPrecision; font-weight: 700; text-align: justify;\">Tuổi dậy thì dễ mắc bệnh lang ben</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricPrecision; text-align: justify;\">Trong độ tuổi dậy thì, sự thay đổi nội tiết tố là điều kiện thuận lợi cho bệnh lang ben phát triển. Bệnh do vi nấm Pityrosporum ovale gây nên, tạo ra những đốm da nhiều màu như trắng, nâu, đỏ… và gây ngứa ngáy khó chịu khi ra nắng khiến người bệnh ngại giao tiếp, từ đó làm giảm chất lượng cuộc sống. Ngoài ra những người tập thể thao, vận động nhiều, ra nhiều mồ hôi cũng là đối tượng dễ mắc loại bệnh này.</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"margin: 0px auto 10px; padding: 0px; max-width: 100%; width: 500px;\">\r\n	<tbody style=\"margin: 0px; padding: 0px;\">\r\n		<tr style=\"margin: 0px; padding: 0px;\">\r\n			<td style=\"margin: 0px; padding: 0px; line-height: 0; text-align: justify;\"><img alt=\"Lang ben do nấm Pityrosporum ovale gây nên, khiến người bệnh ngứa ngáy khó chịu khi đi dưới nắng.\" data-natural-width=\"500\" data-pwidth=\"646.8125\" data-width=\"500\" src=\"http://m.f13.img.vnecdn.net/2014/08/07/nam-2-5350-1407403407.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; font-size: 0px; line-height: 0; max-width: 100%;\" /></td>\r\n		</tr>\r\n		<tr style=\"margin: 0px; padding: 0px;\">\r\n			<td style=\"margin: 0px; padding: 0px; line-height: 0;\">\r\n			<p class=\"Image\" style=\"margin: 0px; padding: 10px; line-height: normal; text-rendering: geometricPrecision; font-stretch: normal; font-size: 12px; color: rgb(51, 51, 51); text-align: justify; background: rgb(245, 245, 245);\">Lang ben do nấm Pityrosporum ovale gây nên, khiến người bệnh ngứa ngáy khó chịu khi đi dưới nắng.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p class=\"subtitle\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricPrecision; font-weight: 700; text-align: justify;\">Nấm da rất dễ lây lan</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricPrecision; text-align: justify;\">Bệnh nấm da có các cách lây truyền phổ biến như nhiễm từ bào tử nấm có trong không khí và môi trường xung quanh; súc vật bị bệnh lây cho người; người bệnh lây sang người lành... Nếu trong nhà có người bị nhiễm nấm, bạn có nguy cơ bị nấm da rất cao nếu không chú ý vệ sinh, dùng chung đồ, nằm chung giường…</p>\r\n\r\n<p class=\"subtitle\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricPrecision; font-weight: 700; text-align: justify;\">Nấm da hay tái phát</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricPrecision; text-align: justify;\">Mặc dù không gây ra hậu quả nghiêm trọng về sức khỏe nhưng các bệnh nấm da thường dai dẳng, dễ tái phát, gây khó khăn trong điều trị. Hơn nữa, những lần mắc bệnh sau thường sẽ nặng, lâu dài và khó dứt hơn lần trước đó. Vì vậy, bệnh cần được điều trị tận gốc và không nên chủ quan trong việc loại trừ loại bệnh này.</p>\r\n\r\n<p class=\"subtitle\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricPrecision; font-weight: 700; text-align: justify;\">Cách điều trị, phòng tránh nấm da</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricPrecision; text-align: justify;\">Khi bị nhiễm nấm, người bệnh không nên dùng các thuốc trị nấm da phối hợp corticoid vì có thể gây tác dụng phụ như teo da, rạn da, và tạo cơ hội nấm phát triển nhiều hơn. Đối với các vết nấm kẽ tay, kẽ chân, nấm thân, nấm móng và lang ben với diện tích nhỏ, người bệnh có thể dùng các loại&nbsp;<a href=\"http://doisong.vnexpress.net/tin-tuc/suc-khoe/6-dieu-can-biet-ve-benh-nam-da-3022227-p2.html\" style=\"margin: 0px; padding: 0px; color: rgb(0, 79, 139); text-decoration: none; outline: 1px;\" title=\"kem bôi\">kem bôi</a>&nbsp;có chứa ketoconazole, miconazole, terbinafine… để điều trị. Trong trường hợp lang ben đã lây lan trên diện tích lớn, ở những vùng da khó bôi thuốc như lưng, gáy cổ…, người bệnh có thể dùng dầu gội có chứa ketoconazole 2% để thoa và tắm trong 5 ngày liên tiếp để diệt tận gốc vi nấm, tránh để bệnh kéo dài, dễ tái phát. Ngoài ra, cần tuân theo những hướng dẫn sau đây để vừa điều trị hiệu quả, vừa phòng tránh nhiễm nấm da:</p>\r\n</div>\r\n','6-dieu-can-biet-ve-benh-nam-da',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','606_1444295382_195561632d614d83.jpg',[now], [now],[user_id],0,'','',[now]);set @news812 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories502,'Nấm cơ thể','Nấm cơ thể<br /><br />\r\n<br /><br />\r\nDieutri.vn - Chuyên về y học, dành cho các bác sỹ, dược sỹ, học viên và sinh viên tự đăng bài viết về sức khỏe, trao đổi nội bộ nhằm nâng cao kiến thức phục vụ công việc !<br /><br />\r\n<br /><br />\r\nBịnh nấm cơ thể là một trong nhiều hình thức bệnh nấm, nhiễm trùng nấm phát triển trên các lớp trên của làn da. Đó là đặc trưng của một phát ban, ngứa, tròn màu đỏ với làn da khỏe mạnh.','<h1 class=\"h1main\" style=\"color: rgb(182, 0, 2); margin: 0px 0px 15px; padding: 12px 0px; font-size: 20pt; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(238, 238, 238); font-family: Arial;\">Nấm cơ thể</h1>\r\n\r\n<div class=\"post_content\" style=\"clear: both; float: none; font-family: Arial; font-size: 13.3333330154419px;\">\r\n<div>\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important;\"><strong>Dieutri.vn - Chuyên về y học, dành cho các bác sỹ, dược sỹ, học viên và sinh viên tự đăng bài viết về sức khỏe, trao đổi nội bộ nhằm nâng cao kiến thức phục vụ công việc !</strong></p>\r\n</div>\r\n\r\n<h2 class=\"des\" style=\"color: rgb(102, 102, 102); margin: 10px 0px; padding: 0px; font-size: 11pt; line-height: 22px;\">Bịnh nấm cơ thể là một trong nhiều hình thức bệnh nấm, nhiễm trùng nấm phát triển trên các lớp trên của làn da. Đó là đặc trưng của một phát ban, ngứa, tròn màu đỏ với làn da khỏe mạnh.</h2>\r\n\r\n<div class=\"ads-content\" style=\"border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(213, 213, 213); padding: 15px 0px; margin: 15px 0px 0px;\">&nbsp;</div>\r\n\r\n<div>\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 16pt; line-height: 32px;\">Định nghĩa</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Bịnh nấm cơ thể là một trong nhiều hình thức bệnh nấm, nhiễm trùng nấm phát triển trên các lớp trên của làn da. Đó là đặc trưng của một phát ban, ngứa, tròn màu đỏ với làn da khỏe mạnh. Bệnh nấm từ vòng đặc trưng có thể xuất hiện, nhưng nó không có gì để làm với thực tế dưới da.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Còn được gọi là corporis ben, bệnh nấm của cơ thể là liên quan chặt chẽ đến nhiễm nấm khác có tên tương tự, bao gồm cả bàn chân của vận động viên (bàn chân ben), ngứa jock (bệnh nấm da đùi) và bệnh nấm da đầu.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 16pt; line-height: 32px;\">Các triệu chứng</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Các dấu hiệu và triệu chứng của bệnh nấm bao gồm:</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Phát ban tròn trên da màu đỏ và cạnh viêm quanh và khỏe mạnh ở giữa.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Hơi nâng lên, mở rộng vòng da màu đỏ có vảy, trên thân hoặc khuôn mặt.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Hình tròn, phẳng vá da, ngứa.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Nhiều hơn một bản vá của bệnh nấm có thể xuất hiện trên da, và các bản vá lỗi hoặc vòng màu đỏ của ban có thể chồng lên nhau. Có thể đã nhiễm bệnh nấm da mà không cần phải có vòng màu đỏ.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 13pt; line-height: 26px;\">Đến gặp bác sĩ khi</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Đi khám bác sĩ nếu có phát ban trên da mà không cải thiện trong vòng hai tuần. Có thể cần uống thuốc theo toa.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Đi khám bác sĩ ngay lập tức nếu trải nghiệm:</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Quá đỏ.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Sưng.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Chảy nước.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Sốt.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 16pt; line-height: 32px;\">Nguyên nhân</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Nhiễm nấm, chẳng hạn như bệnh nấm, được gây ra bởi vi sinh vật trở thành ký sinh trùng trên cơ thể. Những loại nấm mốc (dermatophytes) sống trên các tế bào ở lớp ngoài của da.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Bệnh nấm là truyền nhiễm và có thể lây lan theo những cách sau:</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><b><span lang=\"EN-US\">Người sang người.</span></b><span lang=\"EN-US\">&nbsp;Bệnh nấm thường lây lan bằng cách tiếp xúc da với da trực tiếp với người bị nhiễm bệnh.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><b><span lang=\"EN-US\">Động vật sang người.</span></b><span lang=\"EN-US\">&nbsp;Lây nhiễm bằng cách chạm vào một con vật với bệnh nấm. Bệnh nấm có thể lây lan trong khi vuốt ve hoặc chải lông chó hoặc mèo. cũng có thể nhận được bệnh từ con chồn, thỏ, dê, lợn, ngựa.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><b><span lang=\"EN-US\">Tiếp xúc vật dụng.</span></b><span lang=\"EN-US\">&nbsp;Người bệnh nấm có thể lây lan qua tiếp xúc với các đối tượng hoặc các bề mặt mà một người bị bệnh hoặc động vật gần đây đã chạm hoặc cọ xát, như quần áo, khăn tắm, khăn trải giường, lược và bàn chải.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><b><span lang=\"EN-US\">Nguồn lây từ đất.</span></b><span lang=\"EN-US\">&nbsp;Trong trường hợp hiếm hoi, bệnh nấm có thể được lây truyền sang người do tiếp xúc với đất bị nhiễm bệnh. Nhiễm trùng rất có thể sẽ xảy ra chỉ từ liên hệ lâu dài với đất bị nhiễm cao.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 16pt; line-height: 32px;\">Yếu tố nguy cơ</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Đang có nguy cơ cao của bệnh nấm của cơ thể nếu:</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Sống trong điều kiện ẩm ướt hoặc đông người.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Có tiếp xúc gần với người có bệnh hoặc động vật.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Chia sẻ quần áo, chăn mền hoặc khăn tắm với người có nhiễm nấm.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Mồ hôi quá mức.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Tham gia các môn thể thao như bóng đá, đấu vật hay bóng bầu dục.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Mặc quần áo chặt, hạn chế.</span></p>\r\n\r\n<p style=\"color: rgb(102, 102, 102); line-height: 21px; padding-top: 0px; font-size: 10.5pt !important; margin: 6pt 0cm 0.0001pt; text-align: justify;\"><span lang=\"EN-US\">Có một hệ thống miễn dịch suy yếu.</span></p>\r\n</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n','nam-co-the',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','19_1444295306_9325616328ac77cc.jpg',[now], [now],[user_id],0,'','',[now]);set @news813 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories502,'Bệnh nấm ở phụ nữ','Viêm âm đạo do nấm là bệnh phụ khoa khá phổ biến, ảnh hưởng không ít đến sức khỏe của chị em. Nguyên nhân gây ra bệnh là nhiều loại nấm Candida khác nhau, trong số này chủ yếu là nấm Candida albicans (chiếm tới 80%).Bình thường Nấm Candida Albicans là vi sinh vật ký sinh ở một số nơi trên da và bên trong âm đạo, là một phần hệ vi sinh vật bình thường trong miệng, ruột và âm đạo . Môi trường axít trong âm đạo giữ cho nấm không bùng phát. Nếu môi trường âm đạo bị kiềm hóa vì một lý do nào đó, nấm ','<p style=\"margin: 0px 0px 22px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: Arial, Tahoma, Verdana; font-size: 12px; line-height: 22px; background: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;\">Viêm âm đạo do nấm là bệnh phụ khoa khá phổ biến, ảnh hưởng không ít đến sức khỏe của chị em. Nguyên nhân gây ra bệnh là nhiều loại nấm Candida khác nhau, trong số này chủ yếu là nấm Candida albicans (chiếm tới 80%).</strong>Bình thường Nấm Candida Albicans là vi sinh vật ký sinh ở một số nơi trên da và bên trong âm đạo, là một phần hệ vi sinh vật bình thường trong miệng, ruột và âm đạo . Môi trường axít trong âm đạo giữ cho nấm không bùng phát. Nếu môi trường âm đạo bị kiềm hóa vì một lý do nào đó, nấm bùng phát (phát triển mạnh) gây ra chứng nhiễm nấm âm đạo</p>\r\n\r\n<p style=\"margin: 0px 0px 22px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: Arial, Tahoma, Verdana; font-size: 12px; line-height: 22px; background: rgb(255, 255, 255);\">Môi trường acid âm đạo thường thay đổi trong khi hành kinh, mang thai, khi mắc bệnh tiểu đường, dùng kháng sinh, thuốc ngừa thai qua đường uống và các thuốc chứa steroid, rửa âm đạo thường xuyên bằng xà phòng. Ứ dịch âm đạo và những kích thích âm đạo là điều kiện kích thích cho nấm phát triển.</p>\r\n\r\n<h2 style=\"margin: 0px; padding: 28px 0px 14px; border: 0px; outline: 0px; vertical-align: baseline; clear: both; font-family: Arial, sans-serif; font-size: 19px; line-height: 22px; background: rgb(255, 255, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; text-decoration: underline; color: rgb(255, 0, 0); background: transparent;\">Triệu chứng</span></h2>\r\n\r\n<p style=\"margin: 0px 0px 22px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: Arial, Tahoma, Verdana; font-size: 12px; line-height: 22px; background: rgb(255, 255, 255);\">Nhiễm nấm thường không nguy hiểm nhưng gây ra nhiều bất tiện. Hầu như&nbsp;<span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; text-decoration: underline; background: transparent;\">phụ nữ</span>&nbsp;ai cũng có thể từng bị nhiễm nấm trên dưới một lần trong đời.Dưới đây là các triệu chứng thường gặp trong nhiễm nấm:</p>\r\n\r\n<ul style=\"margin: 0px 0px 22px 2.5em; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: Arial, Tahoma, Verdana; font-size: 12px; line-height: 22px; background: rgb(255, 255, 255);\">\r\n	<li style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;\">Ngứa và nóng ran ở cơ quan sinh dục ngoài (âm hộ &amp; âm đạo)</li>\r\n	<li style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;\">Dịch tiết âm đạo (huyết trắng bệnh lý) giống như pho-mát</li>\r\n	<li style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;\">Đau khi giao hợp</li>\r\n	<li style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;\">Sưng tấy âm hộ</li>\r\n	<li style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;\">Soi tươi cho kết quả sợi nấm dương tính.</li>\r\n</ul>\r\n\r\n<p style=\"margin: 0px 0px 22px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: Arial, Tahoma, Verdana; font-size: 12px; line-height: 22px; background: rgb(255, 255, 255);\">Khoảng 75% phụ nữ trên thế giới bị nhiễm nấm (khoảng 50% phụ nữ bị nhiễm khuẩn hơn một lần trong đời). Nếu Bạn nghi ngờ mình bị nhiễm nấm, hãy trao đổi với BS để BS chẩn đoán xác định nó là nấm &amp; cho Bạn phương pháp điều trị.</p>\r\n','benh-nam-o-phu-nu',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','534_1444295219_46356163233ed1cb.jpg',[now], [now],[user_id],1,'','',[now]);set @news814 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories501,'Nguyên nhân và cách chữa trị bệnh giang mai ở nữ giới','Nguyên nhân và cách chữa trị bệnh giang mai ở nữ giới<br /><br />\r\n<br /><br />\r\nGiang mai là bệnh xã hội nguy hiểm đối với người mắc phải, nó không phổ biến như bệnh lậu, sùi mào gà hay mụn rộp sinh dục…nhưng mức độ nguy hại thì đứng tốp đầu. Bệnh giang mai ở nữ giới nếu không kịp thời phát hiện và chữa trị kịp thời thì sẽ gây ra các biến chứng nặng nề đối với sức khỏe của chị em. ','<p class=\"post-meta\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); font-size: 11.0500001907349px; font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">&nbsp;</p>\r\n\r\n<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"font-family: Arial, Helvetica, sans-serif; padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(205, 90, 36); font-size: 17px;\"><span itemprop=\"name\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nguyên nhân và cách chữa trị bệnh giang mai ở nữ giới</span></h1>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 13px; background-color: rgb(255, 255, 255);\">&nbsp;</div>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); font-size: 13px; background-color: rgb(255, 255, 255);\">\r\n<div id=\"page-content-1\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Giang mai là bệnh xã hội nguy hiểm đối với người mắc phải, nó không phổ biến như bệnh lậu, sùi mào gà hay mụn rộp sinh dục…nhưng mức độ nguy hại thì đứng tốp đầu. Bệnh giang mai ở nữ giới nếu không kịp thời phát hiện và chữa trị kịp thời thì sẽ gây ra các biến chứng nặng nề đối với sức khỏe của chị em. Bệnh</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">&nbsp;có thể kéo dài dai dẳng và gây ra các tổn thương đối với một số cơ quan như viêm loét bộ phận sinh dục, đau nhức cơ xương, thậm chí gây tổn hại đến nội tạng.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Trước những nguy hại của giang mai, chị em cần hết sức để phòng đối với những nguyên nhân gây bệnh cũng như để ý đến các biểu hiện ban đầu của nó để chữa trị bệnh giang mang một cách sớm nhất. Dưới đây, chị em có thể bổ sung một số kiến thức về bệnh giang mai như sau:</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">sau khi nhiễm mầm&nbsp;<a href=\"http://chuabenhxahoi.info/benh-giang-mai\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">bệnh giang mai</strong>&nbsp;</span></a>được khoảng 3 tuần thì trên cơ thể, đặc biệt là ở vùng kín bắt đầu xuất hiện các những nốt hình tròn và không có cảm giác đau nếu không chạm vào.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Khi bước vào giai đoạn đầu sẽ xuất hiện săng giang mai với các mụn nước nhỏ có thể bị loét, hạch sưng to và bắt đầu phát triển mạnh nếu không điều trị săng giang mai. Các nốt nhú và mụn màu đỏ (đường kính từ 1 – 2 cm) xuất hiện ngày càng nhiều, lan rộng ra những vùng xung quanh, sau đó có thể chai cứng, ở giữa các nốt này có hiện tượng bị ăn mòn hoặc lở loét.</span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nguyên nhân bệnh giang mai ở nữ giới</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh giang mai ở nữ giới thường do nhiều nguyên nhân khác nhau, nhưng tập trung ở 3 nguyên nhân chính sau:</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Lây nhiễm qua con đường quan hệ tình dục: Theo thống kê thì có đến 95% các trường hợp nữ giới mắc bệnh giang mai thông qua con đường quan hệ tình dục không đảm bảo an toàn. Khả năng mắc bệnh qua con đường này cao là do nữ giới có cấu tạo cơ quan sinh dục ở dạng mở. Thời điểm 1 năm sau khi mắc bệnh có khả năng lây truyền lớn nhất.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-chua-tri-benh-giang-mai-o-nu-gioi.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Nguyên nhân và cách chữa trị bệnh giang mai ở nữ giới\" class=\"aligncenter wp-image-4503\" height=\"233\" src=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-chua-tri-benh-giang-mai-o-nu-gioi.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Quan hệ tình dục không an toàn gây bệnh giang mai ở nữ giới</em></span></strong></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Giang mai truyền từ mẹ sang con: Những phụ nữ trong qua trình mang thai nếu mắc bệnh giang mai thì khả năng lây truyền xoắn khuẩn giang mai cho thai nhi thông qua nhau thai và tĩnh mạch rốn là không thể tránh khỏi.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Bị nhiễm giang mai qua con đường gián tiếp: Nếu phụ nữ khỏe mạnh mà sử dụng chung các vật dụng cá nhân như quần áo, khăn tắm, bàn chải đánh răng, dụng cụ ăn uống…có nhiễm xoắn khuẩn giang mai của người mắc bệnh thì khả năng bị nhiễm giang mai cũng rất cao. Xoắn khuẩn giang mai trên những vật dụng này có thể xâm nhập vào cơ thể thông qua các vết thương hở.</span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Cách chữa trị bệnh giang mai ở nữ giới</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Trong việc chữa trị các bệnh xã hội nói chung và bệnh giang mai nói riêng thì lựa chọn đúng thời điểm sớm – khi vừa mới bị lây nhiễm sẽ mang lại hiệu quả cao nhất.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chị em không nên e ngại, do dự, lẩn tránh mà cần đối mặt một cách trực diện với căn bệnh này. Khi có các triệu chứng nghi ngờ giang mai thì cần nhanh chóng đến các cơ sở y tế chuyên khoa để khám và thực hiện các chẩn đoán một cách khoa học. Trường hợp bị nhiễm vi rút giang mai thật thì cần tích cực điều trị theo đúng chỉ dẫn của bác sĩ.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-chua-tri-benh-giang-mai-o-nu-gioi-1.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Nguyên nhân và cách chữa trị bệnh giang mai ở nữ giới\" class=\"aligncenter wp-image-4504\" height=\"237\" src=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-chua-tri-benh-giang-mai-o-nu-gioi-1.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Đừng bỏ lỡ thời điểm tốt nhất để chữa trị bệnh giang mai</em></span></strong></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Hiện nay việc phát hiện và chữa trị bệnh giang mai được thực hiện qua 4 bước chính bao gồm xét nghiệm tìm xoắn khuẩn, khống chế vi khuẩn lây lan, tiêu diệt xoắn khuẩn gây bệnh và tiến hành các biện pháp tăng cường hệ miễn dịch.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Các bước khám và chữa trị giang mai trên đã và đang được áp dụng thành công tại&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Phòng khám đa khoa Thiên Hòa</strong>. Bên cạnh đó phòng khám chúng tôi được đầu tư các trang thiết bị y tế hiện đại để phục vụ cho việc điều trị giang mai một cách tốt nhất. Việc ứng dụng điều trị bằng phương pháp Đông – Tây y kết hợp tại Thiên Hòa đã ngăn chặn bệnh giang mai hiệu quả, ngăn&nbsp;</span></p>\r\n</div>\r\n</div>\r\n','nguyen-nhan-va-cach-chua-tri-benh-giang-mai-o-nu-gioi',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','64_1444294876_860561630dc20187.jpg',[now], [now],[user_id],0,'','',[now]);set @news815 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories501,'Nguyên nhân và cách chữa trị bệnh giang mai ở nam giới','Nguyên nhân và cách chữa trị bệnh giang mai ở nam giới<br /><br />\r\n31.12.2014 trong Bệnh Giang Mai<br /><br />\r\n<br /><br />\r\nGiang mai là bệnh trở thành nỗi kinh hoàng của tất cả những người mắc phải, nó có thể bắt gặp ở cả nam giới và nữ giới. Với nam giới, đặc biệt là những người có quan hệ lăng nhăng bên ngoài thì khả năng “dính” bệnh giang mai rất cao. Khi mắc bệnh giang mai nếu chậm trễ điều trị thì khả năng biến chứng đối với nam giới như rối loạn khả năng co thắt, rối loạn cảm giác, đau nhức xương, nguy ','<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: normal; font-family: Arial, Helvetica, sans-serif; color: rgb(205, 90, 36); font-size: 17px; background-color: rgb(255, 255, 255);\"><span itemprop=\"name\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nguyên nhân và cách chữa trị bệnh giang mai ở nam giới</span></h1>\r\n\r\n<p class=\"post-meta\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); font-size: 11.0500001907349px; font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">31.12.2014</span> <span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">trong&nbsp;<a href=\"http://chuabenhxahoi.info/benh-giang-mai\" rel=\"category tag\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); text-decoration: none; transition: all 0.2s ease-in-out;\">Bệnh Giang Mai</a></span></p>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 13px; background-color: rgb(255, 255, 255);\">&nbsp;</div>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); font-size: 13px; background-color: rgb(255, 255, 255);\">\r\n<div id=\"page-content-1\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Giang mai là bệnh trở thành nỗi kinh hoàng của tất cả những người mắc phải, nó có thể bắt gặp ở cả nam giới và nữ giới. Với nam giới, đặc biệt là những người có quan hệ lăng nhăng bên ngoài thì khả năng “dính” bệnh giang mai rất cao. Khi mắc bệnh giang mai nếu chậm trễ điều trị thì khả năng biến chứng đối với nam giới như rối loạn khả năng co thắt, rối loạn cảm giác, đau nhức xương, nguy hiểm đến nội tạng…Vì vậy cần tìm hiểu “<a href=\"http://chuabenhxahoi.info/nguyen-nhan-va-cach-chua-tri-benh-giang-mai-o-nam-gioi.html\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nguyên nhân và cách chữa trị bệnh giang mai ở nam giới</strong></span></a>” để phòng và ngăn chặn bệnh hiệu quả.</span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nguyên nhân bệnh giang mai ở nam giới</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Nguyên nhân dẫn đến giang mai là do xoắn khuẩn nhạt (Treponema pallidum) gây ra. Đây là loại vi khuẩn hoạt động mạnh và có thể gây tổn thương cho nhiều bộ phận khác nhau trên cơ thể người bệnh.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Xoắn xuẩn giang mai sẽ được lây truyền sẽ qua các con đường trực tiếp tiếp xúc giữa người bệnh và người khỏe mạnh hoặc lây gián tiếp qua đồ vật. Bệnh giang mai ở nam giới thường lây qua các con đường sau:</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Lây truyền qua đường tình dục: Gặp ở những đối tượng thường quan hệ tình dục với nhiều người, quan hệ không có các biện pháp bảo vệ an toàn…</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Ngoài ra, giang mai có thể lây truyền qua đường máu: Những nam giới nhận nguồn máu được truyền từ người mắc&nbsp;<a href=\"http://chuabenhxahoi.info/benh-giang-mai\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">bệnh giang mai</strong></span></a>&nbsp;hoặc tiêm chích ma tuý mà bơm tiêm không vô khuẩn, sử dụng cho nhiều người thì khả năng mắc bệnh cũng rất cao.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Bên cạnh đó giang mai còn được truyền từ mẹ sang con trong giai đoạn thai được từ tháng thứ 4 trở đi.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-chua-tri-benh-giang-mai-o-nam-gioi.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Nguyên nhân và cách chữa trị bệnh giang mai ở nam giới\" class=\"aligncenter wp-image-4523\" height=\"263\" src=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-chua-tri-benh-giang-mai-o-nam-gioi.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Những con đường có thể dẫn đến mắc bệnh giang mai</em></span></strong></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chữa trị bệnh giang mai cho nam giới</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Khi xuất hiện các biểu hiện ban đầu của giang mai như xuất hiện các nốt ban, hình thành vết loét có đường kính 1 – 2 cm, xung quanh nổi lên những hình tròn màu đỏ, bề mặt bị loét hoặc loét nhẹ hoặc không có triệu chứng loét… thì nam giới cần nhanh chóng đến các cơ sở y tế chuyên khoa để chẩn đoán, xét nghiệm và điều trị. Giang mai được điều trị càng sớm thì hiệu quả điều trị càng cao, khả năng khỏi hẳn bệnh là lên đến 90%.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Căn cứ vào tình trạng bệnh qua mỗi giai đoạn khác nhau, bác sĩ chuyên khoa sẽ đưa ra phác đồ điều trị riêng. Việc chữa trị giang mai đúng thời điểm, đặc biệt càng chữa sớm càng tốt sẽ hạn chế sự tiến triển của bệnh,&nbsp;</span>ngăn chặn các biến chứng xấu do bệnh gây ra.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-chua-tri-benh-giang-mai-o-nam-gioi1.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Nguyên nhân và cách chữa trị bệnh giang mai ở nam giới\" class=\"aligncenter wp-image-4526\" height=\"268\" src=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-chua-tri-benh-giang-mai-o-nam-gioi1.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nam giới cần chữa trị giang mai theo đúng phác đồ của bác sĩ</em></span></strong></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Những lưu ý khi chữa trị giang mai</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Chữa trị giang mai giai đoạn 1 thì cần chú ý theo dõi thận trọng để ngăn ngừa tái phát, thực hiện tái khám định kỳ theo chỉ định của bác sĩ chuyên khoa. Những trường hợp có hiện tượng tái phát thì có thể được chỉ định tăng thêm liều lượng.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Chữa trị giang mai cần được thực hiện sớm, tránh nóng vội, tuân thủ uống thuốc đủ liều, kiên trì và tuân thủ theo điều trị của bác sĩ các triệu chứng của bệnh mới nhanh được đẩy lùi và hồi phục.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Nếu vợ hoặc chồng bị nhiễm bệnh thì cả hai người cần tiến hành xét nhiệm và điều trị cùng một lúc để ngăn ngừa tình trạng tái phát do chính nguồn bệnh mắc phải.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Chú trọng các biện pháp thanh nhiệt giải độc, tắm và ngâm mình bằng các loại thảo dược có tính diệt khuẩn, tránh lây nhiễm cho người khác. Tuy nhiên để thực hiện điều này thì cần tham khảo ý kiến bác sĩ.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Với những thông tin trên đây hi vọng sẽ giúp nam giới có định hướng tốt trong việc phòng, phát hiện và điều trị giang mai. Với những người mắc bệnh giang mai thì không nên chủ quan xem thường mà cần mạnh dạn đến các cơ sở y tế, phòng khám chuyên khoa bệnh xã hội để khám và chữa trị kịp thời. Hiệu quả chữa trị giang mai phụ thuộc vào thời điểm phát hiện bệnh, lựa chọn phương pháp chữa trị và sự tích cực, kiên trì của người bệnh.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nam giới nói riêng và những người mắc bệnh giang mai nói chung có thể trực tiếp đến&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Phòng khám đa khoa Thiên Hòa</strong>&nbsp;để được các bác sĩ chuyên khoa của chúng tôi khám và đưa ra phác đồ chữa trị an toàn, ngăn chặn các biến chứng mà giang mai có thể gây ra.</span></p>\r\n</div>\r\n</div>\r\n','nguyen-nhan-va-cach-chua-tri-benh-giang-mai-o-nam-gioi',1,'','',[site_id],[user_id],'','',[now], [now],[user_id],0,'','',[now]);set @news816 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories501,'Bệnh giang mai là gì?','Bệnh giang mai là gì?<br /><br />\r\n10.03.2015 trong Bệnh Giang Mai<br /><br />\r\n<br /><br />\r\nBệnh giang mai là gì?<br /><br />\r\n<br /><br />\r\nGiang mai là bệnh được phát hiện từ cách đây 400 năm về trước, bệnh hình thành do xoắn khuẩn Treponema pallidum xâm nhập vào cơ thể khi quan hệ tình dục không an toàn, qua các vết xước trên da hoặc niêm mạc khi tiếp xúc với dịch tiết từ thương tổn giang mai. Bên cạnh đó bệnh có thể lây truyền từ mẹ sang con khi người phụ nữ mang thai mắc bệnh. Vậy cụ thểbệnh giang mai là gì? các thời ','<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: normal; font-family: Arial, Helvetica, sans-serif; color: rgb(205, 90, 36); font-size: 17px; background-color: rgb(255, 255, 255);\"><span itemprop=\"name\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh giang mai là gì?</span></h1>\r\n\r\n<p class=\"post-meta\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); font-size: 11.0500001907349px; font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">10.03.2015</span> <span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">trong&nbsp;<a href=\"http://chuabenhxahoi.info/benh-giang-mai\" rel=\"category tag\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); text-decoration: none; transition: all 0.2s ease-in-out;\">Bệnh Giang Mai</a></span></p>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 13px; background-color: rgb(255, 255, 255);\">&nbsp;</div>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); font-size: 13px; background-color: rgb(255, 255, 255);\">\r\n<div id=\"page-content-1\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\">Bệnh giang mai là gì?</h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Giang mai là bệnh được phát hiện từ cách đây 400 năm về trước, bệnh hình thành do xoắn khuẩn Treponema pallidum xâm nhập vào cơ thể khi quan hệ tình dục không an toàn, qua các vết xước trên da hoặc niêm mạc khi tiếp xúc với dịch tiết từ thương tổn giang mai. Bên cạnh đó bệnh có thể lây truyền từ mẹ sang con khi người phụ nữ mang thai mắc bệnh. Vậy cụ thể<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><a href=\"http://chuabenhxahoi.info/benh-giang-mai-la-gi.html\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\">bệnh giang mai là gì</a>?</strong>&nbsp;các thời kỳ phát triển của nó ra sao? Và cách chữa trị tốt nhất khi bạn bị mắc bệnh.</p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Các thời kỳ phát triển của giang mai</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Giang mai là bệnh lây nhiễm nguy hiểm và phát triển qua 3 thời kỳ với những biểu hiện đặc trưng.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">-&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Thời kỳ I:</strong>&nbsp;là thời kỳ ủ bệnh kéo dài ba tuần. Sau đó khi qua thời gian ủ bệnh, bệnh bắt đầu xuất hiện các triệu chứng của săng và hạch. Săng giang mai xuất hiện ở bộ phận sinh dục, là vết lở tròn hay bầu dục, kích thước 0,5-2cm, giới hạn rõ và đều đặn, đáy sạch màu đỏ như thịt tươi, nền cứng và bóp không đau. Hạch xuất hiện 5-6 ngày sau khi có săng.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><img alt=\"Bệnh giang mai là gì?\" class=\"alignnone wp-image-4906\" height=\"274\" src=\"http://chuabenhxahoi.info/wp-content/uploads/benh-giang-mai-la-gi-1.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; max-width: 100%; height: auto;\" title=\"Bệnh giang mai là gì? 1\" width=\"350\" /></p>\r\n\r\n<h4 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: Helvetica; text-align: center;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(51, 102, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Hình ảnh bệnh giang mai</em></span></h4>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">-&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Thời kỳ II:</strong>&nbsp;là giai đoạn 45 ngày sau khi có săng và có thể kéo dài 2-3 năm. Xuất hiện các tổn thương da và niêm mạc nhưng khi lành không để sẹo. Xoắn khuẩn giang mai dễ gây nhiễm trùng huyết với các triệu chứng nóng sốt và nổi hạch.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">-&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Thời kỳ III:</strong>&nbsp;&nbsp;Xuất hiện thường 5, 10, 15 năm sau khi có săng với các triệu chứng như sang thương sâu, gôm ở da, cơ, xương, nội tạng, tim mạch và thần kinh.</p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Những biến chứng nguy hiểm do giang mai mang lại</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Xoắn khuẩn giang mai có thể gây tổn thương tất cả các cơ quan trong cơ thể.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">-&nbsp;<a href=\"http://chuabenhxahoi.info/benh-giang-mai\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\" title=\"benh giang mai\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh giang mai</strong></a>&nbsp;tác động xấu đến da, niêm mạc, mắt đến các nội tạng như gan, tim mạch, thần kinh.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Bệnh giang mai có thể gây biến chứng nguy hiểm như viêm động mạch chủ, phình động mạch chủ, bại liệt toàn thân, rối loạn tâm thần, viêm gan.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Giang mai bẩm sinh có thể gây tử vong cho thai nhi hoặc dị dạng thai sau khi sinh.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/benh-giang-mai-la-gi.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Bệnh giang mai là gì?\" class=\"aligncenter wp-image-7145\" height=\"311\" src=\"http://chuabenhxahoi.info/wp-content/uploads/benh-giang-mai-la-gi.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Gôm giang mai gây tổn thương viêm nhiễm da nghiêm trọng</em></strong></span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Chữa trị bệnh giang mai bằng cách nào?</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Bệnh giang mai ở giai đoạn 1 và 2 có thể được điều trị với penicillin bằng cách tiêm. Nếu bị dị ứng, bạn nên sử dụng kháng sinh thay thế ở dạng viên nén.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Giai đoạn sau của bệnh cần phải được điều trị bằng thuốc penicillin và tiêm thường xuyên hơn trong 1 tuần.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Ở giai đoạn cuối, việc điều trị chỉ còn là ngăn chặn sự phát triển của bệnh nhưng không thể cải thiện những ảnh hưởng bệnh đã gây ra trước đó.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Việc chữa trị bệnh giang mai cần phải tiến hành càng sớm càng tốt và tuân thủ đúng phác đồ điều trị của bác sĩ chuyên khoa. Nếu việc điều trị không kiên trì, bỏ dở giữa chừng sẽ làm cho bệnh trở nên nặng nề hơn, việc chữa trị sẽ càng khó khăn và tốn kém.</p>\r\n</div>\r\n</div>\r\n','benh-giang-mai-la-gi',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','785_1444294496_12556162f60a4304.jpg',[now], [now],[user_id],0,'','',[now]);set @news817 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories500,'Những suy nghĩ sai lầm về bệnh sùi mào gà','Những suy nghĩ sai lầm về bệnh sùi mào gà<br /><br />\r\n<br /><br />\r\nNhư nhiều người đã biết, sùi mào gà là một trong căn bệnh xã hội nguy hiểm, lây nhiễm chủ yếu qua đường tình dục. Mỗi năm, tỉ lệ người mắc bệnh lại gia tăng khá cao. Một nguyên nhân dẫn đến sự gia tăng này là do mọi người chủ quan và có những suy nghĩ sai lầm về bệnh sùi mào gà. Vì vậy, những thông tin sau đây nhằm giúp mọi người thay đổi cách nghĩ để phòng tránh và hạn chế tối đa sự lây lan bệnh cho cộng đồng.','<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: normal; font-family: Arial, Helvetica, sans-serif; color: rgb(205, 90, 36); font-size: 17px; background-color: rgb(255, 255, 255);\"><span itemprop=\"name\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Những suy nghĩ sai lầm về bệnh sùi mào gà</span></h1>\r\n\r\n<p class=\"post-meta\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); font-size: 11.0500001907349px; font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">8 days ago</span> <span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">trong&nbsp;<a href=\"http://chuabenhxahoi.info/benh-sui-mao-ga\" rel=\"category tag\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); text-decoration: none; transition: all 0.2s ease-in-out;\">Bệnh Sùi Mào Gà</a></span></p>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 13px; background-color: rgb(255, 255, 255);\">&nbsp;</div>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); font-size: 13px; background-color: rgb(255, 255, 255);\">\r\n<div id=\"page-content-1\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Như nhiều người đã biết, sùi mào gà là một trong căn bệnh xã hội nguy hiểm, lây nhiễm chủ yếu qua đường tình dục. Mỗi năm, tỉ lệ người mắc bệnh lại gia tăng khá cao. Một nguyên nhân dẫn đến sự gia tăng này là do mọi người chủ quan và có những suy nghĩ sai lầm về bệnh sùi mào gà. Vì vậy, những thông tin sau đây nhằm giúp mọi người thay đổi cách nghĩ để phòng tránh và hạn chế tối đa sự lây lan bệnh cho cộng đồng.</em></strong></span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Không quan&nbsp;hệ tình dục không có nghĩa là không mắc bệnh</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Sùi mào gà là căn bệnh lây nhiễm chủ yếu qua đường tình dục chứ không phải lây nhiễm hoàn toàn qua đường tình dục. Do đó, nếu ai đang có&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">suy nghĩ sai lầm về bệnh sùi mào gà</em></strong>&nbsp;theo cách này thì nên thay đổi. Bởi bệnh có thể lây nhiễm gián tiếp qua các vật dụng cá nhân của người bệnh nếu chúng ta vô tình dùng phải. Bệnh còn lây nhiễm qua tiếp xúc, cử chỉ thân mật như hôn hay chạm vào vết thương trên cơ thể người bệnh, lây nhiễm qua đường máu từ mẹ sang con…</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/suy-nghi-sai-lam-ve-benh-sui-mao-ga-1.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Suy nghĩ sai lầm về bệnh sùi mào gà 1\" class=\"aligncenter wp-image-7375\" height=\"293\" src=\"http://chuabenhxahoi.info/wp-content/uploads/suy-nghi-sai-lam-ve-benh-sui-mao-ga-1.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" title=\"Suy nghĩ sai lầm về bệnh sùi mào gà 1\" width=\"350\" /></a><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\">Sùi mào gà lây nhiễm qua nhiều đường khác nhau</span></em></strong></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chỉ những người có quan hệ tình dục bừa bãi mới mắc sùi mào gà</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Có những người chung thủy nhưng vẫn có thể mắc sùi mào gà. Bởi bệnh lây nhiễm qua rất nhiều con đường khác nhau, họ vô tình mắc phải nên bị nhiễm bệnh. Vì thế, bạn có thể lây nhiễm bệnh từ bạn tình một cách tự nhiên qua quan hệ tình dục giữa hai người.</span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Trẻ em không thể mắc sùi mào gà</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nhiều người có&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">suy nghĩ sai lầm về bệnh sùi mào gà</em></strong>&nbsp;rằng, trẻ em thì sẽ không bị mắc bệnh. Trên thực tế, nếu phụ nữ bị mắc sùi mào khi đang mang thai thì đứa trẻ sẽ bị lây nhiễm từ người mẹ qua sinh thường. Hoặc đứa trẻ tiếp xúc nhiều với người mẹ hàng ngày cũng sẽ bị lây nhiễm.</span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Sử dụng bao cao su sẽ không lo bị sùi mào gà</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Sử dụng bao cao su là biện pháp phòng tránh và giảm nguy cơ mắc các bệnh lây nhiễm qua đường tình dục. Những trường hợp bao cao su bị rách hoặc bị xê dịch thì vẫn có thể mắc như thường. Bên cạnh đó, bao cao su chỉ ngăn ngừa một bệnh chứ không có tác dụng kháng bệnh hoàn toàn.</span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Sùi mào gà chỉ lây qua tình dục đường âm đạo</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nhiều người nghĩ, sùi mào gà chỉ lây nhiễm khi quan hệ tình dục qua âm đạo. Sùi mào gà có thể lây nhiễm qua tất cả con đường tình dục qua hậu môn, miệng (oral sex). Hoặc chỉ là những tiếp xúc, động chạm trên cơ thể cũng&nbsp;khiến đối phương bị lây nhiễm chứ không riêng gì quan hệ qua âm đạo.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/suy-nghi-sai-lam-ve-benh-sui-mao-ga-2.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Suy nghĩ sai lầm về bệnh sùi mào gà 2\" class=\"aligncenter wp-image-7376\" height=\"245\" src=\"http://chuabenhxahoi.info/wp-content/uploads/suy-nghi-sai-lam-ve-benh-sui-mao-ga-2.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" title=\"Suy nghĩ sai lầm về bệnh sùi mào gà 2\" width=\"350\" /></a><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\">Oral sex cũng là nguyên nhân gây sùi mào gà</span></em></strong></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh sùi mào gà không tái phát</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Sùi mào gà là căn bệnh rất khó điều trị tận gốc, do vậy, bệnh vẫn có thể tái phát đi tái phát lại. Bên cạnh đó, bệnh tái phát còn do người bệnh lây nhiễm lại từ bạn tình. Do vậy, khi điều trị sùi mào gà mọi người nên đi điều trị sớm và phải điều trị cho cả bạn tình (nếu cũng bị mắc). Nguy cơ tái phát bệnh mới có thể hạn chế được.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Trên đây là những&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">suy nghĩ sai lầm về bệnh sùi mào gà</em></strong>&nbsp;mà mọi người hay mắc phải. Thông qua kiến thức này sẽ giúp mọi người thay đổi cách nghĩ, từ đó có cách phòng tránh và hạn chế tối đa sự lây nhiễm bệnh cho cộng đồng. Hiện nay,&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Phòng khám đa khoa Thiên Hòa</strong>&nbsp;đang điều trị hiệu quả sùi mào gà và các bệnh xã hội khác. Nếu không may bị bệnh, mọi người hãy liên hệ ngay tới Phòng khám để nhận được sự tư vấn và hướng dẫn làm điều trị. &nbsp;</span></p>\r\n</div>\r\n</div>\r\n','nhung-suy-nghi-sai-lam-ve-benh-sui-mao-ga',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','559_1444297226_98656163a0a27564.jpg',[now], [now],[user_id],1,'','',[now]);set @news818 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories500,'Nguyên nhân và cách chữa trị bệnh sùi mào gà ở nữ giới','Nguyên nhân và cách chữa trị bệnh sùi mào gà ở nữ giới<br /><br />\r\n<br /><br />\r\nSùi mào gà ở nữ giới gây nên nhiều biến chứng nguy hại như ung thư cổ tử cung, ảnh hưởng đến sức khỏe sinh sản, phụ nữ mang thai mắc sùi mào gà có thể gây nguy hiểm đến tính mạng của trẻ,…Do đó việc hiểu biết về bệnh sùi mào gà là vô cùng cần thiết. Dưới đây các bác sĩ chuyên khoa bệnh xã hội của Phòng khám đa khoa Thiên Hòa sẽ chỉ ra cho bạn những nguyên nhân và cách điều trị sùi mào gà ở nữ giới một cách chi tiết nhất.','<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: normal; font-family: Arial, Helvetica, sans-serif; color: rgb(205, 90, 36); font-size: 17px; background-color: rgb(255, 255, 255);\"><span itemprop=\"name\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nguyên nhân và cách chữa trị bệnh sùi mào gà ở nữ giới</span></h1>\r\n\r\n<p class=\"post-meta\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); font-size: 11.0500001907349px; font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">31.12.2014</span> <span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">trong&nbsp;<a href=\"http://chuabenhxahoi.info/benh-sui-mao-ga\" rel=\"category tag\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); text-decoration: none; transition: all 0.2s ease-in-out;\">Bệnh Sùi Mào Gà</a></span></p>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 13px; background-color: rgb(255, 255, 255);\">&nbsp;</div>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); font-size: 13px; background-color: rgb(255, 255, 255);\">\r\n<div id=\"page-content-1\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Sùi mào gà ở nữ</em></strong>&nbsp;giới gây nên nhiều biến chứng nguy hại như ung thư cổ tử cung, ảnh hưởng đến sức khỏe sinh sản, phụ nữ mang thai mắc sùi mào gà có thể gây nguy hiểm đến tính mạng của trẻ,…Do đó việc hiểu biết về bệnh sùi mào gà là vô cùng cần thiết. Dưới đây các bác sĩ chuyên khoa bệnh xã hội của&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Phòng khám đa khoa Thiên Hòa</strong>&nbsp;sẽ chỉ ra cho bạn những nguyên nhân và cách điều trị&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">sùi mào gà ở nữ giới</em></strong>&nbsp;một cách chi tiết nhất.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- &nbsp;bệnh sùi mào gà ở nữ giới</strong></span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><a href=\"http://chuabenhxahoi.info/bieu-hien-benh-sui-mao-ga-o-nu-gioi.html\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Sùi mào gà ở nữ</em></strong></span></a>&nbsp;giới&nbsp;<span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">cơ bản có những nguyên nhân chính như sau:</span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">1. Nhiễm sùi mào gà do quan hệ tình dục không an toàn</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Đây là nguyên nhân cơ bản và trực tiếp khiến nữ giới nhiễm virus HPV gây nên các u nhú trên cơ thể. Khi phụ nữ có quan hệ với người mắc bệnh sùi mào gà mà không sử dụng biện pháp an toàn nào sẽ dễ dàng mắc sùi mào gà. HPV thông qua đường tình dục ở âm đạo, hậu môn và đường miệng xâm nhập vào cơ thể ủ bệnh từ 2-6 tháng mới phát bệnh.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Thông thường phụ nữ có tỷ lệ mắc sùi mào gà ít hơn nam giới, do đời sống tình dục hạn chế hơn trừ một số trường hợp đặc biệt ( gái mại dâm, phụ nữ có nhu cầu tình dục cao, nghiện sex). Phụ nữ thường bị lây nhiễm qua chồng, thời kỳ mang thai cũng chiếm tỷ lệ quá cao. Phụ nữ thời kỳ mang thai mắc sùi mào gà có nguy cơ lây nhiễm bệnh sang cho thai nhi là khá cao.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><img alt=\"Nguyên nhân và cách chữa trị bệnh sùi mào gà ở nữ giới\" class=\"alignnone wp-image-4536\" height=\"233\" src=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-chua-tri-sui-mao-ga-o-nu-gioi-1.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; max-width: 100%; height: auto;\" title=\"Nguyên nhân và cách chữa trị bệnh sùi mào gà ở nữ giới 1\" width=\"350\" /></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Tình dục không an toàn gây sùi mào gà</em></strong></span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">2. Hệ miễn dịch yếu</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Những phụ nữ có hệ miễn dịch yếu nếu sống chung, tiếp xúc với người mắc bệnh sùi mào gà, dùng chung vật dụng cá nhân như bàn chải đánh răng, quần áo lót, bồn tắm, chăn ga gối đệm cũng có thể bị lây nhiễm.</span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\">Cách chữa trị bệnh sùi mào gà ở nữ giới</h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Có rất nhiều phương pháp điều trị sùi mào gà cho nữ, tùy vào từng trường hợp bệnh, mức độ, mong muốn điều trị của người bệnh. Có những phương pháp chữa&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">trị sùi mào gà ở nữ giới</em></strong>&nbsp;như sau:</span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">1. Dùng thuốc</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Đây được coi là phương pháp truyền thống từ rất lâu trước khi có sự ra đời của các kỹ thuật y khoa hiện đại. Tuy không thể trị dứt điểm nhưng đối với một số trường hợp bắt buộc (những vị trí không thể sử dụng kỹ thuật đốt và điều trị) thì sử dụng thuốc bôi tại chỗ là hữu hiệu nhất để giúp. Thuốc điều trị sùi mào gà bao gồm thuốc bôi, thuốc uống, thuốc tiêm. Các trường hợp thường áp dụng điều trị bằng thuốc bao gồm: sùi mào gà ở âm đạo, sùi mào gà ở miệng việc sử dụng các loại thuốc bôi, chấm tại chỗ sẽ mang lại hiệu quả.</span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">2. Đốt laser</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Dùng tia laser chuyên dụng để phá vỡ cấu trúc u nhú sùi mào gà, loại bỏ các mụn sùi trên bề mặt niêm mạc da. Tia laser là phương pháp nhanh chóng loại bỏ u nhú, tuy nhiên chúng không thể diệt được virus trong cơ thể mà chỉ tiêu diệt được triệu chứng bệnh, dễ gây tái phát.</span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">3. Kết hợp thuốc và laser</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Ngoài việc dùng tia laser loại bỏ u nhú trên bề mặt da, các bác sĩ tại phòng khám Thiên Hòa sẽ kết hợp sử dụng thuốc để điều trị cho người bệnh nhằm nâng cao sức đề kháng. Tùy vào từng trường hợp và mức độ bệnh mà bác sĩ sẽ có phác đồ điều trị khác nhau, áp dụng việc theo dõi, tái khám định kỳ (theo dõi 6-8 tháng). Sử dụng các loại thuốc bao gồm: Thuốc tăng khả năng miễn dịch, thuốc kháng virus, thuốc kháng sinh tiêu viêm chống nhiễm trùng để nâng cao hiệu quả điều trị.</span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">4. Phương pháp cấy tế bào (tự thân)</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Các bác sĩ sẽ tiến hành việc lấy các&nbsp; tế bào sùi mào gà cấy sang vùng mao mạch mới để tiêu diệt virus bằng cách sử dụng thuốc kháng virus và thuốc Đông y tăng hệ miễn dịch cho cơ thể. Đây được coi là phương pháp lấy độc trị độc cũng mang lại kết quả khá cao, tăng sức đề kháng lại với virus sùi mào gà.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><img alt=\"Nguyên nhân và cách chữa trị bệnh sùi mào gà ở nữ giới\" class=\"alignnone wp-image-4537\" height=\"232\" src=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-chua-tri-sui-mao-ga-o-nu-gioi-2.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; max-width: 100%; height: auto;\" title=\"Nguyên nhân và cách chữa trị bệnh sùi mào gà ở nữ giới 2\" width=\"350\" /></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Phụ nữ mang thai mắc sùi mào gà có thể lây nhiễm cho thai nhi</em></span></strong></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">5. Phương pháp ALA – PDT</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Đây là phương pháp chữa trị sùi mào gà hiện đại với công nghệ Mỹ được các chuyên gia hàng đầu về chữa trị sùi mào gà khuyên dùng tại các cơ sở y tế chuyên khoa nhằm mang lại hiệu quả triệt để cho người bệnh ALA – PDT là phương pháp điều trị nhanh chóng, không gây đau đớn, an toàn và triệt để chỉ với một lần điều trị, tỷ lệ tái phát sau điều trị hầu như không có.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Đối với nữ giới thì phương pháp chữa sùi mào gà bằng ALA – PDT là phương pháp an toàn, khả thi nhất giúp bệnh nhân sau điều trị vẫn giữ được chức năng sinh sản một cách an toàn nhất. Do đó, nếu mắc sùi mào gà chị em sử dụng phương pháp điều trị này để đảm bảo an toàn tuyệt đối, tránh tái phát.</span></p>\r\n</div>\r\n</div>\r\n','nguyen-nhan-va-cach-chua-tri-benh-sui-mao-ga-o-nu-gioi',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','318_1444294118_28056162de6bd994.jpg',[now], [now],[user_id],0,'','',[now]);set @news819 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories500,'Bệnh sùi mào gà là gì?','<br /><br />\r\nBệnh sùi mào gà là gì?<br /><br />\r\n<br /><br />\r\nBệnh sùi mào gà là một bệnh lây qua con đường tình dục khi biểu hiện giống như mào của con gà. Sùi mào gà chủ yếu xuất hiện trên bộ phận sinh dục của cả nam và nữ. Bệnh sùi mào gà nếu không được chữa trị kịp thời có thể chuyển thành ác tính gây ung thư cổ tử cung ở phụ nữ và ung thư dương vật ở nam giới. Chúng ta đã biết bệnh sùi mào gà là gì, cùng tìm hiểu nguyên do và cách điều trị bệnh<br /><br />\r\n- Bệnh sùi mào gà xuất hiện do virut Human papova (HPV)','<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: normal; font-family: Arial, Helvetica, sans-serif; color: rgb(205, 90, 36); font-size: 17px; background-color: rgb(255, 255, 255);\"><span itemprop=\"name\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh sùi mào gà là gì?</span></h1>\r\n\r\n<p class=\"post-meta\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); font-size: 11.0500001907349px; font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">28.02.2015</span> <span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">trong&nbsp;<a href=\"http://chuabenhxahoi.info/benh-sui-mao-ga\" rel=\"category tag\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); text-decoration: none; transition: all 0.2s ease-in-out;\">Bệnh Sùi Mào Gà</a></span></p>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 13px; background-color: rgb(255, 255, 255);\">&nbsp;</div>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); font-size: 13px; background-color: rgb(255, 255, 255);\">\r\n<div id=\"page-content-1\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh sùi mào gà là gì?</strong></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh sùi mào gà</strong>&nbsp;là một bệnh lây qua con đường tình dục khi biểu hiện giống như mào của con gà. Sùi mào gà chủ yếu xuất hiện trên bộ phận sinh dục của cả nam và nữ. Bệnh sùi mào gà nếu không được chữa trị kịp thời có thể chuyển thành ác tính gây ung thư cổ tử cung ở phụ nữ và ung thư dương vật ở nam giới. Chúng ta đã biết&nbsp;<a href=\"http://chuabenhxahoi.info/benh-sui-mao-ga-la-gi.html\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">bệnh sùi mào gà là gì</strong></a>, cùng tìm hiểu nguyên do và cách điều trị bệnh</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Bệnh sùi mào gà xuất hiện do virut Human papova (HPV) gây nên và lây qua đường tình dục.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Sùi mào gà có thời gian ủ bệnh kéo dài từ 2 – 9 tháng sau khi quan hệ tình dục với người có mang mầm bệnh HPV.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- HPV có thể kết hợp với virut gây bệnh hạt cơm ở da lòng bàn tay, lòng bàn chân, ngón tay, ngón chân.</p>\r\n\r\n<h4 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: Helvetica; text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/132.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Bệnh sùi mào gà là gì?\" class=\"aligncenter wp-image-5170\" height=\"284\" src=\"http://chuabenhxahoi.info/wp-content/uploads/132.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Tình dục không an toàn gây phát sinh sùi mào gà</em></span></h4>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Triệu chứng biểu hiện của sùi mào gà</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Sau khi nhiễm virus 2-9 tháng bệnh mới phát ra bên ngoài với các biểu hiện như xuất hiện sùi nhỏ mềm, cao lên như những nhú gai, đường kính khoảng 1-2 mm; hoặc là những đĩa bẹt tròn nhỏ bề mặt ráp, màu hồng.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Bệnh có thể phát triển thành những gai hoặc lá, chiều dài có thể đến vài cm, liên kết với nhau thành một mảng rộng trông giống như mào gà hoặc hoa súp lơ màu trắng hồng.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Bề mặt sùi mềm, mủn ra, ẩm ướt, giữa các nhú sùi có thể có mủ.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">-&nbsp;<a href=\"http://chuabenhxahoi.info/bieu-hien-sui-mao-ga-o-nam-gioi-va-cach-dieu-tri.html\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Sùi mào gà ở nam giới</strong></a>&nbsp;có thể gặp ở rãnh quy đầu, miệng sáo, phần đầu của niệu đạo trước hoặc có cả ở da bìu.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Sùi mào gà ở chị em thường xuất hiện ở vùng âm vật, âm hộ, môi lớn, môi bé và còn có thể gặp cả ở cổ tử cung.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Sùi mào gà phát triển mạnh trong điều kiện như ẩm ướt hoặc đang trong thời kỳ mang thai</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Một số trường hợp sùi mào gà có thể phát triển to bằng nắm tay, màu đỏ và tiết dịch mùi hôi.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">- Sùi mào gà không gây đau đớn, nếu sùi phát triển to quá có thể gây khó chịu khi đi lại. Một vài trường hợp có thể bị sốt cao hoặc đau đớn.</p>\r\n\r\n<h4 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: Helvetica; text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/142.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Bệnh sùi mào gà là gì?\" class=\"aligncenter wp-image-5172\" height=\"276\" src=\"http://chuabenhxahoi.info/wp-content/uploads/142.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Sùi mào gà xuất hiện chủ yếu ở bộ phận sinh dục</strong></em></span></h4>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Tránh nhầm lẫn sùi mào gà với một số bệnh</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Sùi mào gà chủ yếu xuất hiện ở bộ phận sinh dục, hậu môn và một số bộ phận khác nên dễ nhầm lẫn với&nbsp; một số bệnh như : bệnh giang mai, bệnh u hạt bẹn, bệnh u (các sẩn nhỏ, tròn và lõm ở trung tâm).</p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Phòng và chữa trị sùi mào gà</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Cần điều trị sớm sùi mào gà khi xuất hiện các biểu hiện để tránh các biến chứng gây nhưung thư cổ tử cung, ung thư dương vật.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Nếu để lâu sẽ rất khó điều trị dứt điểm và dễ bị bội nhiễm vi khuẩn.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Tránh quan hệ tình dục để phòng bệnh lây nhiễm cho người khác.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><a href=\"http://chuabenhxahoi.info/bieu-hien-sui-mao-ga-o-nam-gioi-va-cach-dieu-tri.html\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Điều trị sùi mào gà</strong></a>&nbsp;có thể đốt điện hoặc chấm dung dịch như podophyllin 25%, hoặc chấm acidtrichloracetic 80%. Ngoài ra có các phương pháp điều trị khác như nitơ lỏng, laser hoặc phẫu thuật, được tiến hành ở những trung tâm y tế có đủ trang thiết bị và kỹ thuật.</p>\r\n</div>\r\n</div>\r\n','benh-sui-mao-ga-la-gi',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','142_1444293935_33056162d2f77ef5.jpg',[now], [now],[user_id],1,'','',[now]);set @news820 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories499,'Những điều cần biết về bệnh Chlamydia','Những điều cần biết về bệnh Chlamydia<br /><br />\r\n<br /><br />\r\nBệnh Chlamydia là bệnh lây truyền qua đường tình dục xuất hiện phổ biến ở những người có quan hệ tình dục không an toàn. Bệnh hình thành do vi khuẩn Chlamydia trachomatis gây ra. Chlamydia không được phát hiện và chữa trị kịp thời sẽ gây ra các biến chứng nhiễm trùng ở cổ tử cung, vòi trứng, niệu đạo…','<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: normal; font-family: Arial, Helvetica, sans-serif; color: rgb(205, 90, 36); font-size: 17px; background-color: rgb(255, 255, 255);\"><span itemprop=\"name\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Những điều cần biết về bệnh Chlamydia</span></h1>\r\n\r\n<p class=\"post-meta\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); font-size: 11.0500001907349px; font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">30.06.2014</span> <span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">trong&nbsp;<a href=\"http://chuabenhxahoi.info/benh-chlamydia\" rel=\"category tag\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); text-decoration: none; transition: all 0.2s ease-in-out;\">Bệnh Chlamydia</a></span></p>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 13px; background-color: rgb(255, 255, 255);\">&nbsp;</div>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); font-size: 13px; background-color: rgb(255, 255, 255);\">\r\n<div id=\"page-content-1\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh Chlamydia</strong>&nbsp;là bệnh lây truyền qua đường tình dục xuất hiện phổ biến ở những người có quan hệ tình dục không an toàn. Bệnh hình thành do vi khuẩn Chlamydia trachomatis gây ra. Chlamydia không được phát hiện và chữa trị kịp thời sẽ gây ra các biến chứng nhiễm trùng ở cổ tử cung, vòi trứng, niệu đạo…</span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh Chlamydia xuất hiện do đâu?</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Vi khuẩn&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chlamydia</strong>&nbsp;trachomatis sẽ lây lan cho người khác thông qua quan hệ tình dục không được bảo vệ an toàn.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Nếu người mẹ mang thai mắc bệnh Chlamydia thì có thể dễ dàng lây truyền sang cho con. Chlamydia có thể gây viêm phổi hoặc nhiễm trùng mắt cho trẻ sơ sinh.</span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chlamydia</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh Chlamydia</strong>&nbsp;trong giai đoạn đầu thường có ít các biểu hiện nổi trội. Để sớm phát hiện bệnh thì chị em cần thăm khám kịp thời khi xuất hiện các triệu chứng sau:</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Xuất hiện cảm giác đau như: đau ở bụng, tiểu tiện đau, đau khi quan hệ tình dục, đối với nam giới mắc bệnh thì đau tinh hoàn.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Người bệnh chảy dịch tiết âm đạo (phụ nữ) và dương vật (nam giới).</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0);\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/benh-chlamydia1.jpeg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Những điều cần biết về bệnh Chlamydia\" class=\"aligncenter wp-image-3504\" height=\"262\" src=\"http://chuabenhxahoi.info/wp-content/uploads/benh-chlamydia1.jpeg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Phòng bệnh Chlamydia cần phải làm gì?</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Sử dụng bao cao su khi quan hệ tình dục và tránh quan hệ quá thô bạo dễ gây tổn thương cơ quan sinh dục.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Trước và sau khi quan hệ tình dục cần vệ sinh sạch sẽ để ngăn ngừa vi khuẩn xâm nhập và gây bệnh.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Nên thực hiện đời sống vợ chồng chung thủy, tránh các quan hệ ngoài luồng vì nguy cơ lây bệnh từ những đối tượng này rất cao.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Kiểm tra sức khỏe định kỳ đối với các bệnh lây truyền qua đường tình dục.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Tránh thụt rửa âm đạo vì nó làm giảm số lượng vi khuẩn tốt.</span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chữa trị bệnh Chlamydia</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Khi có các triệu chứng của&nbsp;<a href=\"http://chuabenhxahoi.info/benh-chlamydia\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\" title=\"benh chlamydia\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">bệnh Chlamydia</strong></a>, người bệnh cần đến gặp bác sĩ chuyên khoa để thăm khám và lựa chọn phương pháp điều trị hợp lý nhất cho tình trạng của mỗi người.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh Chlammydia có thể được điều trị bằng các loại thuốc kháng sinh. Các thuốc kháng sinh dạng viên là lựa chọn phổ biến nhất trong việc chữa trị Chlamydia.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Khi tiến hành điều trị bệnh Chlamydia người bệnh tuyệt đối không được quan hệ tình dục vì khả năng lây nhiễm cho người khác là rất lớn.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nên chú trọng đến việc phát hiện và điều trị cho cả bạn tình để phòng bệnh có thể tái phát sau khi điều trị và quan hệ trở lại.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Đối với bệnh Chlamydia nói riêng và các bệnh lây truyền qua đường tình dục nói chung đều rất nguy hiểm, đặc biệt khi người bệnh tự ý dùng thuốc theo sự chỉ dẫn ở các trang thông tin trên mạng. Để điều trị bệnh an toàn, hiệu quả thì đòi hỏi người bệnh phải đến các cơ sở y tế chuyên khoa để thăm khám kỹ càng, sau đó tuân thủ theo đúng phác đồ điều trị của bác sĩ.</p>\r\n</div>\r\n</div>\r\n','nhung-dieu-can-biet-ve-benh-chlamydia',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','266_1444293724_44956162c5c012f8.jpg',[now], [now],[user_id],0,'','',[now]);set @news821 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories499,'Chlamydia gây ra những nguy cơ thai sản nào?','Chlamydia gây ra những nguy cơ thai sản nào?<br /><br />\r\n<br /><br />\r\nNhiễm khuẩn Chlamydia chủ yếu qua quan hệ tình dục không an toàn và gây ra những ảnh hưởng xấu đến sức khỏe, sinh hoạt của người bệnh. Chlamydia tác động đến cả nam giới và phụ nữ, tuy nhiên mức độ nguy hiểm đối với mỗi giới không giống nhau. Theo các bác sĩ chuyên khoa bệnh xã hội của Phòng khám đa khoa Thiên Hòa thì Chlamydia gây ra các biến chứng nặng nề đối với người bệnh, đặc biệt những chị em đang trong thời kỳ sinh sản nếu mắc bệ','<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: normal; font-family: Arial, Helvetica, sans-serif; color: rgb(205, 90, 36); font-size: 17px; background-color: rgb(255, 255, 255);\"><span itemprop=\"name\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chlamydia gây ra những nguy cơ thai sản nào?</span></h1>\r\n\r\n<p class=\"post-meta\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); font-size: 11.0500001907349px; font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">04.07.2014</span> <span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">trong&nbsp;<a href=\"http://chuabenhxahoi.info/benh-chlamydia\" rel=\"category tag\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); text-decoration: none; transition: all 0.2s ease-in-out;\">Bệnh Chlamydia</a></span></p>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 13px; background-color: rgb(255, 255, 255);\">&nbsp;</div>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); font-size: 13px; background-color: rgb(255, 255, 255);\">\r\n<div id=\"page-content-1\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nhiễm khuẩn Chlamydia chủ yếu qua quan hệ tình dục không an toàn và gây ra những ảnh hưởng xấu đến sức khỏe, sinh hoạt của người bệnh. Chlamydia tác động đến cả nam giới và phụ nữ, tuy nhiên mức độ nguy hiểm đối với mỗi giới không giống nhau. Theo các bác sĩ chuyên khoa bệnh xã hội của Phòng khám đa khoa Thiên Hòa thì Chlamydia gây ra các biến chứng nặng nề đối với người bệnh, đặc biệt những chị em đang trong thời kỳ sinh sản nếu mắc bệnh này thì nguy cơ xảy ra tình trạng vô sinh, sảy thai hay sinh non là rất lớn.</span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Những triệu chứng của Chlamydia</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chlamydia là bệnh lây nhiễm xuất hiện khá phổ biến ở những người có đời sống tình dục không an toàn. Sau khi bị lây nhiễm một thời gian (trong vòng 3 tuần kể từ thời điểm bị nhiễm), bệnh sẽ xuất hiện những triệu chứng khó chịu đặc trưng như:</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Ở nữ giới : xuất hiện ngứa vùng kín, khí hư có lẫn mủ, bàng quang có biểu hiện căng tức kèm theo nóng rát khi tiểu tiện, rong kinh, xuất hiện các cảm giác đau ở vùng bụng dưới và đau khi quan hệ tình dục, bệnh nặng có thể gây sốt cao và buồn nôn.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Ở nam giới: niệu quản xuất hiện dịch nhầy lẫn mủ; tiểu đau và có cảm giác bỏng rát.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Triệu chứng chung cho cả nam và&nbsp;nữ là xuất hiện viêm giác mạc, đau khớp, viêm họng và có thể dẫn đến viêm phổi.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Những triệu chứng của Chlamydia đôi khi xuất hiện không rõ ràng, có thể nhầm lẫn với một số bệnh lý khác nên người bệnh cần thực hiện việc thăm khám kỹ càng, cần đến các cơ sở y tế chuyên khoa để thực hiện các biện pháp thăm khám, xét nghiệm cho chắc chắn.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/bien-chung-thai-san-do-chlamydia.jpeg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Chlamydia gây ra những nguy cơ thai sản nào?\" class=\"aligncenter wp-image-3542\" height=\"281\" src=\"http://chuabenhxahoi.info/wp-content/uploads/bien-chung-thai-san-do-chlamydia.jpeg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chị em cần đề phòng với những biến chứng thai sản do Chlamydia</em></span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Những biến chứng nguy hại của Chlamydia đến thai sản</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chlamydia nếu không được phát hiện và điều trị sớm sẽ dẫn đến hình thành các ổ ápxe ở cơ quan sinh sản của người bệnh. Bệnh gây ra những tác động xấu đến thể chất và đời sống tình dục, làm đảo lộn đến mọi mặt của cuộc sống.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Điều nguy hại đe dọa đối với những chị em mắc&nbsp;<a href=\"http://chuabenhxahoi.info/benh-chlamydia\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\" title=\"benh chlamydia\">bệnh Chlamydia</a>&nbsp;là ảnh hưởng lớn đến khả năng sinh sản. Người phụ nữ khi bị nhiễm bệnh nếu mang thai sẽ làm tăng nguy cơ chửa ngoài tử cung, sảy thai, sinh non và nặng nề nhất là có thể dẫn đến vô sinh.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Một số trường hợp sinh con ra gặp các biến chứng do nhiễm khuẩn gây nên tình trạng viêm giác mạc, viêm tai giữa hoặc viêm phổi đe dọa tính mạng của trẻ.</span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Làm gì để phòng và chữa Chlamydia hiệu quả?</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0);\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/chlamydia-gay-nguy-co-thai-san-nao.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Chlamydia gây ra những nguy cơ thai sản nào?\" class=\"aligncenter wp-image-5733\" height=\"234\" src=\"http://chuabenhxahoi.info/wp-content/uploads/chlamydia-gay-nguy-co-thai-san-nao.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0);\">&nbsp;</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\">Đề phòng chlamydia bằng nhiều cách khác nhau</span></em></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Việc điều tiên cần làm đối với mỗi người khi xuất hiện các triệu chứng của bệnh là phải đến ngay các phòng khám chuyên khoa để thực hiện các biện pháp thăm khám, xác định chính xác bệnh.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Sau khi phát hiện bệnh thì cần tích cực điều trị sớm theo đúng phác đồ điều trị của bác sĩ chuyên khoa để nâng cao hiệu quả chữa bệnh cũng như ngăn ngừa bệnh lây lan hay tái phát sau điều trị.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Để phòng tránh cần có phác đồ phòng ngừa và theo dõi, thăm khám định kỳ giúp phát hiện bệnh một cách sớm nhất.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Điều trị Chlamydia có thể dùng thuốc kháng sinh, tuy nhiên việc điều trị cần thực hiện song song cho cả hai vợ chồng; điều trị theo đúng chỉ định của bác sỹ và cần làm xét nghiệm kiểm tra sau khi kết thúc liệu trình.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chlamydia là bệnh lây nhiễm không được xem thường bởi nó chứa đựng nhiều yếu tố nguy cơ ảnh hưởng đến sức khỏe sinh sản. Khi bạn có dấu hiệu nghi ngờ mắc bệnh có thể liên hệ đến&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Phòng khám đa khoa Thiên Hòa</strong>&nbsp;để được điều trị một cách tốt nhất.</span></p>\r\n</div>\r\n</div>\r\n','chlamydia-gay-ra-nhung-nguy-co-thai-san-nao',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','421_1444293504_5956162b8056d21.jpg',[now], [now],[user_id],0,'','',[now]);set @news822 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories499,'Những điểm giống nhau giữa Chlamydia và bệnh lậu','Những điểm giống nhau giữa Chlamydia và bệnh lậu<br /><br />\r\nChlamydia và bệnh lậu là những bệnh lây nhiễm nguy hiểm đối với người bệnh, hai bệnh này thường có một số điểm giống nhau và có thể đi kèm với nhau, làm cho việc chữa trị càng trở nên khó khăn hơn. Dưới đây các bác sĩ chuyên khoa bệnh xã hội của Phòng khám đa khoa Thiên Hòa sẽ phân tích cho người bệnh thấy được những điểm giống nhau của','<p class=\"post-meta\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); font-size: 11.0500001907349px; font-family: Arial, Helvetica, sans-serif;\"><span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">16.07.2014</span> <span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">trong&nbsp;<a href=\"http://chuabenhxahoi.info/benh-chlamydia\" rel=\"category tag\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); text-decoration: none; transition: all 0.2s ease-in-out;\">Bệnh Chlamydia</a>,&nbsp;<a href=\"http://chuabenhxahoi.info/benh-lau\" rel=\"category tag\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); text-decoration: none; transition: all 0.2s ease-in-out;\">Bệnh Lậu</a></span></p>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both;\">\r\n<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"font-family: Arial, Helvetica, sans-serif; padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(205, 90, 36); font-size: 17px;\"><span itemprop=\"name\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Những điểm giống nhau giữa Chlamydia và bệnh lậu</span></h1>\r\n</div>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; font-family: Arial, Helvetica, sans-serif;\">\r\n<div id=\"page-content-1\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chlamydia</strong>&nbsp;và&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">bệnh lậu</strong>&nbsp;là những bệnh lây nhiễm nguy hiểm đối với người bệnh, hai bệnh này thường có một số điểm giống nhau và có thể đi kèm với nhau, làm cho việc chữa trị càng trở nên khó khăn hơn. Dưới đây các bác sĩ chuyên khoa bệnh xã hội của Phòng khám đa khoa Thiên Hòa sẽ phân tích cho người bệnh thấy được những điểm giống nhau của</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">&nbsp;2 bệnh này như sau:</p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat rgb(255, 255, 255);\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Những điểm giống nhau giữa Chlamydia và lậu</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">- Hai bệnh này dễ dàng lây nhiễm ở cổ tử cung của phụ nữ và đường tiết niệu nam giới.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">- Cùng một lúc có thể xuất hiện cả&nbsp;<a href=\"http://chuabenhxahoi.info/benh-chlamydia\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chlamydia</strong></a>&nbsp;và lậu đi kèm với nhau.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">- Bệnh lậu và Chlamydia đều lây truyền qua đường quan hệ tình dục không an toàn.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">- Ngoài ra cả lậu và Chlamydia có thể thấy xuất hiện ở miệng, âm đạo hoặc hậu môn; mầm bệnh còn có thể lây truyền từ mẹ sang con trong khi sinh và gây ra những tác hại xấu đối với sức khỏe và tính mạng của trẻ.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">- Vi khuẩn của cả 2 bệnh kể trên có thể gây nhiễm ở niệu đạo, vùng hầu họng, trực tràng ở cả nam lẫn nữ.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/Chlamydia-v%C3%A0-b%E1%BB%87nh-l%E1%BA%ADu.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Chlamydia và bệnh lậu\" class=\"wp-image-5652 aligncenter\" height=\"234\" src=\"http://chuabenhxahoi.info/wp-content/uploads/Chlamydia-v%C3%A0-b%E1%BB%87nh-l%E1%BA%ADu.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: center; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Quan hệ tình dục không được bảo vệ là con đường ngắn nhất dẫn đến mắc Chlamydia và lậu</em></span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">- Đối với nam giới thì gây ra chứng viêm nhiễm mào tinh hoàn</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">- Đối với phụ nữ thì cả 2 bệnh này đều tác động xấu đến cổ tử cung, tử cung và 2 ống dẫn trứng. Nếu không được phát hiện và chữa trị kịp thời thì&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">lậu và chlamydia</strong>&nbsp;sẽ gây ra viêm phần phụ, sẹo vòi trứng, từ đó dẫn đến chửa ngoài tử cung hoặc vô sinh cho nữ giới..</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">- Cả 2 bệnh này có thể gây nhiễm trùng lan tỏa ở da, khớp và các hệ thống khác.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">- Nhiễm lậu cầu và chlamydia tạo thuận lợi cho việc lây truyền HIV sang bạn tình.</p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat rgb(255, 255, 255);\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Triệu chứng của Chlamydia và lậu xuất hiện ở đâu?</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">Theo phân tích của các bác sĩ chuyên khoa bệnh xã hội của Phòng khám đa khoa Thiên Hòa thì căn cứ vào vị trí xuất hiện, mức độ bệnh trạng của từng người…thì triệu chứng bệnh của chúng được biểu hiện như sau:</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/Chlamydia-v%C3%A0-b%E1%BB%87nh-l%E1%BA%ADu11.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Chlamydia và bệnh lậu1\" class=\"wp-image-5663 aligncenter\" height=\"241\" src=\"http://chuabenhxahoi.info/wp-content/uploads/Chlamydia-v%C3%A0-b%E1%BB%87nh-l%E1%BA%ADu11.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: center; background-color: rgb(255, 255, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\">Triệu chứng bệnh ở mỗi người không giống nhau hoàn toàn</span></em></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">- Ở nữ giới: phụ nữ mắc 2 bệnh trên đều xuất hiện tình trạng tiết dịch ở âm đạo, đau khi quan hệ tình dục, tiểu đau hoặc nóng rát, đau ở bụng hoặc hố chậu, lở loét ở miệng họng, tiết dịch ở trực tràng, khó chịu ở hậu môn.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">- Ở nam giới: các triệu chứng thường thấu là mót tiểu, tiểu rắt, tiết dịch niệu đạo, viêm đỏ niệu đạo, tiểu són – đau rát, sưng đau tinh hoàn, tiết dịch ở trực tràng, khó chịu ở hậu môn…</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">Một số triệu chứng của&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chlammydia và&nbsp;<a href=\"http://chuabenhxahoi.info/benh-lau\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\">bệnh lậu</a></strong>&nbsp;có đặc điểm gần giống với nhiễm trùng đường tiết niệu, viêm hậu môn, viêm phần phụ, viêm ruột thừa, hội chứng ruột kích thích, thống kinh. Vì vậy người bệnh không được chủ quan mà cần được thăm khám và theo dõi của các bác sĩ chuyên khoa.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">Trên đây là những phân tích của các bác sĩ phòng khám Thiên Hòa để giúp chúng ta có cái nhìn tổng quan về bệnh cũng như đưa ra những quyết định đúng đắn khi thăm khám và điều trị. Nếu bạn gặp phải những khó khăn trong việc phát hiện và chữa trị các&nbsp;<a href=\"http://chuabenhxahoi.info/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">bệnh xã hội</strong></a>&nbsp;nói riêng và những vấn đề về sức khỏe nói chung, hãy liên hệ đến Phòng khám đa khoa Thiên Hòa để nhận được những tư vấn bổ ích, giúp ngăn chặn kịp thời tác hại của bệnh gây ra đối với sức khỏe.</p>\r\n\r\n<div class=\"kk-star-ratings  bottom-left lft\" data-id=\"3644\" style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; width: 120px; float: left; color: rgb(51, 51, 51); font-size: 13px; line-height: 19.5px; background-color: rgb(255, 255, 255);\">\r\n<div class=\"kksr-stars kksr-star gray\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; height: 24px; position: relative; background-image: url(http://chuabenhxahoi.info/wp-content/plugins/kk-star-ratings/gray.png); background-position: 0px 0px;\">\r\n<div class=\"kksr-fuel kksr-star yellow\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; height: 24px; position: absolute; top: 0px; left: 0px; z-index: 0; width: 0px; background-image: url(http://chuabenhxahoi.info/wp-content/plugins/kk-star-ratings/yellow.png); background-position: 0px 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div class=\"kksr-legend\" style=\"padding: 3px; margin: 0px; outline: none; list-style: none; border: 0px none; font-size: 12px; color: rgb(153, 153, 153); text-align: center; line-height: 18px;\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n','nhung-diem-giong-nhau-giua-chlamydia-va-benh-lau',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','264_1444293396_66356162b14b9547.jpg',[now], [now],[user_id],0,'','',[now]);set @news823 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories499,'10 bệnh thường gặp do Chlamydia biến chứng gây ra','10 bệnh thường gặp do Chlamydia biến chứng gây ra<br /><br />\r\n<br /><br />\r\nChlamydia là một trong số các bệnh lây lan qua đường tình dục gây nên những tác hại lớn đối với sức khỏe, sinh sản của người bệnh. Chlamydia không được chữa trị kịp thời có thể gây ra biến chứng nặng nề và có thể mắc phải những bệnh lý nguy hiểm khác. Cùng chuyên gia chữa bệnh xã hội của Phòng khám đa khoa Thiên Hòa tìm hiểu về biến chứng của bệnh','<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-family: Arial, Helvetica, sans-serif; color: rgb(205, 90, 36); font-size: 17px; line-height: normal;\"><span itemprop=\"name\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">10 bệnh thường gặp do Chlamydia biến chứng gây ra</span></h1>\r\n\r\n<p class=\"post-meta\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); font-size: 11.0500001907349px; font-family: Arial, Helvetica, sans-serif; line-height: normal;\"><span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">23.07.2014</span> <span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">trong&nbsp;<a href=\"http://chuabenhxahoi.info/benh-chlamydia\" rel=\"category tag\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); text-decoration: none; transition: all 0.2s ease-in-out;\">Bệnh Chlamydia</a></span></p>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both; font-family: Arial, Helvetica, sans-serif; line-height: normal;\">&nbsp;</div>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; font-family: Arial, Helvetica, sans-serif;\">\r\n<div id=\"page-content-1\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chlamydia</strong>&nbsp;là một trong số các bệnh lây lan qua đường tình dục gây nên những tác hại lớn đối với sức khỏe, sinh sản của người bệnh. Chlamydia không được chữa trị kịp thời có thể gây ra biến chứng nặng nề và có thể mắc phải những bệnh lý nguy hiểm khác. Cùng chuyên gia&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">chữa bệnh xã</strong>&nbsp;hội của Phòng khám đa khoa Thiên Hòa tìm hiểu về biến chứng của bệnh</span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; font-weight: bold; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; color: rgb(32, 157, 179) !important; text-align: justify; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(0, 0, 0);\">Bệnh chlamydia có thể gây nên các bệnh như:</span></h2>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: bold; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; color: rgb(211, 86, 126) !important; text-align: justify; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Nhiễm virus suy giảm miễn dịch (HIV)</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Những người bị nhiễm&nbsp;<a href=\"http://chuabenhxahoi.info/benh-chlamydia\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">bệnh chlamydia</strong></a>&nbsp;sẽ làm tăng nguy cơ nhiễm HIV cao hơn những người bình thường vì hệ miễn dịch suy giảm, sức đề kháng kém.</span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: bold; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; color: rgb(211, 86, 126) !important; text-align: justify; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Bệnh hoa liễu</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Những người nhiễm chlamydia cũng có thể có nguy cơ nhiễm trùng qua đường tình dục khác, chẳng hạn như bệnh lậu, giang mai và viêm gan. Bác sĩ khuyên nên bạn nên thử nghiệm về lây nhiễm khác qua đường tình dục nếu có chlamydia.</span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: bold; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; color: rgb(211, 86, 126) !important; text-align: justify; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Viêm vùng chậu</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Viêm vùng chậu là một nhiễm trùng của tử cung và ống dẫn trứng. Mặc dù nó có thể gây ra không có dấu hiệu hoặc triệu chứng, PID có thể làm hỏng các ống dẫn trứng, buồng trứng và tử cung, bao gồm cả cổ tử cung. Nếu không điều trị PID có thể dẫn đến áp – xe trong ống dẫn trứng và buồng trứng.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/benh-chlamydia2.jpeg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\" 10 bệnh thường gặp do Chlamydia biến chứng gây ra\" class=\"aligncenter size-full wp-image-3691\" height=\"205\" src=\"http://chuabenhxahoi.info/wp-content/uploads/benh-chlamydia2.jpeg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"246\" /></a><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chlamydia có thể gây ra nhiều bệnh lý nguy hiểm đối với nữ giới</em></span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: bold; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; color: rgb(211, 86, 126) !important; text-align: justify; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Đau xương chậu mãn tính</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh chlamydia</strong>&nbsp;nếu không sớm điều trị có thể dẫn đến đau xương chậu mãn tính ở phụ nữ.</span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: bold; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; color: rgb(211, 86, 126) !important; text-align: justify; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Vô sinh</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Vô sinh, sẹo ở ống dẫn trứng gây ra bởi nhiễm chlamydia có thể dẫn đến vô sinh.</span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: bold; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; color: rgb(211, 86, 126) !important; text-align: justify; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Viêm mào tinh hoàn</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nhiễm chlamydia có thể biến chứng epididymis, một ống cuộn nằm bên cạnh mỗi tinh hoàn. Epididymitis có thể gây sốt, đau và sưng bìu.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Viêm tuyến tiền liệt</strong></span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Các sinh vật chlamydia có thể lây lan đến tuyến tiền liệt. Viêm tuyến tiền liệt có thể gây đau trong hoặc sau khi quan hệ tình dục, sốt và ớn lạnh, đi tiểu đau và đau lưng.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Viêm trực tràng</strong></span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nếu có quan hệ tình dục qua đường hậu môn, các sinh vật chlamydia có thể gây ra viêm trực tràng. Điều này có thể dẫn đến đau trực tràng và thải chất nhầy.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nhiễm trùng mắt</strong></span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chạm vào mắt với bàn tay truyền nhiễm có thể gây ra nhiễm trùng mắt, như viêm kết mạc. Không được điều trị, nhiễm trùng mắt có thể dẫn đến mù loà.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(255, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nhiễm trùng ở trẻ sơ sinh</strong></span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nhiễm&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">chlamydia</strong>&nbsp;có thể truyền từ mẹ sang con trong thời gian sinh, gây viêm phổi hoặc nhiễm trùng mắt có thể dẫn đến mù loà, dị tật cho em bé</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: right;\">&nbsp;</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Lời khuyên của bác sĩ chuyên khoa:</strong>&nbsp;Khi phát hiện bệnh bạn nên đến điều trị sớm, trong quá trình điều trị phải thực hiện nghiêm chỉnh chỉ định của bác sĩ và tiến hành điều trị song song người bệnh và bạn tình tránh trường hợp tái phát hay lây nhiễm chéo. Nếu bạn quan tâm đến việc kiểm tra và&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">điều trị Chlamydia</strong>&nbsp;an toàn và hiệu quả có thể liên hệ đến Phòng khám đa khoa Thiên Hòa. Phòng khám có đội ngũ y bác sĩ giàu kinh nghiệm cùng với các trang thiết bị kỹ thuật hiện đại đã chữa khỏi bệnh cho tất cả những bệnh nhân đến điều trị.</span></p>\r\n</div>\r\n</div>\r\n','10-benh-thuong-gap-do-chlamydia-bien-chung-gay-ra',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','418_1444293229_72556162a6d6540d.jpg',[now], [now],[user_id],1,'','',[now]);set @news824 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories498,'Cách phòng ngừa các bệnh lây nhiễm qua đường tình dục','Cách phòng ngừa các bệnh lây nhiễm qua đường tình dục<br /><br />\r\nBệnh lây nhiễm qua đường tình dục hay còn gọi với tên khác là bệnh xã hội đang ngày càng có xu hướng tăng cao. Theo số liệu thống kê cho thấy, 5 năm trước tỉ lệ người mắc các bệnh xã hội là 8:1 và chủ yếu gặp ở nam giới. Còn trong những năm gần đây, tỉ lệ này không giảm mà còn tăng cao. Số người mắc bệnh đã theo tỉ lệ 4:1 và nam, nữ mắc bệnh là tương đương nhau. Do vậy, hiểu biết và nắm rõ những biện pháp phòng ngừa các bệnh lây nhiễm ','<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: normal; font-family: Arial, Helvetica, sans-serif; color: rgb(205, 90, 36); font-size: 17px; background-color: rgb(255, 255, 255);\"><span itemprop=\"name\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Cách phòng ngừa các bệnh lây nhiễm qua đường tình dục</span></h1>\r\n\r\n<p class=\"post-meta\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); font-size: 11.0500001907349px; font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">6 days ago</span> <span style=\"padding: 0px; margin: 0px 10px 0px 0px; outline: none; list-style: none; border: 0px none; display: inline-block;\">trong&nbsp;<a href=\"http://chuabenhxahoi.info/benh-lau\" rel=\"category tag\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); text-decoration: none; transition: all 0.2s ease-in-out;\">Bệnh Lậu</a>,&nbsp;<a href=\"http://chuabenhxahoi.info/herpes-sinh-duc\" rel=\"category tag\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(170, 170, 170); text-decoration: none; transition: all 0.2s ease-in-out;\">Herpes Sinh Dục</a></span></p>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 13px; background-color: rgb(255, 255, 255);\">&nbsp;</div>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); font-size: 13px; background-color: rgb(255, 255, 255);\">\r\n<div id=\"page-content-1\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh lây nhiễm qua đường tình dục hay còn gọi với tên khác là bệnh xã hội đang ngày càng có xu hướng tăng cao. Theo số liệu thống kê cho thấy, 5 năm trước tỉ lệ người mắc các bệnh xã hội là 8:1 và chủ yếu gặp ở nam giới. Còn trong những năm gần đây, tỉ lệ này không giảm mà còn tăng cao. Số người mắc bệnh đã theo tỉ lệ 4:1 và nam, nữ mắc bệnh là tương đương nhau. Do vậy, hiểu biết và nắm rõ những biện pháp phòng ngừa các bệnh lây nhiễm qua đường tình dục sẽ giúp bạn giảm thiểu nguy cơ mắc bệnh. Dưới đây là những việc chúng ta cần làm để phòng tránh những căn bệnh nguy hiểm này.</em></strong></span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Không quan hệ khi đang say rượu hoặc dùng chất kích thích</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Không nên quan hệ khi đang say rượu, dùng thuốc kích thích là cách&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">phòng ngừa các bệnh lây nhiễm qua đường tình dục</em></strong>&nbsp;an toàn. Nếu bạn đang say rượu hay sử dụng chất kích thích sẽ làm mất đi kiểm soát, ảnh hưởng tới hành động, từ đó làm bạn chọn sai đối tác, sai bạn tình. Chỉ cần một phút lơ đãng, quên không sử dụng biện pháp an toàn thì nguy cơ mắc các bệnh qua đường tình dục là rất cao.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/phong-ngua-cac-benh-lay-nhiem-qua-duong-tinh-duc-1.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Phòng ngừa các bệnh lây nhiễm qua đường tình dục 1\" class=\"aligncenter wp-image-7381\" height=\"221\" src=\"http://chuabenhxahoi.info/wp-content/uploads/phong-ngua-cac-benh-lay-nhiem-qua-duong-tinh-duc-1.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" title=\"Phòng ngừa các bệnh lây nhiễm qua đường tình dục 1\" width=\"350\" /></a><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Không quan hệ khi đang say rượu&nbsp;</strong></em></span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Quan hệ tình dục chung thủy, tránh quan hệ với nhiều “đối tác”</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nên quan hệ tình dục chung thủy (một vợ một chồng) và tránh quan hệ với nhiều người. Bởi quan hệ với nhiều người sẽ làm tăng khả năng mắc các bệnh lây nhiễm nhiều hơn so với một bạn tình. Tuy nhiên, bạn cũng cần phải đảm bảo, đối tác của mình cũng có quan hệ tình dục chung thủy, 1 bạn tình. Bạn nên biết rằng, khi mới mắc bệnh lây nhiễm qua đường tình dục, người bệnh thường không có triệu chứng và không có vẻ ốm yếu kể là cả là người bị nhiễm HIV.</span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Kiểm tra sức khỏe tình dục định kỳ</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Dù bạn không có bất cứ dấu hiệu bất thường nào thì cũng không thể chắc chắn rằng bạn là người khỏe mạnh. Vì vậy, để đảm bảo an toàn, mọi người (kể cả nam và nữ) nên đi kiểm tra sức khỏe tình dục định kỳ và làm các xét nghiệm cần thiết. Đây cũng là cách&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">phòng ngừa các bệnh lây nhiễm qua đường tình dục</em></strong>&nbsp;một cách tốt nhất. Việc xét nghiệm kiểm tra sớm và đều đặn giúp phát hiện bệnh và điều trị kịp thời, tránh gặp các biến chứng nguy hiểm đến sức khỏe và tính mạng.</span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Sử dụng bao cao su khi quan hệ</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bao cao su không chỉ là một biện pháp tránh thai mà nó còn giúp làm giảm nguy cơ mắc phải các bệnh lây nhiễm qua đường tình dục. Tuy nhiên, chúng ta cũng cần lưu ý, một số bệnh lây truyền qua đường tình dục vẫn có thể lây truyền được kể cả khi dùng bao cao su, phải kể đến như bệnh rận mu, herpes sinh dục …vì các bệnh này có cả ở xung quanh cơ quan sinh dục.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/phong-ngua-cac-benh-lay-nhiem-qua-duong-tinh-duc-2.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Phòng ngừa các bệnh lây nhiễm qua đường tình dục 2\" class=\"aligncenter wp-image-7382\" height=\"233\" src=\"http://chuabenhxahoi.info/wp-content/uploads/phong-ngua-cac-benh-lay-nhiem-qua-duong-tinh-duc-2.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" title=\"Phòng ngừa các bệnh lây nhiễm qua đường tình dục 2\" width=\"350\" /></a><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nên sử dụng bao cao su khi quan hệ</strong></em></span></p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Vệ sinh bộ phận sinh dục trước và sau quan hệ</strong></span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Cho dù là nam hay nữ thì việc vệ sinh bộ phận sinh dục trước và sau khi quan hệ là điều hết sức quan trọng. Bởi nó làm giảm thiểu nguy cơ mắc bệnh lây nhiễm qua đường tình dục. Với nữ giới có thể vệ sinh vùng kín bằng nước ấm pha với một chút muối hoặc có thể sử dụng dung dịch vệ sinh chuyên dụng.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Trên đây là một số cách<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">&nbsp;phòng ngừa các bệnh lây nhiễm qua đường tình dục</em></strong>&nbsp;hiệu quả. Nắm rõ và thực hiện đúng những điều trên sẽ giúp mọi người hạn chế và giảm thiểu nguy cơ mắc bệnh, bảo vệ sức khỏe của bản thân và gia đình mình một cách tốt nhất.</span></p>\r\n</div>\r\n</div>\r\n','cach-phong-ngua-cac-benh-lay-nhiem-qua-duong-tinh-duc',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','731_1444293080_802561629d80c1fc.jpg',[now], [now],[user_id],0,'','',[now]);set @news825 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories498,'Nguyên nhân và cách chữa trị bệnh lậu ở nam giới','Nguyên nhân và cách chữa trị bệnh lậu ở nam giới<br /><br />\r\nBệNh lậu – 1 trong 5 bệnh hoa liễu cổ điển là mối đe dọa và nỗi ám ảnh của nam giới với những biến chứng nguy hiểm như các bệnh viêm tuyến tiền liệt, viêm niệu đạo<br /><br />\r\n, viêm mào tinh hoàn, viêm ống dẫn tinh, nghiêm trọng hơn lậu có thể khiến nam giới vô sinh. Vậy nguồn gốc bệnh có nguyên nhân từ đâu và có những cách nào để điều trị bệnh.<br /><br />\r\n','<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-family: Arial, Helvetica, sans-serif; color: rgb(205, 90, 36); font-size: 17px; line-height: normal;\">&nbsp;</h1>\r\n\r\n<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"line-height: normal; padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-family: Arial, Helvetica, sans-serif; color: rgb(205, 90, 36); font-size: 17px;\"><span itemprop=\"name\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nguyên nhân và cách chữa trị bệnh lậu ở nam giới</span></h1>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both; font-family: Arial, Helvetica, sans-serif; line-height: normal;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">BệNh lậu – 1 trong 5 bệnh hoa liễu cổ điển là mối đe dọa và nỗi ám ảnh của nam giới với những biến chứng nguy hiểm như các bệnh&nbsp;</em></strong><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">viêm tuyến tiền liệt, viêm niệu đạo</em></strong></span></div>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both; font-family: Arial, Helvetica, sans-serif; line-height: normal;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">, viêm mào tinh hoàn, viêm ống dẫn tinh, nghiêm trọng hơn lậu có thể khiến nam giới vô sinh. Vậy nguồn gốc bệnh có nguyên nhân từ đâu và có những cách nào để điều trị bệnh</em></strong><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">.</strong></span></div>\r\n\r\n<div class=\"clear\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; clear: both; font-family: Arial, Helvetica, sans-serif; line-height: normal;\">&nbsp;</div>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; font-family: Arial, Helvetica, sans-serif;\">\r\n<div id=\"page-content-1\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Theo các chuyên gia của Phòng khám đa khoa Thiên Hòa, bệnh lậu là bệnh lý nhiễm trùng rất phổ biến, do lậu cầu khuẩn “Neisseria gonorrhoeae” gây nên, có thể biểu hiện triệu chứng hoặc không có triệu chứng trên lâm sàng.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-dieu-tri-benh-lau-o-nam-gioi2.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Nguyên nhân và cách chữa trị bệnh lậu ở nam giới\" class=\"aligncenter wp-image-4524\" height=\"161\" src=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-dieu-tri-benh-lau-o-nam-gioi2.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\">Mắc bệnh lậu qua đường giao hợp không an toàn</span></em></strong></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Vi khuẩn gây bệnh lậu không thể sống bên ngoài cơ thể quá vài phút, cũng như không thể sống trên bề mặt da của bàn tay, cánh tay, hay chân. Vì thế bệnh lậu không lây qua những hình thức giao tiếp thông thường như bắt tay, ôm hôn.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Hình thức lây truyền của bệnh lậu chính là trong giao hợp không an toàn, không sử dụng bất kỳ biện pháp bảo vệ nào mà đối tượng giao hợp lại mắc bệnh &nbsp;từ đó gây nên nhiễm bệnh chéo.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Do đặc trưng niệu đạo của nam giới tương đối dài hơn nữ giới, vì vậy bệnh lậu ở nam giới biểu hiện rầm rộ hơn, niệu đạo chia làm 2 phần gồm niệu đạo trước và niệu đạo sau, được ngăn cách bởi cơ thắt niệu đạo, có chiều dài từ 14 – 16cm.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Niệu đạo trước có nhiều hang, nhiều ngõ ngách đó là nơi trú ẩn của lậu cầu.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Niệu đạo sau càng phức tạp hơn cũng có nhiều ngóc ngách, xuyên qua tuyến tiền liệt, thông với túi tinh, ống dẫn tinh, mao tinh và tinh hoàn.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Ngoài ra, còn có tuyến Morgagni và tuyến Littre cũng thuận lợi cho lậu cầu sinh sôi và phát triển.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Sau khi lậu cầu xâm nhập vào cơ thể thông qua niệu đạo, vi khuẩn có khuynh hướng ưa thích tế bào mô bì trụ ở niệu mạc đường tiết niệu, đưa đến phản ứng viêm tại chỗ, kéo theo bạch cầu đa nhiễm đến để thực bào, từ đó trở thành tổ chức hoại tử trong quá trình viêm, được thoát ra ngoài theo nước tiểu, màu trắng hơi vàng gọi là tiểu ra mủ, vi khuẩn tiếp tục phát triển và đi dọc theo chiều dài của niệu đạo, đi đến đâu gây viêm đến đó gây viêm tiền liệt tuyến, viêm túi tinh, viêm tinh hoàn ở nam giới.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Ngoài những điều đã nêu trên, bệnh lậu có thể lây truyền từ mẹ sang con khi thai sổ qua ống đẻ.</p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; font-weight: bold; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; color: rgb(32, 157, 179) !important; text-align: justify; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\">&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Điều trị</strong></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-dieu-tri-benh-lau-o-nam-gioi1.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Nguyên nhân và cách chữa trị bệnh lậu ở nam giới\" class=\"wp-image-4512 aligncenter\" height=\"233\" src=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-dieu-tri-benh-lau-o-nam-gioi1.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\">Điều trị bệnh lậu với bác sĩ chuyên khoa</span></em></strong></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Nguyên tắc điều trị: Lậu cầu cứ 15 phút phân chia một lần do đó lan nhanh. Vì vậy cần điều trị sớm.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Điều trị đúng thuốc – đủ liều.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Điều trị cả với người tiếp xúc sinh lý.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Điều trị cho nam bao giờ cũng dùng thuốc và thời gian bằng một nửa so với nữ.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Chỉ được kết luận là khỏi bệnh khi cấy hai lần liên tiếp âm tính hoặc không còn tiết dịch niệu đạo với nghiệm pháp kích thích, (cho bệnh nhân lao động nặng, thức khuya, uống rượu bia. Sáng hôm sau lấy dịch xét nghiệm lúc bệnh nhân chưa đi tiểu).</p>\r\n\r\n<p class=\"post-box-title\" style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Việc điều trị còn có thể sử dụng các loại kháng sinh. Việc điều trị cụ thể ra sao phải dựa trên phác đồ của bác sĩ, người bệnh không nên tự ý mua và dùng sẽ khiến bệnh càng thêm nặng và đưa tới những hậu quả không mong muốn.</p>\r\n</div>\r\n</div>\r\n','nguyen-nhan-va-cach-chua-tri-benh-lau-o-nam-gioi',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','391_1444292863_825561628ff0d755.jpg',[now], [now],[user_id],0,'','',[now]);set @news826 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories498,'Bệnh lậu ở nữ giới nguyên nhân và cách chữa','Bệnh lậu ở nữ giới nguyên nhân và cách chữa<br /><br />\r\n<br /><br />\r\nLà 1 trong năm bệnh hoa liễu cổ điển, bệnh lậu không chỉ là nỗi ám ảnh và lo sợ của nam giới mà với nữ giới cũng hết sức nguy hại và đáng lưu tâm. Khi nữ giới có những biểu hiện đái buốt, mủ chảy ra từ trong niệu đạo, cổ tử cung mầu nâu, vàng hoặc xanh, số lượng nhiều, có mùi hôi thì lúc đó bạn hoàn toàn có quyền nghi ngờ mình đã mắc bệnh lậu. Tuy nhiên, bệnh lậu ở nữ giới có những đặc trưng riêng so với bệnh lậu ở nam, với nguy hại khôn','<h1 class=\"name post-title entry-title\" itemprop=\"itemReviewed\" itemscope=\"\" itemtype=\"http://schema.org/Thing\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-weight: normal; font-family: Arial, Helvetica, sans-serif; color: rgb(205, 90, 36); font-size: 17px; background-color: rgb(255, 255, 255);\"><span itemprop=\"name\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh lậu ở nữ giới nguyên nhân và cách chữa</span></h1>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); font-size: 13px; background-color: rgb(255, 255, 255);\">\r\n<div id=\"page-content-1\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Là 1 trong năm bệnh hoa liễu cổ điển, bệnh lậu không chỉ là nỗi ám ảnh và lo sợ của nam giới mà với nữ giới cũng hết sức nguy hại và đáng lưu tâm. Khi nữ giới có những biểu hiện đái buốt, mủ chảy ra từ trong niệu đạo, cổ tử cung mầu nâu, vàng hoặc xanh, số lượng nhiều, có mùi hôi thì lúc đó bạn hoàn toàn có quyền nghi ngờ mình đã mắc bệnh lậu. Tuy nhiên, bệnh lậu ở nữ giới có những đặc trưng riêng so với bệnh lậu ở nam, với nguy hại khôn lường. Vậy nguyên nhân và cách chữa trị bệnh ra sao. Cùng lắng nghe lý giải của chuyên gia Phòng khám đa khoa Thiên Hòa dưới đây.</p>\r\n\r\n<ul style=\"padding: 5px 0px 10px; margin: 0px 0px 25px 10px; outline: none; list-style: none; border: 1px dashed rgb(122, 180, 91); min-height: 64px; height: auto; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/247.png) right 5px top 50% no-repeat scroll, none 0% 0% repeat scroll rgb(194, 234, 195);\">\r\n	<li class=\"post-box-title\" style=\"padding: 0px 0px 0px 10px; margin: 4px 0px 0px 10px; outline: none; list-style: none outside none; border: 0px none; text-align: justify; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-5.png) left 0% top 10px no-repeat scroll transparent;\"><span style=\"color: rgb(32, 157, 179); font-family: Helvetica; font-size: 16px; line-height: 13.2pt; text-indent: 0.5in; background-color: initial;\">Nguyên nhân bệnh lậu</span></li>\r\n	<li class=\"post-box-title\" style=\"padding: 0px 0px 0px 10px; margin: 4px 0px 0px 10px; outline: none; list-style: none outside none; border: 0px none; text-align: justify; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-5.png) left 0% top 10px no-repeat scroll transparent;\"><span style=\"color: rgb(211, 86, 126); font-family: Helvetica; font-size: 14px; line-height: 13.2pt; text-indent: 0.5in; background-color: initial;\">Lậu do quan hệ tình dục không an toàn</span></li>\r\n</ul>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-dieu-tri-benh-lau-o-nu-gioi.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Nguyên nhân và cách chữa trị bệnh lậu ở nữ giới\" class=\"wp-image-4533 aligncenter\" height=\"229\" src=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-dieu-tri-benh-lau-o-nu-gioi.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\">Tình dục không an toàn là cách lây truyền phổ biến nhất của bệnh lậu</span></em></strong></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Hình thức lây truyền của bệnh lậu chính là trong giao hợp không an toàn, không sử dụng bất kỳ biện pháp bảo vệ nào mà đối tượng giao hợp lại mắc bệnh&nbsp; từ đó gây nên nhiễm bệnh chéo. Nguyên nhân này chiếm chủ yếu trong số các nguyên nhân gây ra bệnh lậu cho cả nam và nữ. Bệnh lậu là một trong những căn bệnh tình dục dễ dàng để ngăn chặn hơn các bệnh xã hội khác vì các vi khuẩn gây nhiễm trùng có thể tồn tại chỉ trong điều kiện nhất định. Vì vậy lời khuyên là nên sử dụng các biện pháp bảo vệ chống lại nhiễm trùng bệnh lậu.</p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Khả năng miễn dịch kém</strong></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Bệnh lậu không chỉ hình thành ở người trực tiếp tham gia hoạt động tình dục bừa bãi mà với những chị em có sức đề kháng kém, khả năng miễn dịch yếu khi không may và bất cẩn tiếp xúc với các vật dụng cá nhân của người bệnh, chứa các dịch tiết có chứa vi khuẩn như khăn tắm, đồ bơi, bệ xí đặc biệt là nữ giới ( do niệu đạo và cơ quan sinh dục gần nhau ) cũng sẽ rất dễ lây nhiễm.</p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat;\">Nhiễm lậu do vết thương hở ngoài da</h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Khi nữ giới có những vết thương hở ngoài da, nếu không chăm sóc cẩn thận, vi khuẩn sẽ dễ dàng xâm nhập có tổ chức lên niêm mạc da và các vết thương nhỏ. Sau thời gian ủ bệnh ngắn ngủi, vi khuẩn sẽ mở rộng phạm vi khu trú và hình thành bệnh.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Khác với đặc trưng bệnh lậu ở nam giới, nữ giới khi mắc lậu mãn tính ngay từ đầu, tốc độ phát triển bệnh không ngờ và khó khăn cho nữ giới nhận biết bệnh bởi những triệu chứng đều khá sơ sài.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Chính bởi vậy, muốn xác định rõ bạn có mắc bệnh lậu hay không, cần phải tiến hành xét nghiệm&nbsp; nhiều loại bệnh phẩm khác nhau và nhiều phương pháp khác nhau. Bệnh lậu mạn tính ở nữ cũng cần chẩn đoán phân biệt với một số bệnh cũng gây viêm âm hộ, âm đạo do vi khuẩn chlamydia trachomatis, mycoplasma, trùng roi, nấm candida albicans, tạp khuẩn để điều trị có hiệu quả.</p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Điều trị sớm bệnh lậu ở nữ giới sẽ hạn chế biến chứng nguy hiểm</strong></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-dieu-tri-benh-lau-o-nu-gioi1.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"Nguyên nhân và cách chữa trị bệnh lậu ở nữ giới\" class=\"wp-image-4534 aligncenter\" height=\"255\" src=\"http://chuabenhxahoi.info/wp-content/uploads/nguyen-nhan-va-cach-dieu-tri-benh-lau-o-nu-gioi1.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></a><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\">Chữa trị bệnh lậu ở nữ giai đoạn đầu sẽ đơn giản và chóng hồi phục</span></em></strong></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Lậu ở nữ là bệnh lý gây viêm âm hộ và các tuyến ở vùng âm hộ, âm đạo tạo thành các túi mủ. Đang tuổi sinh đẻ nếu có thai thì rất dễ lây cho con sau khi sinh đặc biệt là gây viêm kết mạc mắt có thể dẫn đến mù lòa.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Cũng cần lưu ý rằng mắc lậu mạn tính rất dễ gây vô sinh do lây nhiễm gây viêm cổ tử cung, tử cung, vòi trứng, buồng trứng và tiểu khung mà để khắc phục điều đó (chữa vô sinh) thật không đơn giản chút nào.Ngoài ra có thể gây viêm bàng quang biểu hiện tiểu dắt, buốt.</p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Chính bởi những nguy hại trên, mà các chuyên gia khuyến cáo, nữ giới khi mắc bệnh cần đến ngay các cơ sở y tế chuyên khoa để điều trị. Vì bệnh lậu đa số thường kèm theo cả chlamydia nên khi chữa trị thường dùng cả thuốc có tác dụng trị liệu cả hai loại bệnh, và không chỉ điều trị cho người bệnh mà cũng cần chữa cả bạn tình để tránh tình trạng lây nhiễm tái phát sau điều trị.</p>\r\n\r\n<p class=\"post-box-title\" style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0); text-align: justify;\">Việc chữa trị bệnh lậu cần đồng thời điều trị cả viêm vùng chậu trong trường hợp chị em mắc. Giải pháp chữa trị ở nhóm bệnh nhân này là sử dụng kháng sinh kết hợp với một số phương pháp khác được chỉ định bởi bác sĩ. Trong quá trình điệu trị, cần chú ý tới đối tượng phụ nữ mang thai để tránh ảnh hưởng sức khỏe thai phụ và thai nhi.</p>\r\n</div>\r\n</div>\r\n','benh-lau-o-nu-gioi-nguyen-nhan-va-cach-chua',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','46_1444292604_845561627fc1f1df.jpg',[now], [now],[user_id],0,'','',[now]);set @news827 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories498,'Bệnh lậu là gì?','Nhiều người biết bệnh lậu là bệnh xã hội nguy hiểm, nhưng không biết gì về nguyên nhân, triệu chứng của bệnh như thế nào. Bài viết này sẽ giúp mọi người hiểu được bệnh lậu là gì, nguyên nhân, triệu chứng để mọi người có cái nhìn tổng quan về bệnh lậu, khi gặp bệnh lậu có thể tự nhận biết phát hiện bệnh kịp thời và chia sẻ cách chữa bệnh lậu tránh bệnh kéo dài gây ảnh hưởng đến cuộc sống và nguy hại cho sức khỏe người bệnh.','<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Nhiều người biết bệnh lậu là&nbsp;<a href=\"http://chuabenhxahoi.info/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">bệnh xã hội</em></a>&nbsp;nguy hiểm, nhưng không biết gì về nguyên nhân, triệu chứng của bệnh như thế nào.&nbsp;Bài viết này sẽ giúp mọi người hiểu được&nbsp;<strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">bệnh lậu là gì</strong>, nguyên nhân, triệu chứng để mọi người có cái nhìn tổng quan về bệnh lậu, khi gặp bệnh lậu có thể tự nhận biết phát hiện bệnh kịp thời và chia sẻ cách chữa bệnh lậu tránh bệnh kéo dài gây ảnh hưởng đến cuộc sống và nguy hại cho sức khỏe người bệnh.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">&nbsp;</p>\r\n\r\n<h2 style=\"padding: 0px; margin: 0px 0px 10px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; font-size: 16px; text-indent: 0.5in; text-align: justify; color: rgb(32, 157, 179) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-3.png) left 22px top 2px no-repeat rgb(255, 255, 255);\"><span style=\"padding: 0px 0px 5px 23px; margin: 0px 0px 0px -23px; outline: none; list-style: none; border-width: 0px 0px 1px; border-style: none none solid; border-bottom-color: rgb(211, 86, 126); color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh lậu là gì</strong>?</span></h2>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><a href=\"http://chuabenhxahoi.info/benh-lau\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh lậu</em></a>&nbsp;là bệnh xã hội do lậu cầu khuẩn “Neisseria gonorrhoeae” gây ra và có thể bắt gặp ở nhiều đối tượng khác nhau. Bệnh lậu có ảnh hưởng lớn đến sức khỏe của người bệnh bởi khả năng lây nhiễm nhanh chóng cũng như dễ dàng gây ra các biến chứng viêm nhiễm đối với cơ quan sinh sản. Cách tốt nhất để phát hiện và điều trị bệnh sớm là người bệnh cần có những kiến thức tổng quát về bệnh và tiến hành thăm khám điều trị trong thời gian sớm nhất.</span></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_7056\" style=\"padding: 4px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 13px; width: 503px; background: rgb(241, 241, 241);\"><a class=\"gallery_colorbox\" href=\"http://chuabenhxahoi.info/wp-content/uploads/benh-lau-la-gi.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out; cursor: url(http://chuabenhxahoi.info/wp-content/plugins/image-zoom/img/zoomin.cur), pointer;\"><img alt=\"bệnh lậu là gì\" class=\"size-full wp-image-7056\" height=\"335\" src=\"http://chuabenhxahoi.info/wp-content/uploads/benh-lau-la-gi.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; max-width: 100%; height: auto;\" width=\"493\" /></a>\r\n\r\n<p class=\"wp-caption-text\" style=\"padding: 0px; margin: 5px; outline: none; list-style: none; border: 0px none; line-height: 20px; color: rgb(0, 0, 0);\">Khi bệnh lậu xuất hiện ngoài da</p>\r\n</div>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Nguyên nhân bệnh lậu</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh lậu do vi khuẩn lậu cầu gây nên thì hầu hết chúng ta ai cũng biết nhưng vì sao bạn lại bị nhiễm khuẩn gây bệnh. Theo các bác sĩ chuyên khoa thì người mắc bệnh lậu chủ yếu thông qua các con đường như:</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Lây nhiễm qua con đường quan hệ tình dục:</em>&nbsp;Đây là nguyên nhân hàng đầu chiếm đến 95% số người mắc bệnh lậu. Việc lây nhiễm có thể do quan hệ tình dục bằng miệng, đường hậu môn hoặc quan hệ qua âm đạo.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><img alt=\"Bệnh lậu là gì?\" class=\"aligncenter wp-image-2740\" height=\"237\" src=\"http://chuabenhxahoi.info/wp-content/uploads/benh-lau16.jpeg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"350\" /></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; background-color: rgb(255, 255, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\">Bệnh lậu có thể lây qua đường truyền máu của người bị bệnh</span></em></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Qua đường máu:</em>&nbsp;Nếu người mắc bệnh lậu chưa được kiểm tra kỹ càng mà thực hiện truyền máu cho người khác thì sẽ lây nhiễm vi khuẩn bệnh cho người được nhận máu.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Lây truyền qua tiếp xúc:</em>&nbsp;Những người tiếp xúc trực tiếp hay gián tiếp qua việc sử dụng chung đồ dùng với người mắc bệnh lậu thì nguy cơ lây nhiễm cũng rất cao.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Lây từ mẹ sang con:</em>&nbsp;người mẹ mắc bệnh lậu sẽ truyền nhiễm qua thai nhi thông qua hệ thống tuần hoàn.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Nhiễm bệnh lậu qua đường sản:</em>&nbsp;Những thai phụ mắc bệnh lậu khi thực hiện việc đẻ thông thường dễ làm cho thai nhi mắc bệnh lậu ở mắt.</span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Biểu hiện của bệnh lậu</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">-&nbsp;<a href=\"http://chuabenhxahoi.info/thuoc-chua-benh-lau-o-nam-gioi.html\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh lậu ở&nbsp;nam giới</em></a>, sau thời gian ủ bệnh khoảng từ 2 đến 6 ngày sẽ xuất hiện các triệu chứng như: đau dọc theo niệu đạo, rát, nóng, buốt khi đi tiểu, chảy mủ, nước tiểu đục, mủ đặc màu vàng. Khi chuyển sang mãn tính thì xuất hiện tình trạng: tiểu rắt, tiểu buốt, tiểu có mủ, có chất nhầy như nhựa chuối chảy ra ở niệu đạo vào sáng sớm.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">-&nbsp;<a href=\"http://chuabenhxahoi.info/nguyen-nhan-va-cach-chua-tri-benh-lau-o-nu-gioi.html\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(5, 15, 255); text-decoration: none; transition: all 0.2s ease-in-out;\" target=\"_blank\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh lậu ở&nbsp;nữ giới</em></a>, thời gian ủ bệnh khó xác định, xuất hiện các triệu chứng kín đáo. Một số biểu hiện thường thấy như tiểu rắt, đau vùng xương mu sau khi quan hệ tình dục. Thăm khám thấy viêm niệu đạo, cổ tử cung, âm đạo viêm tấy đỏ, có mủ. Ở nữ giới, bệnh lậu chuyển sang giai đoạn mãn tính ngay từ đầu, có rất ít triệu chứng ngoài dấu hiệu ra khí hư màu vàng.</span></p>\r\n\r\n<h3 style=\"padding: 4px 0px 11px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 13.2pt; font-family: Helvetica; text-indent: 0.5in; font-size: 14px; text-align: justify; color: rgb(211, 86, 126) !important; background: url(http://chuabenhxahoi.info/wp-content/themes/cbxh/images/list-4.png) left 32px top 8px no-repeat rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 0);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(211, 86, 126);\">Cách chữa trị bệnh lậu</strong></span></h3>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Bệnh lậu có thể được điều trị bằng các loại thuốc kháng sinh với nhiều dạng khác nhau, tuy nhiên việc dùng thuốc cần nhận được sự tư vấn chỉ dẫn của các bác sĩ chuyên khoa. Bệnh lậu có thể chữa trị bằng các loại thuốc như: Thuốc tiêm (Ceftriaxime, Spectionmycine , Ceftriaxone, Cefoxitine 250mg) , Thuốc uống ( Unasyn, Azithromycin 250mg X 4, Tequin 200mg). Việc sử dụng cần tuân thủ theo chỉ định của bác sĩ về liều dùng, thời gian sử dụng…</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; background-color: rgb(255, 255, 255);\"><img alt=\"chữa bệnh lậu bằng dha\" class=\"wp-image-4592 aligncenter\" height=\"245\" src=\"http://chuabenhxahoi.info/wp-content/uploads/chua-benh-xa-hoi-bang-dha.jpg\" style=\"padding: 4px; margin: 2px auto; outline: none; list-style: none; border: 1px solid rgb(203, 205, 204); clear: both; max-width: 100%; height: auto; display: block;\" width=\"367\" /></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(0, 0, 255);\"><em style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Chữa bệnh lậu bằng công nghệ điều trị DHA</em></span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">- Phòng khám đa khoa &nbsp;sử dụng công nghệ DHA – ứng dụng sóng siêu ngắn bên ngoài cơ thể, kỹ thuật bức xạ nhiệt và phương pháp điện dung giúp sản sinh nhiệt lượng tại nơi biến chứng, gây teo và làm biến tính các tổ chức tế bào, giúp cho huyết quản được lưu thông dễ dàng, cải thiện tuần hoàn máu, loại bỏ triệt để mầm bệnh.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">Đây là phương pháp điều trị thông qua kỹ thuật nhiệt điện trường sản sinh ra trường điện từ với tần số cao, tăng độ thẩm thấu của các chứng viêm, phục hồi trao đổi chất.</span></p>\r\n\r\n<p style=\"padding: 7px 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; background-color: rgb(255, 255, 255);\">&nbsp;</p>\r\n','benh-lau-la-gi',1,'','',[site_id],[user_id],'/media/images/170/news/ava/','279_1444292361_874561627094b3bd.jpg',[now], [now],[user_id],1,'','',[now]);set @news828 = LAST_INSERT_ID();
